contracts/ArgoPetz.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import "./common/ERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/token/common/ERC2981.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

error InvalidTokenId();
error NoMoreTokenIds();
error WithdrawFailed();

// Cred: Elementals contract -> learning from the best!
contract ArgoPetz is ERC2981, ERC721, Ownable {
    using Strings for uint256;
    using ECDSA for bytes32;

    uint16 public immutable MAX_SUPPLY;
    uint16 internal _numAvailableRemainingTokens;
    // Data structure used for Fisher Yates shuffle
    uint16[65536] internal _availableRemainingTokens;
    uint256 public constant PUBLIC_MAX_MINT = 20;
    uint256 public constant WHITELIST_MAX_MINT = 5;
    address public immutable WITHDRAW_ADDRESS;
    address public immutable WHITELIST_SIGNER_ADDRESS;
    mapping(address => uint256) public whitelistMintCount;
    mapping(address => uint256) public publicMintCount;
    uint256 public whitelistMintPrice = 1 ether;
    uint256 public publicMintPrice = 2 ether;
    uint8 public stage;
    string public baseURI;

    constructor(
        string memory _name,
        string memory _symbol,
        string memory _baseURI,
        uint16 maxSupply_,
        address withdrawAddress,
        address _whitelistSignerAddress
    ) ERC721(_name, _symbol) {
        MAX_SUPPLY = maxSupply_;
        _numAvailableRemainingTokens = maxSupply_;
        setBaseURI(_baseURI);
        WITHDRAW_ADDRESS = withdrawAddress;
        WHITELIST_SIGNER_ADDRESS = _whitelistSignerAddress;
    }

    // ---------------
    // Name and symbol
    // ---------------
    function setNameAndSymbol(string calldata _newName, string calldata _newSymbol) external onlyOwner {
        name = _newName;
        symbol = _newSymbol;
    }

    function _useRandomAvailableTokenId() internal returns (uint256) {
        uint256 numAvailableRemainingTokens = _numAvailableRemainingTokens;
        if (numAvailableRemainingTokens == 0) {
            revert NoMoreTokenIds();
        }

        uint256 randomNum = _getRandomNum(numAvailableRemainingTokens);
        uint256 randomIndex = randomNum % numAvailableRemainingTokens;
        uint256 valAtIndex = _availableRemainingTokens[randomIndex];

        uint256 result;
        if (valAtIndex == 0) {
            // This means the index itself is still an available token
            result = randomIndex;
        } else {
            // This means the index itself is not an available token, but the val at that index is.
            result = valAtIndex;
        }

        uint256 lastIndex = numAvailableRemainingTokens - 1;
        if (randomIndex != lastIndex) {
            // Replace the value at randomIndex, now that it's been used.
            // Replace it with the data from the last index in the array, since we are going to decrease the array size afterwards.
            uint256 lastValInArray = _availableRemainingTokens[lastIndex];
            if (lastValInArray == 0) {
                // This means the index itself is still an available token
                // Cast is safe as we know that lastIndex cannot > MAX_SUPPLY, which is a uint16
                _availableRemainingTokens[randomIndex] = uint16(lastIndex);
            } else {
                // This means the index itself is not an available token, but the val at that index is.
                // Cast is safe as we know that lastValInArray cannot > MAX_SUPPLY, which is a uint16
                _availableRemainingTokens[randomIndex] = uint16(lastValInArray);
                delete _availableRemainingTokens[lastIndex];
            }
        }

        --_numAvailableRemainingTokens;

        return result;
    }

    function _getRandomNum(uint256 numAvailableRemainingTokens) internal view returns (uint256) {
        return
            uint256(
                keccak256(
                    abi.encode(
                        block.prevrandao,
                        blockhash(block.number - 1),
                        address(this),
                        numAvailableRemainingTokens
                    )
                )
            );
    }

    function whitelistMint(
        address to,
        uint256 _amount,
        bytes calldata nonce,
        bytes calldata signature
    ) external payable {
        // Check if user is whitelisted
        require(whitelistSigned(msg.sender, nonce, signature, stage), "ArgoPetz: Invalid Signature!");

        // Check if whitelist sale is open
        require(stage == 1, "ArgoPetz: Whitelist Mint is not open");

        // Check if enough ETH is sent
        require(msg.value == _amount * whitelistMintPrice, "ArgoPetz: Insufficient CRO!");

        // Check if mints does not exceed MAX_SUPPLY
        require(totalSupply() + _amount <= MAX_SUPPLY, "ArgoPetz: Exceeded Max Supply for ArgoPetz!");

        // Check if mints does not exceed max wallet allowance for public sale
        require(
            whitelistMintCount[msg.sender] + _amount <= WHITELIST_MAX_MINT,
            "ArgoPetz: Wallet has already minted Max Amount for Whitelist Mint!"
        );

        whitelistMintCount[msg.sender] += _amount;
        for (uint256 i; i < _amount; ) {
            uint256 tokenId = _useRandomAvailableTokenId();
            _safeMint(to, tokenId);
            unchecked {
                ++i;
            }
        }
    }

    function publicMint(address to, uint256 _amount) external payable {
        // Check if public sale is open
        require(stage == 2, "ArgoPetz: Public Sale Closed!");
        // Check if enough ETH is sent
        require(msg.value == _amount * publicMintPrice, "ArgoPetz: Insufficient CRO!");

        // Check if mints does not exceed total max supply
        require(totalSupply() + _amount <= MAX_SUPPLY, "ArgoPetz: Max Supply for Public Mint Reached!");
        // Check if mints does not exceed max wallet allowance for public sale
        require(
            publicMintCount[msg.sender] + _amount <= PUBLIC_MAX_MINT,
            "ArgoPetz: Wallet has already minted Max Amount for Public Mint!"
        );
        publicMintCount[msg.sender] += _amount;
        for (uint256 i; i < _amount; ) {
            uint256 tokenId = _useRandomAvailableTokenId();
            _safeMint(to, tokenId);
            unchecked {
                ++i;
            }
        }
    }

    function whitelistSigned(
        address sender,
        bytes calldata nonce,
        bytes calldata signature,
        uint8 _stage
    ) private view returns (bool) {
        bytes32 _hash = keccak256(abi.encodePacked(sender, nonce, _stage));
        return WHITELIST_SIGNER_ADDRESS == ECDSA.toEthSignedMessageHash(_hash).recover(signature);
    }

    function withdraw() external {
        (bool sent, ) = WITHDRAW_ADDRESS.call{ value: address(this).balance }("");
        if (!sent) {
            revert WithdrawFailed();
        }
    }

    // ------------
    // Mint
    // ------------

    function setPublicMintPrice(uint256 _publicMintPrice) public onlyOwner {
        publicMintPrice = _publicMintPrice;
    }

    function setWhitelistMintPrice(uint256 _whitelistMintPrice) public onlyOwner {
        whitelistMintPrice = _whitelistMintPrice;
    }

    function setStage(uint8 _newStage) public onlyOwner {
        stage = _newStage;
    }

    // ------------
    // Total Supply
    // ------------
    function totalSupply() public view returns (uint256) {
        unchecked {
            // Does not need to account for burns as they aren't supported.
            return MAX_SUPPLY - _numAvailableRemainingTokens;
        }
    }

    // --------
    // Metadata
    // --------
    function tokenURI(uint256 tokenId) public view override returns (string memory) {
        if (_ownerOf[tokenId] == address(0)) {
            revert InvalidTokenId();
        }
        return string(abi.encodePacked(baseURI, tokenId.toString()));
    }

    function setBaseURI(string memory _baseURI_) public onlyOwner {
        baseURI = _baseURI_;
    }

    // --------
    // EIP-2981
    // --------
    function setDefaultRoyalty(address receiver, uint96 feeNumerator) external onlyOwner {
        _setDefaultRoyalty(receiver, feeNumerator);
    }

    function setTokenRoyalty(uint256 tokenId, address receiver, uint96 feeNumerator) external onlyOwner {
        _setTokenRoyalty(tokenId, receiver, feeNumerator);
    }

    // -------
    // EIP-165
    // -------
    function supportsInterface(bytes4 interfaceId) public view override(ERC721, ERC2981) returns (bool) {
        return ERC721.supportsInterface(interfaceId) || ERC2981.supportsInterface(interfaceId);
    }
}

contracts/ArgoQuest.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import "@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "hardhat/console.sol";

contract ArgoQuest is ERC721Holder, Ownable {
    IERC721 public argonauts;
    IERC721 public argopetz;

    uint8 public maxCrews = 3;
    // Track ownership of argonauts
    mapping(uint256 => address) public argonautsOwners;
    // Track ownership of argopetz
    mapping(uint256 => address) public argopetzOwners;
    bool public canQuest;
    mapping(uint256 => uint256) public tokensLastQuestedAt; // argonaut tokenId => timestamp
    mapping(uint256 => uint256[]) public questCrews; // argonaut tokenId => argopetz tokenIds
    mapping(uint256 => uint256[]) public argopetzCrew; // argopetz tokenId => argonaut tokenId [array of 1 uint256]
    uint256 public constant MAX_SUPPLY = 8888;
    event QuestStarted(uint256 indexed tokenId, uint256 questStartedAt, uint256[] crews);
    event QuestEdited(uint256 indexed tokenId, uint256 questStartedAt, uint256[] crews, uint256 questEditedAt);
    event QuestStopped(uint256 indexed tokenId, uint256 questStartedAt, uint256 questStoppedAt);

    constructor(address _argonauts, address _argopetz) {
        argonauts = IERC721(_argonauts);
        argopetz = IERC721(_argopetz);
    }

    // =============== Questing ===============

    struct QuestInfo {
        uint256 tokenId;
        uint256[] argopetzTokenIds;
    }

    function batchStartQuest(QuestInfo[] calldata questInfos) external {
        uint256 batch = questInfos.length;
        for (uint256 i; i < batch; ) {
            startQuest(questInfos[i].tokenId, questInfos[i].argopetzTokenIds);
            unchecked {
                ++i;
            }
        }
    }

    function batchEditQuest(QuestInfo[] calldata questInfos) external {
        require(canQuest, "questing not open");
        require(address(argopetz) != address(0), "Argopetz contract not set");

        uint256 batch = questInfos.length;
        for (uint256 i; i < batch; ) {
            uint256 tokenId = questInfos[i].tokenId;
            require(tokensLastQuestedAt[tokenId] > 0, "quested not started for [argonaut tokenId]");

            _resetCrew(tokenId);
            unchecked {
                ++i;
            }
        }

        for (uint256 i; i < batch; ) {
            uint256 tokenId = questInfos[i].tokenId;
            uint256[] calldata argopetzTokenIds = questInfos[i].argopetzTokenIds;

            require(argopetzTokenIds.length <= maxCrews, "too many crews [argopetzTokenIds]");

            _addCrew(tokenId, argopetzTokenIds);
            emit QuestEdited(tokenId, tokensLastQuestedAt[tokenId], argopetzTokenIds, block.timestamp);
            unchecked {
                ++i;
            }
        }
    }

    function batchStopQuest(uint256[] calldata tokenIds) external {
        uint256 batch = tokenIds.length;
        for (uint256 i; i < batch; ) {
            stopQuest(tokenIds[i]);
            unchecked {
                ++i;
            }
        }
    }

    function startQuest(uint256 tokenId, uint256[] calldata argopetzTokenIds) public {
        require(canQuest, "questing not open");
        require(address(argopetz) != address(0), "argopetz not set");
        require(tokensLastQuestedAt[tokenId] == 0, "quested already started for [argonaut tokenId]");
        require(argopetzTokenIds.length <= maxCrews, "too many crews [argopetzTokenIds]");
        _addCrew(tokenId, argopetzTokenIds);

        tokensLastQuestedAt[tokenId] = block.timestamp;

        emit QuestStarted(tokenId, block.timestamp, argopetzTokenIds);
    }

    function editQuest(uint256 tokenId, uint256[] calldata argopetzTokenIds) public {
        require(canQuest, "questing not open");
        require(address(argopetz) != address(0), "argopetz not set");
        require(tokensLastQuestedAt[tokenId] > 0, "quested not started for [argonaut tokenId]");
        require(argopetzTokenIds.length <= maxCrews, "too many crews [argopetzTokenIds]");

        _resetCrew(tokenId);
        _addCrew(tokenId, argopetzTokenIds);

        emit QuestEdited(tokenId, tokensLastQuestedAt[tokenId], argopetzTokenIds, block.timestamp);
    }

    function _addCrew(uint256 tokenId, uint256[] calldata argopetzTokenIds) private {
        uint256 crews = argopetzTokenIds.length;
        argonautsOwners[tokenId] = msg.sender;
        uint256[] memory wrapper = new uint256[](1);
        wrapper[0] = tokenId;
        if (crews >= 1) {
            for (uint256 i; i < crews; ) {
                uint256 pTokenId = argopetzTokenIds[i];
                argopetzOwners[pTokenId] = msg.sender;
                uint256[] storage existCheck = argopetzCrew[pTokenId];
                if (existCheck.length != 0) {
                    removeCrew(pTokenId);
                }
                argopetz.safeTransferFrom(msg.sender, address(this), pTokenId);
                argopetzCrew[pTokenId] = wrapper;
                unchecked {
                    ++i;
                }
            }
            questCrews[tokenId] = argopetzTokenIds;
        }
        argonauts.safeTransferFrom(msg.sender, address(this), tokenId);
    }

    function removeCrew(uint256 argopetzTokenId) public {
        require(address(argopetz) != address(0), "argopetz not set");
        require(argopetzOwners[argopetzTokenId] == msg.sender, "not argopetz owner");

        uint256[] storage existCheck = argopetzCrew[argopetzTokenId];
        require(existCheck.length != 0, "argopetzTokenId not questing");
        uint256 tokenId = existCheck[0];
        uint256 empty = MAX_SUPPLY;

        uint256[] memory pTokenIds = questCrews[tokenId];
        uint256 crews = pTokenIds.length;
        uint256 crewLength = pTokenIds.length;
        for (uint256 i; i < crews; ) {
            uint256 pTokenId = pTokenIds[i];
            if (pTokenId == argopetzTokenId) {
                argopetzOwners[pTokenId] = address(0);
                pTokenIds[i] = empty;
                crewLength--;
                // Transfer back to owner
                argopetz.safeTransferFrom(address(this), msg.sender, pTokenId);
            }
            unchecked {
                ++i;
            }
        }

        require(pTokenIds.length != crewLength, "argopetzTokenId not in crew");

        uint256[] memory newCrews = new uint256[](crewLength);
        uint256 activeIdx;
        for (uint256 i; i < crews; ) {
            if (pTokenIds[i] != empty) {
                newCrews[activeIdx++] = pTokenIds[i];
            }
            unchecked {
                ++i;
            }
        }

        questCrews[tokenId] = newCrews;
        argopetzCrew[argopetzTokenId] = new uint256[](0);
    }

    function _resetCrew(uint256 tokenId) private {
        uint256[] storage argopetzTokenIds = questCrews[tokenId];
        uint256 crews = argopetzTokenIds.length;
        if (crews >= 1) {
            uint256[] memory empty = new uint256[](0);
            for (uint256 i; i < crews; ) {
                uint256 pTokenId = argopetzTokenIds[i];
                argopetzCrew[pTokenId] = empty;
                // Transfer back to owner
                argopetz.safeTransferFrom(address(this), argopetzOwners[pTokenId], pTokenId);
                unchecked {
                    ++i;
                }
            }
            questCrews[tokenId] = empty;
        }
        argonauts.safeTransferFrom(address(this), argonautsOwners[tokenId], tokenId);
    }

    function stopQuest(uint256 tokenId) public {
        require(tokensLastQuestedAt[tokenId] > 0, "quested not started for [argonaut tokenId]");
        require(argonautsOwners[tokenId] == msg.sender, "not argonauts owner");
        _resetCrew(tokenId);

        uint256 tlqa = tokensLastQuestedAt[tokenId];
        tokensLastQuestedAt[tokenId] = 0;
        emit QuestStopped(tokenId, tlqa, block.timestamp);
    }

    function isArgopetzQuesting(uint256 tokenId) external view returns (bool) {
        uint256[] storage existCheck = argopetzCrew[tokenId];
        return existCheck.length > 0;
    }

    function getTokenInfo(
        uint256 tokenId
    ) external view returns (uint256 lastQuestedAt, uint256[] memory crewTokenIds) {
        return (tokensLastQuestedAt[tokenId], questCrews[tokenId]);
    }

    // =============== Admin ===============

    function setCanQuest(bool b) external onlyOwner {
        canQuest = b;
    }

    function setArgopetz(address addr) external onlyOwner {
        argopetz = IERC721(addr);
    }

    function setArgonauts(address addr) external onlyOwner {
        argonauts = IERC721(addr);
    }
}

contracts/common/ERC721.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

/// @notice Modern, minimalist, and gas-optimized ERC721 implementation.
/// @author SolDAO (https://github.com/Sol-DAO/solbase/blob/main/src/tokens/ERC721.sol)
/// @author Modified from Solmate (https://github.com/transmissions11/solmate/blob/main/src/tokens/ERC721.sol)
abstract contract ERC721 {
    /// -----------------------------------------------------------------------
    /// Events
    /// -----------------------------------------------------------------------

    event Transfer(address indexed from, address indexed to, uint256 indexed id);

    event Approval(address indexed owner, address indexed spender, uint256 indexed id);

    event ApprovalForAll(address indexed owner, address indexed operator, bool approved);

    /// -----------------------------------------------------------------------
    /// Custom Errors
    /// -----------------------------------------------------------------------

    error NotMinted();

    error ZeroAddress();

    error Unauthorized();

    error WrongFrom();

    error InvalidRecipient();

    error UnsafeRecipient();

    error AlreadyMinted();

    /// -----------------------------------------------------------------------
    /// Metadata Storage/Logic
    /// -----------------------------------------------------------------------

    string public name;

    string public symbol;

    function tokenURI(uint256 id) public view virtual returns (string memory);

    /// -----------------------------------------------------------------------
    /// ERC721 Balance/Owner Storage
    /// -----------------------------------------------------------------------

    mapping(uint256 => address) internal _ownerOf;

    mapping(address => uint256) internal _balanceOf;

    function ownerOf(uint256 id) public view virtual returns (address owner) {
        if ((owner = _ownerOf[id]) == address(0)) revert NotMinted();
    }

    function balanceOf(address owner) public view virtual returns (uint256) {
        if (owner == address(0)) revert ZeroAddress();
        return _balanceOf[owner];
    }

    /// -----------------------------------------------------------------------
    /// ERC721 Approval Storage
    /// -----------------------------------------------------------------------

    mapping(uint256 => address) public getApproved;

    mapping(address => mapping(address => bool)) public isApprovedForAll;

    /// -----------------------------------------------------------------------
    /// Constructor
    /// -----------------------------------------------------------------------

    constructor(string memory _name, string memory _symbol) {
        name = _name;
        symbol = _symbol;
    }

    /// -----------------------------------------------------------------------
    /// ERC721 Logic
    /// -----------------------------------------------------------------------

    function approve(address spender, uint256 id) public virtual {
        address owner = _ownerOf[id];

        if (msg.sender != owner && !isApprovedForAll[owner][msg.sender]) revert Unauthorized();

        getApproved[id] = spender;

        emit Approval(owner, spender, id);
    }

    function setApprovalForAll(address operator, bool approved) public virtual {
        isApprovedForAll[msg.sender][operator] = approved;

        emit ApprovalForAll(msg.sender, operator, approved);
    }

    function transferFrom(address from, address to, uint256 id) public virtual {
        if (from != _ownerOf[id]) revert WrongFrom();

        if (to == address(0)) revert InvalidRecipient();

        if (msg.sender != from && !isApprovedForAll[from][msg.sender] && msg.sender != getApproved[id])
            revert Unauthorized();

        // Underflow of the sender's balance is impossible because we check for
        // ownership above and the recipient's balance can't realistically overflow.
        unchecked {
            _balanceOf[from]--;

            _balanceOf[to]++;
        }

        _ownerOf[id] = to;

        delete getApproved[id];

        emit Transfer(from, to, id);
    }

    function safeTransferFrom(address from, address to, uint256 id) public virtual {
        transferFrom(from, to, id);

        if (to.code.length != 0) {
            if (
                ERC721TokenReceiver(to).onERC721Received(msg.sender, from, id, "") !=
                ERC721TokenReceiver.onERC721Received.selector
            ) revert UnsafeRecipient();
        }
    }

    function safeTransferFrom(address from, address to, uint256 id, bytes calldata data) public virtual {
        transferFrom(from, to, id);

        if (to.code.length != 0) {
            if (
                ERC721TokenReceiver(to).onERC721Received(msg.sender, from, id, data) !=
                ERC721TokenReceiver.onERC721Received.selector
            ) revert UnsafeRecipient();
        }
    }

    /// -----------------------------------------------------------------------
    /// ERC165 Logic
    /// -----------------------------------------------------------------------

    function supportsInterface(bytes4 interfaceId) public view virtual returns (bool) {
        return
            interfaceId == 0x01ffc9a7 || // ERC165 Interface ID for ERC165
            interfaceId == 0x80ac58cd || // ERC165 Interface ID for ERC721
            interfaceId == 0x5b5e139f; // ERC165 Interface ID for ERC721Metadata
    }

    /// -----------------------------------------------------------------------
    /// Internal Mint/Burn Logic
    /// -----------------------------------------------------------------------

    function _mint(address to, uint256 id) internal virtual {
        if (to == address(0)) revert InvalidRecipient();

        if (_ownerOf[id] != address(0)) revert AlreadyMinted();

        // Counter overflow is incredibly unrealistic.
        unchecked {
            _balanceOf[to]++;
        }

        _ownerOf[id] = to;

        emit Transfer(address(0), to, id);
    }

    function _burn(uint256 id) internal virtual {
        address owner = _ownerOf[id];

        if (owner == address(0)) revert NotMinted();

        // Ownership check above ensures no underflow.
        unchecked {
            _balanceOf[owner]--;
        }

        delete _ownerOf[id];

        delete getApproved[id];

        emit Transfer(owner, address(0), id);
    }

    /// -----------------------------------------------------------------------
    /// Internal Safe Mint Logic
    /// -----------------------------------------------------------------------

    function _safeMint(address to, uint256 id) internal virtual {
        _mint(to, id);

        if (to.code.length != 0) {
            if (
                ERC721TokenReceiver(to).onERC721Received(msg.sender, address(0), id, "") !=
                ERC721TokenReceiver.onERC721Received.selector
            ) revert UnsafeRecipient();
        }
    }

    function _safeMint(address to, uint256 id, bytes memory data) internal virtual {
        _mint(to, id);

        if (to.code.length != 0) {
            if (
                ERC721TokenReceiver(to).onERC721Received(msg.sender, address(0), id, data) !=
                ERC721TokenReceiver.onERC721Received.selector
            ) revert UnsafeRecipient();
        }
    }
}

/// @notice A generic interface for a contract which properly accepts ERC721 tokens.
/// @author SolDAO (https://github.com/Sol-DAO/solbase/blob/main/src/tokens/ERC721.sol)
/// @author Modified from Solmate (https://github.com/transmissions11/solmate/blob/main/src/tokens/ERC721.sol)
abstract contract ERC721TokenReceiver {
    function onERC721Received(address, address, uint256, bytes calldata) external virtual returns (bytes4) {
        return ERC721TokenReceiver.onERC721Received.selector;
    }
}

contracts/common/SetUtils.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

library SetUtils {
    using EnumerableSet for EnumerableSet.AddressSet;
    using EnumerableSet for EnumerableSet.UintSet;

    /// @dev Converst an iterable set of addresses to a corresponding array
    function toArray(EnumerableSet.AddressSet storage _set) internal view returns (address[] memory) {
        uint256 numElements = _set.length();
        address[] memory elements = new address[](numElements);
        for (uint256 i = 0; i < numElements; ++i) {
            elements[i] = _set.at(i);
        }
        return elements;
    }

    /// @dev Converst an iterable set of uint to a corresponding array
    function toArray(EnumerableSet.UintSet storage _set) internal view returns (uint256[] memory) {
        uint256 numElements = _set.length();
        uint256[] memory elements = new uint256[](numElements);
        for (uint256 i = 0; i < numElements; ++i) {
            elements[i] = _set.at(i);
        }
        return elements;
    }
}

contracts/StarMapCrafting.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.18;

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "./common/SetUtils.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";

/// @title StarMapCrafting
/// @author Kratos
/// @notice This contract is used to craft starmaps with Argopetz

contract StarMapCrafting is Ownable, IERC721Receiver {
    using EnumerableSet for EnumerableSet.UintSet;
    using EnumerableSet for EnumerableSet.AddressSet;
    using SetUtils for EnumerableSet.UintSet;
    using SetUtils for EnumerableSet.AddressSet;

    /// @notice Mapping of (user) => (EnumerableSet of NFT tokenIds) Number of NFTs staked for each user
    mapping(address => EnumerableSet.UintSet) internal userStakedNFTs;

    /// @notice Starmap crafting time
    uint256 public starmapCraftingTime = 3 days;

    /// @notice Event emitted when an NFT is staked
    event StakedNFT(address indexed user, uint256 indexed nftId, uint256 startTime);

    /// @notice Event emitted when an NFT is unstaked
    event UnstakedNFT(address indexed user, uint256 indexed nftId, uint256 unstakeTime);

    /// @notice Event emitted when crafting time is set
    event StarmapCraftingTimeSet(uint256 craftingTime);

    IERC721 public argopetz;

    constructor(address _argopetz) {
        // Set the Argopetz token address
        argopetz = IERC721(_argopetz);
    }

    /**
     * @notice Stake NFTs
     * @param _nftIds The IDs of the NFTs
     */
    function stakeNFT(uint256[] calldata _nftIds) external {
        // More than 1 NFT must be staked
        require(_nftIds.length > 0, "Must stake at least 1 NFT");
        for (uint256 i; i < _nftIds.length; ) {
            // Add staked NFT to the stakedNFTs mapping
            userStakedNFTs[msg.sender].add(_nftIds[i]);
            // Transfer the NFT to this contract
            argopetz.safeTransferFrom(msg.sender, address(this), _nftIds[i]);
            // Emit the StakedNFT event
            emit StakedNFT(msg.sender, _nftIds[i], block.timestamp);
            unchecked {
                ++i;
            }
        }
    }

    /**
     * @notice Unstake NFTs
     * @param _nftIds The IDs of the NFTs
     */
    function unstakeNFT(uint256[] calldata _nftIds) external {
        for (uint256 i; i < _nftIds.length; ) {
            // Check if the NFT is staked
            require(userStakedNFTs[msg.sender].contains(_nftIds[i]), "NFT is not staked");
            // Remove the NFT from the stakedNFTs mapping
            userStakedNFTs[msg.sender].remove(_nftIds[i]);
            // Transfer the NFT back to the user
            argopetz.safeTransferFrom(address(this), msg.sender, _nftIds[i]);
            emit UnstakedNFT(msg.sender, _nftIds[i], block.timestamp);
            unchecked {
                ++i;
            }
        }
    }

    /**
     * @notice Get staked NFT count of a user
     * @param _user The address of the user
     * @return The number of staked NFTs
     */
    function getStakedNFTCount(address _user) external view returns (uint256) {
        return userStakedNFTs[_user].length();
    }

    /**
     * @notice Get staked NFTs of a user
     * @param _user The address of the user
     * @return The IDs of the NFTs
     */
    function getStakedNFTs(address _user) external view returns (uint256[] memory) {
        return userStakedNFTs[_user].toArray();
    }

    /**
     * @notice Set starmap crafting time
     * @param _starmapCraftingTime The starmap crafting time
     */
    function setStarmapCraftingTime(uint256 _starmapCraftingTime) external onlyOwner {
        starmapCraftingTime = _starmapCraftingTime;
        // Emit event
        emit StarmapCraftingTimeSet(_starmapCraftingTime);
    }

    // Set argopetz
    function setArgopetz(address _argopetz) external onlyOwner {
        argopetz = IERC721(_argopetz);
    }

    function onERC721Received(
        address,
        address,
        uint256,
        bytes calldata
    ) external pure override returns (bytes4) {
