contracts/Airdropper.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.11;

// Import IERC20, IERC721, IERC1155
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";

contract Airdropper {
    /**
     * @dev Airdrop ERC20 tokens
     * @param token The address of the ERC20 token
     * @param _addresses The addresses of the recipients
     * @param _amounts The amounts of tokens to send
     */
    function airdropERC20(IERC20 token, address[] calldata _addresses, uint256[] calldata _amounts) external {
        for (uint256 i; i < _addresses.length; i++) {
            token.transferFrom(msg.sender, _addresses[i], _amounts[i]);
        }
    }

    /**
     * @dev Airdrop ERC721 tokens
     * @param token The address of the ERC721 token
     * @param _addresses The addresses of the recipients
     * @param _tokenIds The token IDs of the tokens to send
     */
    function airdropERC721(IERC721 token, address[] calldata _addresses, uint256[] calldata _tokenIds) external {
        for (uint256 i; i < _addresses.length; i++) {
            token.transferFrom(msg.sender, _addresses[i], _tokenIds[i]);
        }
    }

    /**
     * @dev Airdrop ERC1155 tokens
     * @param token The address of the ERC1155 token
     * @param _addresses The addresses of the recipients
     * @param _tokenIds The token IDs of the tokens to send
     * @param _amounts The amounts of tokens to send
     */
    function airdropERC1155(
        IERC1155 token,
        address[] calldata _addresses,
        uint256[] calldata _tokenIds,
        uint256[] calldata _amounts
    ) external {
        for (uint256 i; i < _addresses.length; i++) {
            token.safeTransferFrom(msg.sender, _addresses[i], _tokenIds[i], _amounts[i], "");
        }
    }

    /**
     * @dev Airdrop ETH
     * @param _addresses The addresses of the recipients
     * @param _amounts The amounts of ETH to send
     */
    function airdropETH(address[] calldata _addresses, uint256[] calldata _amounts) external payable {
        for (uint256 i; i < _addresses.length; i++) {
            payable(_addresses[i]).transfer(_amounts[i]);
        }
    }
}

contracts/Atlantis.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.11;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol";
import "./interface/IAtlantis.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "hardhat/console.sol"; // TODO: Remove in production

/// @title Atlantis
contract Atlantis is Ownable, ERC721Holder, IAtlantis, ReentrancyGuard {
    // Address of stardust
    IERC20 public stardust;
    // Planet address
    IAtlantisPlanets public atlantisPlanets;
    // Atlantis materials address
    IAtlantisMaterials public atlantisMaterials;
    // When settings these, remember is * 100
    // Base rate of stardust per expedition
    uint256 public baseStardustRate = 0.5 ether;
    // Level parameter
    uint256 public levelParameter = 1;
    // Rarity parameter
    uint256 public rarityParameter = 20;
    // Mapping of level to material rate
    mapping(uint8 => uint256) public materialRate;
    // Mapping of NFTs staked to material multiplier
    mapping(uint8 => uint256) public nftMaterialMultiplier;
    // Mapping of orbit to rarity multiplier
    mapping(uint8 => uint256) public rarityMultiplier;
    // Mapping of address to boolean whitelisted status
    mapping(address => bool) public whitelistedCollections;
    // Mapping of user address to array of Expedition struct
    mapping(uint256 => Expedition) public expeditions;
    // Variable to track current Expedition id
    uint256 public currentExpeditionId = 0;
    // Variable to track expedition duration
    uint256 public expeditionDuration = 3 days;

    // Event for starting an expedition
    event ExpeditionStarted(
        address indexed user,
        uint256 indexed expeditionId,
        uint256[] tokenIds,
        address[] collectionIds,
        uint256 startTime,
        uint256 endTime
    );
    event ExpeditionEnded(address indexed user, uint256 indexed expeditionId, uint256 timeEnded);
    event RewardsClaimed(
        address indexed user,
        uint256 indexed expeditionId,
        uint256 indexed materialId,
        uint256 materialGenerated,
        uint256 stardust
    );

    constructor(address _stardust, IAtlantisPlanets _atlantisPlanets, IAtlantisMaterials _atlantisMaterials) {
        // Set stardust address
        stardust = IERC20(_stardust);
        // Set Atlantis planets address
        atlantisPlanets = _atlantisPlanets;
        // Set Atlantis materials address
        atlantisMaterials = _atlantisMaterials;
    }

    // --------------------- VIEW FUNCTINOS ---------------------
    /**
     * @notice Get Expedition Info
     * @param _expeditionId Expedition Id
     * @return Expedition of given expeditionId
     */
    function getExpeditionInfo(uint256 _expeditionId) external view returns (Expedition memory) {
        Expedition memory expedition = expeditions[_expeditionId];
        return expedition;
    }

    /**
     * @notice Returns number of NFTs that can be staked on the planet
     * @param _planetLevel Level of the planet
     */
    function calculateNFTsStakable(uint8 _planetLevel) public pure returns (uint8 nftsStakable) {
        if (_planetLevel >= 1 && _planetLevel < 20) {
            nftsStakable = 0;
        } else if (_planetLevel >= 20 && _planetLevel < 30) {
            nftsStakable = 1;
        } else if (_planetLevel >= 30 && _planetLevel < 40) {
            nftsStakable = 2;
        } else if (_planetLevel >= 40) {
            nftsStakable = 3;
        }
    }

    /**
     * @notice Returns the amount of starust earned
     * @param _planetLevel Level of the planet
     * @param _planetOrbit Orbit tier of the planet
     * From Spreadsheet:
     * Stardust earned per 72 hours expedition =
     * (((planet_level -1) ^ 2) * level_parameter + 1) * ((planet_rarity -1) * rarity_parameter + 1) * xDAWG_base_rate
     */
    function calculateStardustPerExpedition(uint8 _planetLevel, uint8 _planetOrbit) public view returns (uint256) {
        // Convert _planetLevel and _planetOrbit to uint256
        uint256 __planetLevel = uint256(_planetLevel);
        uint256 __planetOrbit = uint256(_planetOrbit);
        return
            (((((__planetLevel - 1) * 100) ** 2 * levelParameter + 1000000) / 10000) *
                (((__planetOrbit - 1) * 100) * rarityParameter + 10000) *
                baseStardustRate) / 1000000;
    }

    /**
     * @notice Returns the amount of materials earned
     * @param _planetLevel ExpeditionId of the expedition
     * @param _planetOrbit ExpeditionId of the expedition
     * @param _amountArgonauts ExpeditionId of the expedition
     * From Spreadsheet:
     * Material generated / expedition =
     * roundup(base_material_rate * planet_rarity * argonauts_material_multiplier)
     */
    function materialGenerated(
        uint8 _planetLevel,
        uint8 _planetOrbit,
        uint8 _amountArgonauts
    ) public view returns (uint256) {
        uint256 _temp = materialRate[_planetLevel] *
            (rarityMultiplier[_planetOrbit] * 10) *
            nftMaterialMultiplier[_amountArgonauts] *
            10;
        if ((_temp % 10000) != 0) {
            return _temp / 10000 + 1;
        } else {
            return _temp / 10000;
        }
    }

    function calculateRewards(uint256 _expeditionId) public view returns (uint256, AtlantisLib.Element, uint256) {
        // Get expedition
        Expedition memory expedition = expeditions[_expeditionId];
        uint256 planetId = expedition.planetId;
        // Get Planet NFT
        AtlantisLib.Planet memory planet = atlantisPlanets.getPlanetDetails(planetId);
        // Get Planet level and Orbit
        uint8 planetLevel = planet.level;
        uint8 planetOrbit = uint8(planet.orbit) + 1;

        // Material multiplier is in terms of 100s, 1.2 = 120
        uint256 _materialGenerated = materialGenerated(planetLevel, planetOrbit, uint8(expedition.tokenIds.length));

        // Calculate stardust earned
        uint256 _stardustEarned = calculateStardustPerExpedition(planetLevel, planetOrbit);
        // Return rewards
        return (_materialGenerated, planet.element, _stardustEarned);
    }

    // --------------------- PUBLIC FUNCTINOS ---------------------

    /**
     * @notice Starts an Atlantis Expedition
     * @dev NFTs sent on expedition must be whitelisted
     * @param _planetId PlanetId of planets to send NFTs on expedition
     * @param _collectionAddresses Collection Addresses of nfts to send on expedition
     * @param _tokenIds TokenIds of nfts to send on expedition
     */
    function startExpedition(
        uint256 _planetId,
        address[] memory _collectionAddresses,
        uint256[] memory _tokenIds
    ) external {
        // Get planetLevel from planetId
        uint8 planetLevel = atlantisPlanets.getPlanetDetails(_planetId).level;
        // REVERT: If _collectionAddresses.length != _tokenIds.length
        if (_collectionAddresses.length != _tokenIds.length) {
            revert InvalidExpeditionInput();
        }

        // REVERT: If staked NFTs is more than limit
        if (_collectionAddresses.length > calculateNFTsStakable(planetLevel)) {
            revert InvalidExpeditionInput();
        }

        // Record the expedition
        Expedition memory expedition = expeditions[currentExpeditionId] = Expedition({
            collectionAddresses: _collectionAddresses,
            tokenIds: _tokenIds,
            id: currentExpeditionId,
            planetId: _planetId,
            startTime: block.timestamp,
            endTime: block.timestamp + expeditionDuration,
            owner: msg.sender,
            hasEnded: false
        });
        // Increment currentExpeditionId
        currentExpeditionId++;

        // Transfer Planet NFT to this contract
        atlantisPlanets.transferFrom(msg.sender, address(this), _planetId);
        // Transfer Staked NFTs to this contract
        for (uint256 i; i < _collectionAddresses.length; i++) {
            // Check if collection address is whitelisted
            if (!whitelistedCollections[_collectionAddresses[i]]) {
                revert NFTCollectionNotWhitelisted();
            }
            IERC721(_collectionAddresses[i]).transferFrom(msg.sender, address(this), _tokenIds[i]);
        }
        // Emit event
        emit ExpeditionStarted(
            msg.sender,
            currentExpeditionId,
            expedition.tokenIds,
            expedition.collectionAddresses,
            expedition.startTime,
            expedition.endTime
        );
    }

    /**
     * @notice Stops an Atlantis Expedition
     * @param _expeditionId ExpeditionId of expedition to stop
     * @dev - Must be called by the owner of the expedition
     *      - Must be called after the expedition has ended (block.timestamp > block.timestamp)
     *      - Must be called before the expedition has been claimed (hasEnded == false)
     */
    function endExpedition(uint256 _expeditionId) public {
        Expedition storage currentExpedition = expeditions[_expeditionId];

        // REVERT: If expedition has ended
        if (currentExpedition.hasEnded) {
            revert ExpeditionAlreadyEnded();
        }
        // REVERT: If current time is less than end time
        if (currentExpedition.endTime > block.timestamp) {
            revert ExpeditionInProgress();
        }
        // REVERT: If caller is not the owner of the expedition
        if (currentExpedition.owner != msg.sender) revert NotOwnerOfExpedition();

        // Transfer all staked NFTs back to user
        for (uint256 i; i < currentExpedition.collectionAddresses.length; i++) {
            IERC721(currentExpedition.collectionAddresses[i]).transferFrom(
                address(this),
                msg.sender,
                currentExpedition.tokenIds[i]
            );
        }
        // Transfer Planet NFT back to user
        atlantisPlanets.transferFrom(address(this), msg.sender, currentExpedition.planetId);

        _claimReward(_expeditionId);
        currentExpedition.hasEnded = true;

        emit ExpeditionEnded(msg.sender, _expeditionId, block.timestamp);
    }

    /**
     * @notice Ends multiple Atlantis Expeditions
     * @param _expeditionIds ExpeditionIds of expeditions to stop
     * @dev - Must be called by the owner of the expedition
     *      - Must be called after the expedition has ended (block.timestamp > block.timestamp)
     *      - Must be called before the expedition has been claimed (hasEnded == false)
     */
    function endExpeditions(uint256[] calldata _expeditionIds) external {
        // Loop through all expedition ids
        for (uint256 i; i < _expeditionIds.length; i++) {
            endExpedition(_expeditionIds[i]);
        }
    }

    /**
     * @notice Claims rewards for an expedition
     * @param _expeditionId ExpeditionId of expedition to claim rewards for
     * @dev - Must only be called by the owner of the expedition
     *      - Must only be called after the expedition has ended (block.timestamp > block.timestamp)
     *      - Stardust is minted to the caller
     *      - AtlantisMaterials are minted to the caller
     *      - EndDate is Updated
     */
    function claimReward(uint256 _expeditionId) public nonReentrant {
        // check if claimant is owner of expeditionId
        require(expeditions[_expeditionId].owner == msg.sender, "Atlantis: User does not own expedition");
        _claimReward(_expeditionId);
    }

    /**
     * @notice Claims rewards for multiple expeditions
     * @param _expeditionIds ExpeditionIds of expeditions to claim rewards for
     */
    function claimRewards(uint256[] calldata _expeditionIds) external {
        // Loop through all expedition ids
        for (uint256 i; i < _expeditionIds.length; i++) {
            claimReward(_expeditionIds[i]);
        }
    }

    // Internal _claimRewards function that is used by claimRewards and endExpedition
    function _claimReward(uint256 _expeditionId) internal {
        // Get expedition
        Expedition memory expedition = expeditions[_expeditionId];
        // Check if expedition has ended
        require(expedition.endTime < block.timestamp, "Atlantis: Expedition has not ended");
        AtlantisLib.Element materialId;
        uint256 _materialGenerated;
        uint256 _stardustEarned;
        // Calculate rewards
        (_materialGenerated, materialId, _stardustEarned) = calculateRewards(_expeditionId);
        // Distribute stardust rewards
        stardust.transfer(msg.sender, _stardustEarned);
        // Distribute material rewards
        atlantisMaterials.mint(msg.sender, uint8(materialId) + 1, _materialGenerated);

        // Update end time so that user cannot claim again
        expeditions[_expeditionId].hasEnded = true;
        // Emit event
        emit RewardsClaimed(msg.sender, _expeditionId, uint8(materialId) + 1, _materialGenerated, _stardustEarned);
    }

    // -------------------- ADMIN FUNCTIONS ----------------------
    /**
     * @notice Set the duration of an expedition
     * @param _duration The duration of an expedition in seconds
     */
    function setExpeditionDuration(uint256 _duration) external onlyOwner {
        expeditionDuration = _duration;
    }

    /**
     * @notice Set the whitelisted status of a collection
     * @param _collectionAddresses The address of the collection
     * @param _status The whitelisted status of the collection
     * @dev Only whitelisted collections can be staked
     */
    function setWhitelistedCollections(address[] memory _collectionAddresses, bool _status) external onlyOwner {
        for (uint256 i; i < _collectionAddresses.length; i++) {
            whitelistedCollections[_collectionAddresses[i]] = _status;
        }
    }

    /**
     * @notice Set the material rate for a level
     * @param _levels The level of the material
     * @param _rates The rate of the material
     */
    function setMaterialRate(uint8[] memory _levels, uint256[] memory _rates) external onlyOwner {
        if (_levels.length != _rates.length) revert InvalidSetMaterialRateInput();
        for (uint256 i; i < _levels.length; i++) {
            materialRate[_levels[i]] = _rates[i];
        }
    }

    /**
     * @notice Set the material multiplier for a NFT
     * @param _numNFTs The number of NFTs staked
     * @param _multipliers The multiplier of the rewards
     */
    function setNftMaterialMultiplier(uint8[] memory _numNFTs, uint256[] memory _multipliers) external onlyOwner {
        // require(_numNFTs.length == _multipliers.length, "Atlantis: Invalid input");
        if (_numNFTs.length != _multipliers.length) revert InvalidSetNFTMaterialMultiplierInput();

        for (uint256 i; i < _numNFTs.length; i++) {
            nftMaterialMultiplier[_numNFTs[i]] = _multipliers[i];
        }
    }

    // Set rarityMultiplier
    function setRarityMultiplier(uint8[] memory _rarity, uint256[] memory _multipliers) external onlyOwner {
        // require(_rarity.length == _multipliers.length, "Atlantis: Invalid input");
        if (_rarity.length != _multipliers.length) revert InvalidSetRarityMultiplierInput();

        for (uint256 i; i < _rarity.length; i++) {
            rarityMultiplier[_rarity[i]] = _multipliers[i];
        }
    }

    /**
     * @notice Set AtlantisMaterials Contract
     * @param _atlantisMaterials Contract address of AtlantisMaterials
     */
    function setAtlantisMaterials(IAtlantisMaterials _atlantisMaterials) external onlyOwner {
        atlantisMaterials = _atlantisMaterials;
    }

    /**
     * @notice Set AtlantisPlanets Contract
     * @param _atlantisPlanets Contract address of AtlantisMaterials
     */
    function setAtlantisPlanets(IAtlantisPlanets _atlantisPlanets) external onlyOwner {
        atlantisPlanets = _atlantisPlanets;
    }

    /**
     * @notice Set Stardust Contract
     * @param _stardust Contract address of Stardust
     */
    function setStardust(IERC20 _stardust) external onlyOwner {
        stardust = _stardust;
    }

    // Set baseStardustRate
    function setBaseStardustRate(uint256 _baseStardustRate) external onlyOwner {
        baseStardustRate = _baseStardustRate;
    }

    // Set levelParameter
    function setLevelParameter(uint256 _levelParameter) external onlyOwner {
        levelParameter = _levelParameter;
    }

    // Set rarityParameter
    function setRarityParameter(uint256 _rarityParameter) external onlyOwner {
        rarityParameter = _rarityParameter;
    }
}

contracts/AtlantisAuction.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./AtlantisPlanets.sol";

error AuctionSettled();
error AuctionNotInitialized();
error AuctionNotLive();
error ReservePriceNotMet();
error IncrementalPriceNotMet();
error BidsNotSorted();
error NonExistentBid();
error AuctionStillLive();
error WithdrawFailed();
error BidIncrementTooLow();
error NotEOA();

contract AtlantisAuction is Ownable, ReentrancyGuard {
    struct Bid {
        address bidder;
        uint192 amount;
        uint64 bidTime;
    }

    struct BidIndex {
        uint8 index;
        bool isSet;
    }

    event NewBid(address bidder, uint256 value);
    event BidIncreased(address bidder, uint256 oldValue, uint256 increment);
    event AuctionExtended();

    // The max number of top bids the auction will accept
    uint256 public constant MAX_NUM_BIDS = 12;

    // The token contract to mint from
    AtlantisPlanets public st;

    // The minimum amount of time left in an auction after a new bid is created
    uint256 public timeBuffer;

    // The minimum price accepted in an auction
    uint256 public reservePrice;

    // The minimum percentage difference between the last bid amount and the current bid
    uint8 public minBidIncrementPercentage;

    // The minimum amount a user needs to submit for a stacked bid
    uint256 public minStackedBidIncrement;

    // The start time of the auction
    uint256 public startTime;

    // The end time of the auction
    uint256 public endTime;

    // Whether or not the auction has settled.
    bool public auctionSettled;

    // The current highest bids made in the auction
    Bid[MAX_NUM_BIDS] public activeBids;

    // The mapping between an address and its active bid. The isSet flag differentiates the default
    // uint value 0 from an actual 0 value.
    mapping(address => BidIndex) public bidIndexes;

    constructor(
        AtlantisPlanets _st,
        uint256 _timeBuffer,
        uint256 _reservePrice,
        uint8 _minBidIncrementPercentage,
        uint256 _minStackedBidIncrement,
        uint256 _startTime,
        uint256 _endTime
    ) {
        st = _st;
        timeBuffer = _timeBuffer;
        reservePrice = _reservePrice;
        minBidIncrementPercentage = _minBidIncrementPercentage;
        minStackedBidIncrement = _minStackedBidIncrement;
        startTime = _startTime;
        endTime = _endTime;
    }

    modifier onlyEOA() {
        if (tx.origin != msg.sender) {
            revert NotEOA();
        }
        _;
    }

    /**
     * @notice Handle users' bids
     * @dev Bids must be made while the auction is live. Bids must meet a minimum reserve price.
     *
     * The first 8 bids made will be accepted as valid. Subsequent bids must be a percentage
     * higher than the lowest of the 8 active bids. When a low bid is replaced, the ETH will
     * be refunded back to the original bidder.
     *
     * If a valid bid comes in within the last `timeBuffer` seconds, the auction will be extended
     * for another `timeBuffer` seconds. This will continue until no new active bids come in.
     *
     * If a wallet makes a bid while it still has an active bid, the second bid will
     * stack on top of the first bid. If the second bid doesn't meet the `minStackedBidIncrement`
     * threshold, an error will be thrown. A wallet will only have one active bid at at time.
     */
    function bid() public payable nonReentrant onlyEOA {
        if (auctionSettled) {
            revert AuctionSettled();
        }
        if (startTime == 0 || endTime == 0) {
            revert AuctionNotInitialized();
        }
        if (block.timestamp < startTime || block.timestamp > endTime) {
            revert AuctionNotLive();
        }

        BidIndex memory existingIndex = bidIndexes[msg.sender];
        if (existingIndex.isSet) {
            // Case when the user already has an active bid
            if (msg.value < minStackedBidIncrement || msg.value == 0) {
                revert BidIncrementTooLow();
            }

            uint192 oldValue = activeBids[existingIndex.index].amount;
            unchecked {
                activeBids[existingIndex.index].amount = oldValue + uint192(msg.value);
            }
            activeBids[existingIndex.index].bidTime = uint64(block.timestamp);

            emit BidIncreased(msg.sender, oldValue, msg.value);
        } else {
            if (msg.value < reservePrice || msg.value == 0) {
                revert ReservePriceNotMet();
            }

            uint8 lowestBidIndex = getBidIndexToUpdate();
            uint256 lowestBidAmount = activeBids[lowestBidIndex].amount;
            address lowestBidder = activeBids[lowestBidIndex].bidder;

            unchecked {
                if (msg.value < lowestBidAmount + (lowestBidAmount * minBidIncrementPercentage) / 100) {
                    revert IncrementalPriceNotMet();
                }
            }

            // Refund lowest bidder and remove bidIndexes entry
            if (lowestBidder != address(0)) {
                delete bidIndexes[lowestBidder];
                _transferETH(lowestBidder, lowestBidAmount);
            }

            activeBids[lowestBidIndex] = Bid({
                bidder: msg.sender,
                amount: uint192(msg.value),
                bidTime: uint64(block.timestamp)
            });

            bidIndexes[msg.sender] = BidIndex({ index: lowestBidIndex, isSet: true });

            emit NewBid(msg.sender, msg.value);
        }

        // Extend the auction if the bid was received within `timeBuffer` of the auction end time
        if (endTime - block.timestamp < timeBuffer) {
            unchecked {
                endTime = block.timestamp + timeBuffer;
            }
            emit AuctionExtended();
        }
    }

    /**
     * @notice Gets the index of the entry in activeBids to update
     * @dev The index to return will be decided by the following rules:
     * If there are less than MAX_NUM_BIDS bids, the index of the first empty slot is returned.
     * If there are MAX_NUM_BIDS or more bids, the index of the lowest value bid is returned. If
     * there is a tie, the most recent bid with the low amount will be returned. If there is a tie
     * among bidTimes, the highest index is chosen.
     */
    function getBidIndexToUpdate() public view returns (uint8) {
        uint256 minAmount = activeBids[0].amount;
        // If the first value is 0 then we can assume that no bids have been submitted
        if (minAmount == 0) {
            return 0;
        }

        uint8 minIndex = 0;
        uint64 minBidTime = activeBids[0].bidTime;

        for (uint8 i = 1; i < MAX_NUM_BIDS; ) {
            uint256 bidAmount = activeBids[i].amount;
            uint64 bidTime = activeBids[i].bidTime;

            // A zero bidAmount means the slot is empty because we enforce non-zero bid amounts
            if (bidAmount == 0) {
                return i;
            } else if (bidAmount < minAmount || (bidAmount == minAmount && bidTime >= minBidTime)) {
                minAmount = bidAmount;
                minIndex = i;
                minBidTime = bidTime;
            }

            unchecked {
                ++i;
            }
        }

        return minIndex;
    }

    /**
     * @notice Get all active bids.
     * @dev Useful for ethers client to get the entire array at once.
     */
    function getAllActiveBids() external view returns (Bid[MAX_NUM_BIDS] memory) {
        return activeBids;
    }

    /**
     * @notice Settles the auction and mints a skateboard ticket NFT to each winner.
     * @dev Bids will be sorted in descending order off-chain due to constraints with
     * sorting structs on-chain via a field on the struct, however we will validate the
     * input on-chain before minting the NFTs. The input bids must be in descending order
     * by amount and all input bids must correspond to a bid in the `activeBids` mapping.
     * @dev Duplicate bids can be passed in to circumvent the validation logic. We are ok
     * with this loophole since this function is ownerOnly.
     * @dev Settlement is only possible once the auction is over.
     */
    function settleAuction(Bid[MAX_NUM_BIDS] calldata sortedBids) external onlyOwner nonReentrant {
        if (block.timestamp <= endTime) {
            revert AuctionStillLive();
        }
        if (auctionSettled) {
            revert AuctionSettled();
        }

        // Validate the input bids
        for (uint256 i = 0; i < MAX_NUM_BIDS; ) {
            Bid memory inputBid = sortedBids[i];
            BidIndex memory bidIndex = bidIndexes[inputBid.bidder];
            if (
                !bidIndex.isSet ||
                activeBids[bidIndex.index].bidder != inputBid.bidder ||
                activeBids[bidIndex.index].amount != inputBid.amount ||
                activeBids[bidIndex.index].bidTime != inputBid.bidTime
            ) {
                revert NonExistentBid();
            }

            // The zero-th index has nothing to compare against
            if (i != 0) {
                Bid memory prevBid = sortedBids[i - 1];
                if (inputBid.amount > prevBid.amount) {
                    revert BidsNotSorted();
                }
            }

            unchecked {
                ++i;
            }
        }
        // Get addresses from bids
        address[MAX_NUM_BIDS] memory winners;
        for (uint256 i = 0; i < MAX_NUM_BIDS; ) {
            winners[i] = sortedBids[i].bidder;
            unchecked {
                ++i;
            }
        }

        // Mint tickets to auction winners
        st.epicMint(winners);

        auctionSettled = true;
    }

    /**
     * @notice Transfers ETH to a specified address.
     * @dev This function can only be called internally.
     */
    function _transferETH(address to, uint256 value) internal returns (bool) {
        (bool success, ) = to.call{ value: value, gas: 30000 }(new bytes(0));
        return success;
    }

    /**
     * @notice Sets the start and end time of the auction.
     * @dev Only callable by the owner.
     */
    function setAuctionTimes(uint256 _startTime, uint256 _endTime) external onlyOwner {
        startTime = _startTime;
        endTime = _endTime;
    }

    /**
     * @notice Set the auction time buffer.
     * @dev Only callable by the owner.
     */
    function setTimeBuffer(uint256 _timeBuffer) external onlyOwner {
        timeBuffer = _timeBuffer;
    }

    /**
     * @notice Set the auction reserve price.
     * @dev Only callable by the owner.
     */
    function setReservePrice(uint256 _reservePrice) external onlyOwner {
        reservePrice = _reservePrice;
    }

    /**
     * @notice Set the auction minimum bid increment percentage.
     * @dev Only callable by the owner.
     */
    function setMinBidIncrementPercentage(uint8 _minBidIncrementPercentage) external onlyOwner {
        minBidIncrementPercentage = _minBidIncrementPercentage;
    }

    /**
     * @notice Set the auction replacing bid buffer amount.
     * @dev Only callable by the owner.
     */
    function setMinReplacementIncrease(uint256 _minStackedBidIncrement) external onlyOwner {
        minStackedBidIncrement = _minStackedBidIncrement;
    }

    /**
     * @notice Withdraws the contract value to the owner
     */
    function withdraw() external onlyOwner {
        bool success = _transferETH(msg.sender, address(this).balance);
        if (!success) {
            revert WithdrawFailed();
        }
    }
}

contracts/AtlantisEquipments.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "./interface/IAtlantisComponents.sol";
import "./interface/IAtlantisMaterials.sol";
import "./interface/IStakingWithLock.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
// hardhat console log
import "hardhat/console.sol"; // TODO: Remove in production

contract AtlantisComponents is ERC1155, Pausable, Ownable, IAtlantisComponents {
    // Contract name
    string public name;
    // Contract symbol
    string public symbol;

    mapping(uint256 => uint256) public tokenSupply;

    mapping(uint256 => uint256) private _totalSupply;
    mapping(uint256 => string) private _tokenURIs;
    string public baseTokenURI;
    uint256 private _currentTokenID = 0;
    uint256 public totalTokenIds = 30;
    // Mint cost
    uint256 public MINT_COST = 30 ether;
    IStakingWithLock public stakingWithLock;
    IAtlantisMaterials public atlantisMaterials;
    IERC20 public stardust;
    uint256[5] public stardustCost;
    uint16[5] public materialsRequired;
    uint16[10] public componentSpeeds;
    uint256 private nonce;
    address public treasury;
    // ------------------------- EVENTS --------------------------
    event FuseComponent(address indexed _from, uint256 indexed _id, uint256 _amount, uint256 _totalSupply);

    // ------------------------- ERRORS --------------------------
    error UpgradeError();
    error IdNotExistsError();

    // ----------------------- MODIFIERS -------------------------

    constructor(
        string memory _name,
        string memory _symbol,
        string memory baseURI,
        address owner,
        address _stardust,
        address _stakingWithLock,
        address _atlantisMaterials,
        address _treasury
    ) ERC1155(baseURI) {
        name = _name;
        symbol = _symbol;
        setBaseMetadataURI(baseURI);
        transferOwnership(owner);
        stardust = IERC20(_stardust);
        atlantisMaterials = IAtlantisMaterials(_atlantisMaterials);
        stakingWithLock = IStakingWithLock(_stakingWithLock);
        treasury = _treasury;
    }

    // Receive payable function
    receive() external payable {}

    // ------------------------- VIEW FUNCTIONS ------------------------------

    /**
     * @dev Total amount of tokens in with a given id.
     */
    function totalSupply(uint256 id) public view returns (uint256) {
        return _totalSupply[id];
    }

    function uri(uint256 tokenId) public view override returns (string memory) {
        require(exists(tokenId), "Resources: URI query for nonexistent token");

        string memory base = _baseURI();
        string memory _tokenURI = _tokenURIs[tokenId];

        return string(abi.encodePacked(base, _tokenURI, Strings.toString(tokenId)));
    }

    // ------------------------- ADMIN FUNCTIONS ------------------------------

    /**
     * @dev Will update the base URL of token's URI
     * @param _newBaseMetadataURI New base URL of token's URI
     */
    function setBaseMetadataURI(string memory _newBaseMetadataURI) public onlyOwner {
        baseTokenURI = _newBaseMetadataURI;
    }

    /// @dev Will update the token URL of token's URI
    function setTokenURI(uint256 tokenId, string memory _newTokenURI) public onlyOwner {
        _tokenURIs[tokenId] = _newTokenURI;
    }

    /**
     * @dev Mints some amount of tokens to an address
     * @param _to          Address of the future owner of the token
     * @param _id          Token ID to mint
     * @param _quantity    Amount of tokens to mint
     * @param _data        Data to pass if receiver is contract
     */
    function devMint(address _to, uint256 _id, uint256 _quantity, bytes memory _data) public onlyOwner {
        _mint(_to, _id, _quantity, _data);
    }

    function random() private view returns (uint) {
        uint randomHash = uint(
            keccak256(
                abi.encodePacked(msg.sender, block.coinbase, block.difficulty, block.gaslimit, block.timestamp, nonce)
            )
        );
        return randomHash % 3;
    }

    // Public mint with random id
    function mint(address _to, uint256 _quantity, bytes memory _data) public payable {
        require(msg.value == _quantity * MINT_COST, "AtlantisComponents: Insufficient CRO!");
        uint256 _id;
        // Loop through quantity
        for (uint256 i = 0; i < _quantity; i++) {
            _id = random() + 1;
            nonce += 1;
            console.log("Minting token with id: %s", _id);
            _mint(_to, _id, 1, _data);
        }
    }

    /**
     * @dev Mint tokens for each id in _ids
     * @param _to          The address to mint tokens to
     * @param _ids         Array of ids to mint
     * @param _quantities  Array of amounts of tokens to mint per id
     * @param _data        Data to pass if receiver is contract
     */
    function batchMint(
        address _to,
        uint256[] memory _ids,
        uint256[] memory _quantities,
        bytes memory _data
    ) public onlyOwner {
        _mintBatch(_to, _ids, _quantities, _data);
    }

    /**
     * @notice Withdraw all CRO from this account to the owner
     */
    function withdrawFund() external onlyOwner {
        (bool success, ) = payable(treasury).call{ value: address(this).balance }("");
        require(success, "Transfer failed");
    }

    // ------------------------- INTERNAL FUNCTIONS ------------------------------

    /**
     * @dev See {ERC1155-_mint}.
     */
    function _mint(address account, uint256 id, uint256 amount, bytes memory data) internal override {
        super._mint(account, id, amount, data);
    }

    /**
     * @dev See {ERC1155-_mintBatch}.
     */
    function _mintBatch(
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) internal override {
        super._mintBatch(to, ids, amounts, data);
    }

    /// @dev Gets baseToken URI
    function _baseURI() internal view returns (string memory) {
        return baseTokenURI;
    }

    /**
     * @dev See {ERC1155-_beforeTokenTransfer}.
     *
     * Requirements:
     *
     * - the contract must not be paused.
     */
    function _beforeTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) internal virtual override(ERC1155) {
        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);

        require(!paused(), "ERC1155Pausable: token transfer while paused");
        if (from == address(0)) {
            for (uint256 i = 0; i < ids.length; ++i) {
                _totalSupply[ids[i]] += amounts[i];
            }
        }

        if (to == address(0)) {
            for (uint256 i = 0; i < ids.length; ++i) {
                uint256 id = ids[i];
                uint256 amount = amounts[i];
                uint256 supply = _totalSupply[id];
                require(supply >= amount, "ERC1155: burn amount exceeds totalSupply");
                unchecked {
                    _totalSupply[id] = supply - amount;
                }
            }
        }
    }

    /**
     * @dev Indicates whether any token exist with a given id, or not.
     */
    function exists(uint256 _id) public view virtual returns (bool) {
        return _id > 0 && _id <= totalTokenIds;
    }

    // Atlantis
    function getMaterialIdFromComponentId(uint256 _id) public pure returns (uint256) {
        if (_id > 27) {
            revert UpgradeError();
        }
        uint256 _element = (_id % 3);
        uint256 _materialId;
        if (_element == 1) {
            _materialId = 10;
        } else if (_element == 2) {
            _materialId = 11;
        } else if (_element == 0) {
            _materialId = 12;
        }
        return _materialId;
    }

    function fuseComponent(uint256 _id, uint16 _amountToCreate) external {
        // Total fusion cost
        uint256 _fusionCost = calculateFusionCost(_id, _amountToCreate);
        // Material cost
        uint256 _materialCost = calculateMaterialsCost(_id, _amountToCreate);
        // Calculate components required to fuse
        uint256 _amountToBurn = fuseComponentsView(_id, _amountToCreate);
        uint256 _toId = _id + 3;

        // Burn material
        atlantisMaterials.burn(msg.sender, getMaterialIdFromComponentId(_id), _materialCost);

        _burn(msg.sender, _id, _amountToBurn);
        // Mint new component
        _mint(msg.sender, _toId, _amountToCreate, "");
        // Burn stardust if _fusionCost is more than 0
        if (_fusionCost > 0) {
            stakingWithLock.unstakeAndBurn(_fusionCost);
        }
        // Emit fuse event
        emit FuseComponent(msg.sender, _toId, _amountToCreate, totalSupply(_toId));
    }

    function getSpeed(uint256 _id) public view returns (uint16) {
        if (_id > 30) {
            revert IdNotExistsError();
        } else if (_id == 0) {
            return 0;
        }
        uint8 _level = getLevel(_id);
        return componentSpeeds[_level - 1];
    }

    function getLevel(uint256 _id) public pure returns (uint8) {
        if (_id > 30) {
            revert IdNotExistsError();
        }
        // Logic above level 5
        uint8 _level;
        if ((_id % 3) == 0) {
            _level = uint8(_id) / 3;
        } else {
            _level = uint8(_id) / 3 + 1;
        }

        return _level;
    }

    // Get element from id
    function getElement(uint256 _id) public pure returns (uint8) {
        if (_id > 30) {
            revert IdNotExistsError();
        }
        uint8 _element = uint8(_id) % 3;
        return _element;
    }

    /** Token Ids
     * 1 2 3 Fire Lightning Steel Component Level 1
     * 4 5 6 Fire Lightning Steel Component Level 2
     * 7 8 9 Fire Lightning Steel Component Level 3
     * 10 11 12 Fire Lightning Steel Component Level 4
     * 13 14 15 Fire Lightning Steel Component Level 5
     * 16 17 18 Fire Lightning Steel Component Level 6
     * 19 20 21 Fire Lightning Steel Component Level 7
     * 22 23 24 Fire Lightning Steel Component Level 8
     * 25 26 27 Fire Lightning Steel Component Level 9
     * 28 29 30 Fire Lightning Steel Component Level 10
     *
     * stardustCost[0] =  250
     * stardustCost[1] =  360
     * stardustCost[2] =  490
     * stardustCost[3] =  640
     * stardustCost[4] =  810
     */
    function calculateFusionCost(uint256 _id, uint16 _amountToCreate) public view returns (uint256) {
        // Logic for level 1 - 4
        if (_id < 13) {
            return 0;
        } else if (_id > 27) {
            revert UpgradeError();
        }
        // Logic above level 5
        uint8 _level = getLevel(_id);
        return uint256(_amountToCreate) * stardustCost[_level - 5];
    }

    function calculateMaterialsCost(uint256 _id, uint16 _amountToCreate) public view returns (uint16) {
        // Logic for level 1 - 4
        if (_id < 13) {
            return 0;
        }
        // Logic above level 5
        uint8 _level;
        if ((_id % 3) == 0) {
            _level = uint8(_id) / 3;
        } else {
            _level = uint8(_id) / 3 + 1;
        }
        return _amountToCreate * materialsRequired[_level - 5];
    }

    function fuseComponentsView(uint256 _id, uint16 _amountToCreate) public view override returns (uint16) {
        if (!exists(_id) || _amountToCreate == 0) {
            revert UpgradeError();
        }
        // Get amount of materials to fuse
        uint16 amountToFuse = 2 * _amountToCreate;
        return amountToFuse;
    }

    // Set stardust costs
    function setStardustCosts(uint256[] calldata _stardustCost) external onlyOwner {
        // Loop through and set stardust
        for (uint256 i = 0; i < _stardustCost.length; i++) {
            stardustCost[i] = _stardustCost[i];
        }
    }

    // Set materialsRequired
    function setMaterialsRequired(uint16[] memory _materialsRequired) external onlyOwner {
        // Loop through and set materialsRequired
        for (uint8 i = 0; i < _materialsRequired.length; i++) {
            materialsRequired[i] = _materialsRequired[i];
        }
    }

    // Set componentSpeeds
    function setComponentSpeeds(uint16[] memory _componentSpeeds) external onlyOwner {
        // Loop through and set componentSpeeds
        for (uint8 i = 0; i < _componentSpeeds.length; i++) {
            componentSpeeds[i] = _componentSpeeds[i];
        }
    }
}

contracts/AtlantisMaterials.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

import { IAtlantisMaterials } from "./interface/IAtlantisMaterials.sol";
import "./common/AtlantisLib.sol";
import "./interface/IStakingWithLock.sol";
import "hardhat/console.sol"; // TODO: Remove in production

contract AtlantisMaterials is ERC1155, Pausable, Ownable, IAtlantisMaterials {
    using AtlantisLib for AtlantisLib.Element;

    string public name;
    string public symbol;

    string public baseTokenURI;
    uint256 public totalTokenIds = 12;

    mapping(uint256 => uint256) private _totalSupply;
    // mapping of address to whitelisted status
    mapping(address => bool) private _whitelist;
    IERC20 public stardust;
    // StakingWithLock address
    IStakingWithLock public stakingWithLock;
    // stardust fusion cost
    uint256 public FUSION_COST = 2 ether;
    // ------------------------- EVENTS --------------------------
    event MintMaterial(address indexed _to, uint256 indexed _id, uint256 _amount, uint256 _totalSupply);
    event FuseMaterial(address indexed _from, uint256 indexed _id, uint256 _amount, uint256 _totalSupply);

    // ----------------------- MODIFIERS -------------------------

    modifier onlyAtlantisContracts() {
        _isAtlantisContracts();
        _;
    }

    function _isAtlantisContracts() internal view {
        if (!(_whitelist[msg.sender] || msg.sender == owner())) revert OnlyAtlantisContractsAllowed();
    }

    constructor(
        string memory _name,
        string memory _symbol,
        string memory baseURI,
        address owner,
        address _stardust,
        address _stakingWithLock
    ) ERC1155(baseURI) {
        name = _name;
        symbol = _symbol;
        setBaseMetadataURI(baseURI);
        transferOwnership(owner);
        stardust = IERC20(_stardust);
        stakingWithLock = IStakingWithLock(_stakingWithLock);
    }

    // ------------------------- PUBLIC FUNCTIONS ------------------------------
    /**
     * @dev Fuse 3 materials to get another material of a higher tier
     * @param _id The material id
     * @param _amountToCreate The amount of materials to create
     * Material Upgrade Sheet
     *           Tier 1 -> Tier 2 -> Tier 3 -> Tier 4
     * FIRE      ID 1   -> ID 4   -> ID 7   -> ID 10
     * LIGHTNING ID 2   -> ID 5   -> ID 8   -> ID 11
     * STEEL     ID 3   -> ID 6   -> ID 9   -> ID 12
     */
    function fuseMaterials(uint8 _id, uint8 _toId, uint256 _amountToCreate) external whenNotPaused {
        // Calculate material required to fuse
        uint256 _amountToBurn = calculateMaterialCost(_id, _toId, _amountToCreate);
        // Burn materials
        _burn(msg.sender, _id, _amountToBurn);
        // Mint new material
        _mint(msg.sender, _toId, _amountToCreate, "");
        // Total fusion cost
        uint256 fusionCost = calculateFusionCost(_id, _toId, _amountToCreate);
        // Transfer fusion cost
        //  IERC20(stardust).transferFrom(msg.sender, address(this), fusionCost);
        // Burn stardust
        stakingWithLock.unstakeAndBurn(fusionCost);
        // Emit fuse event
        emit FuseMaterial(msg.sender, _toId, _amountToCreate, totalSupply(_toId));
    }

    // View function to calculate fusion cost
    function calculateFusionCost(uint8 _id, uint8 _toId, uint256 _amountToCreate) public view returns (uint256) {
        // Calculate material required to fuse
        uint256 _amountToBurn = calculateMaterialCost(_id, _toId, _amountToCreate);
        return (_amountToBurn / 3) * FUSION_COST;
    }

    // View function to see how many materials required to fuse to amountToCreate
    function calculateMaterialCost(uint8 _id, uint8 _toId, uint256 _amountToCreate) public view returns (uint256) {
        // require(_amountToCreate > 0, "AtlantisMaterials: AMOUNT_MUST_BE_GREATER_THAN_0");
        if (_amountToCreate == 0) revert InvalidInputAmount();
        // require(exists(_id), "AtlantisMaterials: NON_EXISTENT_TOKEN");
        // require(exists(_toId), "AtlantisMaterials: NON_EXISTENT_TOKEN");
        if (!exists(_id) || !exists(_toId)) revert NonExistentToken();

        // require((_toId - _id) % 3 == 0, "AtlantisMaterials: DIFFERENT_ELEMENT");
        if ((_toId % 3 != _id % 3)) revert InvalidElement();

        // require(_id != _toId, "AtlantisMaterials: SAME_ID");
        // require(_toId > _id, "AtlantisMaterials: INVALID_ID");
        if (_id == _toId || _id > _toId) revert InvalidUpgrade();

        // Require toId to be more than id
        // Require toId to be in multiples of 3 more than id
        // Calculate how many materials to fuse to get toId
        // Get tier difference
        uint8 tierDifference = (_toId - _id) / 3;
        // Get amount of materials to fuse
        uint256 amountToFuse = 3 ** tierDifference * _amountToCreate;
        return amountToFuse;
    }

    // Setter for fusion cost
    function setFusionCost(uint256 _cost) external onlyOwner {
        FUSION_COST = _cost;
    }

    function setAtlantisContracts(address[] calldata _contracts) external onlyOwner {
        // Loop and set _whitelisted true
        for (uint256 i = 0; i < _contracts.length; i++) {
            _whitelist[_contracts[i]] = true;
        }
    }

    /**
     * @dev Mints some amount of tokens to an address
     * @param _to          Address of the future owner of the token
     * @param _id          Token ID to mint
     * @param _quantity    Amount of tokens to mint
     */
    function mint(address _to, uint256 _id, uint256 _quantity) external onlyAtlantisContracts {
        // Only this contract, the owner or Atlantis contract can mint
        // require(_whitelist[msg.sender] || msg.sender == owner(), "AtlantisMaterials: Only Atlantis contracts allowed");
        // require(exists(_id), "AtlantisMaterials: NON_EXISTENT_TOKEN");
        if (!exists(_id)) revert NonExistentToken();
        _mint(_to, _id, _quantity, "");
        // emit Mint event
        emit MintMaterial(_to, _id, _quantity, totalSupply(_id));
    }

    /**
     * @dev Burns some amount of tokens from an address
     */
    function burn(address _user, uint256 _id, uint256 _quantity) external onlyAtlantisContracts {
        // Require tx.origin is same as _user
        // require(exists(_id), "AtlantisMaterials: NON_EXISTENT_TOKEN");
        if (!exists(_id)) revert NonExistentToken();
        _burn(_user, _id, _quantity);
    }

    function setStardust(IERC20 _stardust) external onlyOwner {
        stardust = _stardust;
    }

    // ------------------------- PUBLIC VIEW FUNCTIONS ------------------------------

    function uri(uint256 tokenId) public view override returns (string memory) {
        if (!exists(tokenId)) revert NonExistentToken();

        string memory base = _baseURI();

        return string(abi.encodePacked(base, Strings.toString(tokenId)));
    }

    // ------------------------- ADMIN FUNCTIONS ------------------------------

    /**
     * @dev Will update the base URL of token's URI
     * @param _newBaseMetadataURI New base URL of token's URI
     */
    function setBaseMetadataURI(string memory _newBaseMetadataURI) public onlyOwner {
        baseTokenURI = _newBaseMetadataURI;
    }

    // ------------------------- INTERNAL FUNCTIONS ------------------------------

    /**
     * @dev Get the id of the next tier of material
     * @param _id The current material id
     */
    function _getNextTierId(uint8 _id) internal pure returns (uint8) {
        return _id + 3;
    }

    /**
     * @dev Gets baseToken URI
     */
    function _baseURI() internal view returns (string memory) {
        return baseTokenURI;
    }

    /**
     * @dev Total amount of tokens in with a given id.
     */
    function totalSupply(uint256 id) public view virtual returns (uint256) {
        return _totalSupply[id];
    }

    /**
     * @dev Indicates whether any token exist with a given id, or not.
     * @dev There are only materials with tokenIds 1-12
     */
    function exists(uint256 _id) public view virtual returns (bool) {
        return _id > 0 && _id <= totalTokenIds;
    }

    /**
     * @dev See {ERC1155-_beforeTokenTransfer}.
     *
     * Requirements:
     *
     * - the contract must not be paused.
     */
    function _beforeTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) internal virtual override(ERC1155) {
        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);

        require(!paused(), "ERC1155Pausable: token transfer while paused");

        if (from == address(0)) {
            for (uint256 i = 0; i < ids.length; ++i) {
                _totalSupply[ids[i]] += amounts[i];
            }
        }

        if (to == address(0)) {
            for (uint256 i = 0; i < ids.length; ++i) {
                uint256 id = ids[i];
                uint256 amount = amounts[i];
                uint256 supply = _totalSupply[id];
                require(supply >= amount, "ERC1155: burn amount exceeds totalSupply");
                unchecked {
                    _totalSupply[id] = supply - amount;
                }
            }
        }
    }
}

contracts/AtlantisPlanets.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.11;
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./interface/IAtlantisPlanets.sol";
import "./interface/IAtlantisMaterials.sol";
import "./interface/IStakingWithLock.sol";
import "./common/WithLimitedSupply.sol";
import "./common/RandomlyAssigned.sol";
import "./common/Base64.sol";
import "hardhat/console.sol"; // TODO: Remove in production

/// @title Atlantis Planets Mint Contract
/// @dev Max Supply of 4512 planets
/// @dev First 12 planets are EPIC planets

contract AtlantisPlanets is ERC721, Ownable, IAtlantisPlanets, RandomlyAssigned {
    using Strings for uint256;
    using ECDSA for bytes32;

    string public baseURI;

    // Stages
    // 0: Before all minting commence
    // 1: WhiteList Sale
    // 2: Public sale
    // 3: Post-Mint (Admin Phase)
    // 4: Game Phase

    uint8 public stage;
    uint256 currentSupply;

    // Whitelist Mint Settings  cro
    uint256 public whitelistMintMaxPerWallet = 5; // Private Sale Address Mint Cap
    uint256 public whitelistMintPrice = 500 ether; // Private Sale Mint Price
    mapping(address => uint256) public whitelistMintCount;
    address private whitelistSignerAddress;

    // Public Sale Mint Settings 600 cro
    uint256 public publicMintPrice = 750 ether;
    uint256 public publicMintMaxPerWallet = type(uint256).max;
    mapping(address => uint256) public publicMintCount;

    // Treasury
    address public treasury;

    IERC20 public xARGO;
    IERC20 public stardust;
    IStakingWithLock public stakingWithLock;
    address public auctionContract;
    // Levelling
    bytes public levelUpMaterial;
    // xARGO base cost
    uint256 public xArgoBaseCost = 40 ether;
    // stardust base cost
    uint256 public stardustBaseCost = 2 ether;
    // stardust base cost scaling
    uint256 public stardustBaseCostScaling = 25;
    // stardust rarity cost scaling
    uint256 public stardustRarityCostScaling = 20;
    // Mapping of planet token id to planet struct
    mapping(uint256 => AtlantisLib.Planet) public planets;

    // Variable to track material tiers
    uint16 public materialTiers = 4;
    // Variable to track ERC1155 material token contract
    IAtlantisMaterials public materialToken;
    // Variable to track max planet level
    uint16 public maxPlanetLevel = 50;

    bool public revealed = false;
    string public unrevealedImageURI = "ipfs://unrevealedhash";

    // material token ids is in the following sequence:
    // 1 - Fire 1
    // 2 - Lightning 1
    // 3 - Steel 1
    // 4 - Fire 2
    // 5 - Lightning 2
    // 6 - Steel 2
    // 7 - Fire 3
    // 8 - Lightning 3
    // 9 - Steel 3
    // 10 - Fire 4
    // 11 - Lightning 4
    // 12 - Steel 4

    // Events
    event PlanetUpgraded(uint256 indexed tokenId, uint256 indexed level);
    event PrivateMint(address indexed to, uint256 amount);
    event PublicMint(address indexed to, uint256 amount);

    // -------------------- MODIFIERS ----------------------

    /**
     * @dev Prevent Smart Contracts from calling the functions with this modifier
     */
    modifier onlyEOA() {
        require(msg.sender == tx.origin, "Planets: must use EOA");
        _;
    }

    /**
     * @dev Only Auction Contract Modifier
     */
    modifier onlyAuctionContract() {
        require(msg.sender == auctionContract, "Planets: Only Auction Contract");
        _;
    }

    constructor(
        address _owner,
        address _whitelistSignerAddress,
        string memory __baseURI,
        address _materialToken,
        IERC20 _xArgo,
        IERC20 _stardust,
        address _stakingWithLock
    ) ERC721("AtlantisPlanets", "AP") RandomlyAssigned(4500, 13) {
        setTreasury(_owner);
        setWhitelistSignerAddress(_whitelistSignerAddress);
        setBaseURI(__baseURI);
        transferOwnership(_owner);
        currentSupply = 0;
        materialToken = IAtlantisMaterials(_materialToken);
        xARGO = _xArgo;
        stardust = _stardust;
        stakingWithLock = IStakingWithLock(_stakingWithLock);
    }

    /**
     * @dev Set Revealed Metadata URI
     */
    function setBaseURI(string memory _newBaseURI) public onlyOwner {
        baseURI = _newBaseURI;
    }

    // -------------------- ATLANTIS PUBLIC FUNCTIONS ----------------------

    /**
     * @dev Get material upgrade requirements for upgrading a planet
     * @param _tokenId The token id of the planet
     * @param _levels The number of levels to upgrade
     * @return materialRequirements The material requirements for upgrading the planet
     */
    function getUpgradeRequirements(
        uint256 _tokenId,
        uint8 _levels
    ) internal view returns (uint16[4] memory materialRequirements) {
        // Get planet struct
        AtlantisLib.Planet memory planet = planets[_tokenId];
        uint8 planetLevel = planet.level;
        uint8 newLevel = planetLevel + _levels;
        uint8 orbit = uint8(planet.orbit);

        // require(planetLevel + _levels <= maxPlanetLevel, "Planets: Planet cannot be upgraded to this level!");
        if (planetLevel + _levels > maxPlanetLevel) revert ExceededMaxLevel();

        // Cumulative requirement at target level - Cumulative requirement at current level = requirement for upgrade
        materialRequirements[0] =
            toUint16(levelUpMaterial, orbit, newLevel, 0) -
            toUint16(levelUpMaterial, orbit, planetLevel, 0);

        materialRequirements[1] =
            toUint16(levelUpMaterial, orbit, newLevel, 1) -
            toUint16(levelUpMaterial, orbit, planetLevel, 1);

        materialRequirements[2] =
            toUint16(levelUpMaterial, orbit, newLevel, 2) -
            toUint16(levelUpMaterial, orbit, planetLevel, 2);

        materialRequirements[3] =
            toUint16(levelUpMaterial, orbit, newLevel, 3) -
            toUint16(levelUpMaterial, orbit, planetLevel, 3);
    }

    /**
     * @notice Get uint16 value from a byte array
     * @param _bytes The token id of the planet
     * @param orbit Orbit of the planet
     * @param level Level of the planet
     * @param tier Tier of the material
     * @dev This function was modified to serve retriving the material upgrade requirements
     */
    function toUint16(
        bytes memory _bytes,
        uint256 orbit,
        uint256 level,
        uint256 tier
    ) internal pure returns (uint16 tempUint) {
        uint256 _start = orbit * 8 + (level - 1) * 32 + tier * 2;
        require(_bytes.length >= _start + 2, "toUint16_outOfBounds");

        assembly {
            tempUint := mload(add(add(_bytes, 0x2), _start))
        }
    }

    /**
     * @notice Get evolution stage of planet
     * @param level Level of a planet
     */
    function _getPlanetEvolution(
        uint16 level
    ) internal pure returns (AtlantisLib.Evolution evo, string memory evoString) {
        if (level < 20) {
            evo = AtlantisLib.Evolution.BABY;
            evoString = "Baby";
        } else if (level >= 20 && level < 30) {
            evo = AtlantisLib.Evolution.ROOKIE;
            evoString = "Rookie";
        } else if (level >= 30 && level < 40) {
            evo = AtlantisLib.Evolution.CHAMPION;
            evoString = "Champion";
        } else if (level >= 40 && level < 50) {
            evo = AtlantisLib.Evolution.ULTIMATE;
            evoString = "Ultimate";
        } else if (level == 50) {
            evo = AtlantisLib.Evolution.MEGA;
            evoString = "Mega";
        }
    }

    /**
     * @notice Get planet details
     * @dev Planet details include: level, element, orbit, onExpedition
     */
    function getPlanetDetails(uint256 _planetId) external view returns (AtlantisLib.Planet memory) {
        AtlantisLib.Planet memory planet = planets[_planetId];
        return planet;
    }

    /**
     * @notice Return xArgo cost for upgrading a planet
     */
    function getxArgoCost(uint8 currentLevel, uint8 newLevel) internal view returns (uint256) {
        if (currentLevel == 0 || currentLevel >= newLevel) revert InvalidUpgradeLevel(currentLevel, newLevel);
        // require(currentLevel < newLevel, "Planets: Cannot downgrade planet!");
        return xArgoBaseCost * (uint256(newLevel)) - xArgoBaseCost * uint256(currentLevel);
    }

    /**
     * @notice Return stardust cost for upgrading a planet
     */
    function getStardustCost(
        uint8 currentLevel,
        uint8 newLevel,
        uint8 orbit
    ) internal view returns (uint256 stardustCost) {
        if (currentLevel == 0 || currentLevel >= newLevel) revert InvalidUpgradeLevel(currentLevel, newLevel);

        // require(currentLevel > 0, "Planets: No zero level");
        // require(currentLevel < newLevel, "Planets: Cannot downgrade planet!");
        // Loop through all levels and add up stardust cost
        for (uint256 i = currentLevel; i <= newLevel; i++) {
            stardustCost +=
                (stardustBaseCost *
                    (((i - 1) * stardustBaseCostScaling + 100) * ((orbit) * stardustRarityCostScaling + 100))) /
                10000;
        }
    }

    // External function for frontend to retrieve stardust, xArgo, and material costs in 1 multicall
    function getUpgradeCosts(
        uint256 _tokenId,
        uint8 _levels
    ) external view returns (uint256 stardustCost, uint256 xArgoCost, uint16[4] memory materialRequirements) {
        // Get planet struct
        AtlantisLib.Planet memory planet = planets[_tokenId];
        uint8 planetLevel = planet.level;
        uint8 newLevel = planetLevel + _levels;
        uint8 orbit = uint8(planet.orbit);

        require(planetLevel + _levels <= maxPlanetLevel, "Planets: Planet cannot be upgraded to this level!");

        // Get stardust cost
        stardustCost = getStardustCost(planetLevel, newLevel, orbit);

        // Get xArgo cost
        xArgoCost = getxArgoCost(planetLevel, newLevel);

        // Get material requirements
        materialRequirements = getUpgradeRequirements(_tokenId, _levels);
    }

    /**
     * @notice Returns total supply of AtlantisPlanets
     */
    function totalSupply() public view returns (uint256) {
        return currentSupply;
    }

    /**
     * @dev Upgrade a planet by `_levels` levels
     * @param _tokenId Token ID of planet
     * @param _levels Number of levels to upgrade planet by
     */
    function upgradePlanet(uint256 _tokenId, uint8 _levels) external {
        // Check if upgrader is owner of planet
        if (ownerOf(_tokenId) != msg.sender) revert NotOwnerOfPlanet();
        // Check if stage 4
        if (stage != 4) revert InvalidStage(stage, 4);

        AtlantisLib.Planet storage planet = planets[_tokenId];

        uint8 orbit = uint8(planet.orbit);

        // require(planet.level + _levels <= maxPlanetLevel, "Planets: Planet cannot be upgraded to this level!");
        if (planet.level + _levels > maxPlanetLevel) revert ExceededMaxLevel();

        // Get xARGO requirements
        uint256 xArgoCost = getxArgoCost(planet.level, planet.level + _levels);
        // Get Stardust requirements
        // Base cost scaling for stardust 0.25 = 25, need math properly
        uint256 stardustCost = getStardustCost(planet.level, planet.level + _levels, orbit);

        // Get material requirements
        uint16[4] memory materialRequirements = getUpgradeRequirements(_tokenId, _levels);

        uint16 planetTypeTierStep = 0;
        uint16 step = 1;

        while (step <= materialTiers) {
            uint256 toBurn = materialRequirements[step - 1];
            // Burn materials
            if (toBurn > 0) {
                materialToken.burn(msg.sender, uint256(uint256(orbit) + 1 + planetTypeTierStep), toBurn);
            }
            step++;
            planetTypeTierStep += 3;
        }

        // Transfer xARGO and Stardust to this contract
        IERC20(xARGO).transferFrom(msg.sender, address(this), xArgoCost);
        if (stardustCost > 0) {
            stakingWithLock.unstakeAndBurn(stardustCost);
        }

        // Increase planet level
        planet.level += _levels;

        emit PlanetUpgraded(_tokenId, _levels);
    }

    // -------------------- MINT FUNCTIONS --------------------------

    /**
     * @dev Mint planet (Whitelist only)
     * @param _mintAmount Amount of planets to mint
     * @param nonce Unique Nonce
     * @param signature Signature provided by the signerAddress
     */
    function whitelistMint(
        uint256 _mintAmount,
        bytes memory nonce,
        bytes memory signature
    ) external payable onlyEOA ensureAvailabilityFor(_mintAmount) {
        // Check if user is whitelisted
        // require(whitelistSigned(msg.sender, nonce, signature), "Planets: Invalid Signature!");
        if (!whitelistSigned(msg.sender, nonce, signature)) revert InvalidSignature();

        // Check if whitelist sale is open
        // require(stage == 1, "Planets: Whitelist Mint is not open");
        if (stage != 1) revert InvalidStage(stage, 1);

        // Check if enough ETH is sent
        // require(msg.value == _mintAmount * whitelistMintPrice, "Planets: Insufficient CRO!");
        if (msg.value != _mintAmount * whitelistMintPrice)
            revert InsufficientCRO(msg.value, _mintAmount * whitelistMintPrice);

        // Check if mints does not exceed max wallet allowance for public sale
        // require(
        //     whitelistMintCount[msg.sender] + _mintAmount <= whitelistMintMaxPerWallet,
        //     "Planets: Wallet has already minted Max Amount for Whitelist Mint!"
        // );
        if (whitelistMintCount[msg.sender] + _mintAmount > whitelistMintMaxPerWallet) revert ExceedMaxMintPerWallet();

        whitelistMintCount[msg.sender] += _mintAmount;
        currentSupply += _mintAmount;

        for (uint256 i; i < _mintAmount; i++) {
            _mintPlanet();
        }
        emit PrivateMint(msg.sender, _mintAmount);
    }

    /**
     * @notice Public Mint
     * @param _mintAmount Amount that is minted
     */
    function mint(uint256 _mintAmount) external payable onlyEOA ensureAvailabilityFor(_mintAmount) {
        // Check if public sale is open
        // require(stage == 2, "Planets: Public Sale Closed!");
        if (stage != 2) revert InvalidStage(stage, 2);

        // Check if enough ETH is sent
        // require(msg.value == _mintAmount * publicMintPrice, "Planets: Insufficient CRO!");
        if (msg.value != _mintAmount * publicMintPrice)
            revert InsufficientCRO(msg.value, _mintAmount * publicMintPrice);
        // Check if mints does not exceed total max supply

        // require(totalSupply() + _mintAmount <= totalMaxSupply(), "Planets: Max Supply for Public Mint Reached!");
        // if (totalSupply() + _mintAmount > totalMaxSupply()) revert ExceededMaxSupply();

        publicMintCount[msg.sender] += _mintAmount;
        currentSupply += _mintAmount;
        for (uint256 i; i < _mintAmount; i++) {
            _mintPlanet();
        }

        emit PublicMint(msg.sender, _mintAmount);
    }

    /**
     * @notice Mint planet
     * @dev Set initial planet level to 1 and random mint to msg.sender
     */
    function _mintPlanet() internal {
        // Get next token Id
        uint256 _tokenId = nextToken();
        // Initialize planet
        planets[_tokenId].level = 1;
        // Mint planet
        _safeMint(msg.sender, _tokenId);
    }

    // -------------------- ATLANTIS ADMIN FUNCTIONS ----------------------
    /**
     * @dev Set planet orbit
     * @param _tokenIds Token ID of planets
     * @param _backgrounds Backgrounds of planets
     */
    function setPlanetBackgrounds(
        uint256[] calldata _tokenIds,
        AtlantisLib.Background[] calldata _backgrounds
    ) external onlyOwner {
        if (stage != 3) revert InvalidStage(stage, 3);
        // Loop through planet types and set planet type
        for (uint256 i; i < _backgrounds.length; i++) {
            planets[_tokenIds[i]].background = _backgrounds[i];
        }
    }

    /**
     * @dev Set planet orbit
     * @param _tokenIds Token ID of planets
     * @param _planetOrbitNames Orbit of planets
     */

    function setPlanetOrbitNames(
        uint256[] calldata _tokenIds,
        AtlantisLib.OrbitName[] calldata _planetOrbitNames
    ) external onlyOwner {
        if (stage != 3) revert InvalidStage(stage, 3);
        // Loop through planet types and set planet type
        for (uint256 i; i < _planetOrbitNames.length; i++) {
            planets[_tokenIds[i]].orbitName = _planetOrbitNames[i];
        }
    }

    /**
     * @dev Set planet orbit
     * @param _tokenIds Token ID of planets
     * @param _planetOrbits Orbit of planets
     */
    function setPlanetOrbits(
        uint256[] calldata _tokenIds,
        AtlantisLib.Orbit[] calldata _planetOrbits
    ) external onlyOwner {
        if (stage != 3) revert InvalidStage(stage, 3);
        // Loop through planet types and set planet type
        for (uint256 i; i < _planetOrbits.length; i++) {
            planets[_tokenIds[i]].orbit = _planetOrbits[i];
        }
    }

    /**
     * @dev Set planet element
     * @param _tokenIds Token ID of planets
     * @param _materialTypes Element of planets
     */
    function setPlanetElements(
        uint256[] calldata _tokenIds,
        AtlantisLib.Element[] calldata _materialTypes
    ) external onlyOwner {
        // Require stage 3
        if (stage != 3) revert InvalidStage(stage, 3);
        // Set planet type
        for (uint256 i; i < _tokenIds.length; i++) {
            planets[_tokenIds[i]].element = _materialTypes[i];
        }
    }

    /**
     * @notice Set level up material costs
     * @param _data Material cost packed in bytes
     * @dev _data is packed as follows:
     *     Cumulative cost for each material tier for each planet type
     *              | Common              | Uncommon            | Rare                | Epic
     *              | T1   T2   T3   T4   | T1   T2   T3   T4   | T1   T2   T3   T4   | T1   T2   T3   T4
     *     Level 1  | 0000 0000 0000 0000 | 0000 0000 0000 0000 | 0000 0000 0000 0000 | 0000 0000 0000 0000
     *     ...
     *     Level 50 | 0122 00af 00e1 0113 | 015c 00d2 010e 014a | 0196 00f5 013b 0181 | 01d0 0118 0168 01b8
     */
    function setLevelUpMaterial(bytes calldata _data) external onlyOwner {
        // Require stage 3
        if (stage != 3) revert InvalidStage(stage, 3);
        levelUpMaterial = _data;
    }

    /**
     * @dev Withdraw xARGO to treasury address
     * @param _amount Amount of xARGO to withdraw
     */
    function withdrawXArgo(uint256 _amount) external onlyOwner {
        IERC20(xARGO).transfer(treasury, _amount);
    }

    /**
     * @dev Withdraw stardust to treasury address
     * @param _amount Amount of stardust to withdraw
     */
    function withdrawStardust(uint256 _amount) external onlyOwner {
        IERC20(stardust).transfer(treasury, _amount);
    }

    // Setters for base costs
    /**
     * @dev Set xArgo And Stardust base costs
     * @param _xArgoBaseCost xArgo base cost
     * @param _stardustBaseCost Stardust base cost
     */
    function setBaseCosts(uint256 _xArgoBaseCost, uint256 _stardustBaseCost) external onlyOwner {
        xArgoBaseCost = _xArgoBaseCost;
        stardustBaseCost = _stardustBaseCost;
    }

    // -------------------- WHITELIST FUNCTION ----------------------

    /**
     * @dev Checks if the the signature is signed by a valid signer for whitelist
     * @param sender Address of minter
     * @param nonce Random bytes32 nonce
     * @param signature Signature generated off-chain
     */
    function whitelistSigned(address sender, bytes memory nonce, bytes memory signature) private view returns (bool) {
        bytes32 _hash = keccak256(abi.encodePacked(sender, nonce));
        return whitelistSignerAddress == ECDSA.toEthSignedMessageHash(_hash).recover(signature);
    }

    // ------------------------- ATLANTIS AUCTION FUNCTIONS ----------------------------
    /**
     * @dev Dev mint for epic planets
     * @dev Only callable by the auction contract
     * @dev Only callable ONCE
     */
    function epicMint(address[12] calldata _auctionWinners) external onlyAuctionContract {
        require(totalSupply() == uint256(0), "AtlantisPlanets: Epic mint over");
        require(_auctionWinners.length == 12, "AtlantisPlanets: Invalid length");
        // Mint token Ids 1-12
        for (uint256 i = 1; i < 13; i++) {
            _safeMint(_auctionWinners[i - 1], i);
        }
    }

    // ------------------------- ADMIN FUNCTIONS ----------------------------

    /**
     * @dev Set stage of minting
     */
    function setStage(uint8 _newStage) public onlyOwner {
        stage = _newStage;
    }

    /**
     * @dev Toggle Reveal
     */
    function toggleReveal() public onlyOwner {
        revealed = !revealed;
    }

    /**
     * @dev Set signer address for whitelist mint
     */
    function setWhitelistSignerAddress(address signer) public onlyOwner {
        whitelistSignerAddress = signer;
    }

    /**
     * @dev Set whitelist mint max per wallet
     */
    function setWhitelistMaxMintPerWallet(uint256 amount) public onlyOwner {
        whitelistMintMaxPerWallet = amount;
    }

    /**
     * @dev Set public mint price
     */
    function setPublicMintPrice(uint256 _publicMintPrice) public onlyOwner {
        publicMintPrice = _publicMintPrice;
    }

    /**
     * @dev Set whitelist mint price
     */
    function setWhitelistMintPrice(uint256 _whitelistMintPrice) public onlyOwner {
        whitelistMintPrice = _whitelistMintPrice;
    }

    /**
     * @dev Set Acution Contract
     */
    function setAuctionContract(address _auctionContract) public onlyOwner {
        auctionContract = _auctionContract;
    }

    /**
     * @notice Withdraw all CRO from this account to the owner
     */
    function withdrawFund() external onlyOwner {
        (bool success, ) = payable(treasury).call{ value: address(this).balance }("");
        require(success, "Transfer failed");
    }

    /**
     * @notice Sets the treasury address
     */
    function setTreasury(address _treasury) public onlyOwner {
        treasury = _treasury;
    }

    /**
     * @notice Set xArgo contract
     */
    function setxArgo(IERC20 _xArgo) external onlyOwner {
        xARGO = _xArgo;
    }

    /**
     * @notice Set Stardust contract
     */
    function setStardust(IERC20 _stardust) external onlyOwner {
        stardust = _stardust;
    }

    /**
     * @dev Base URI for computing {tokenURI}. If set, the resulting URI for each
     * token will be the concatenation of the `baseURI` and the `tokenId`. Empty
     * by default, it can be overridden in child contracts.
     */
    function _baseURI() internal view virtual override returns (string memory) {
        return baseURI;
    }

    /**
     * @notice Returns if given tokenId exists in AtlantisPlanets
     */
    function exists(uint256 tokenId) external view returns (bool) {
        return _exists(tokenId);
    }

    // ------------------------- TOKEN METADATA ----------------------------

    /**
     * @notice Get ImageURI
     */
    function getImageURI(
        AtlantisLib.Background background,
        AtlantisLib.Element element,
        AtlantisLib.OrbitName orbitName,
        AtlantisLib.Evolution evo
    ) internal view returns (string memory) {
        if (!revealed) {
            return unrevealedImageURI;
        }
        return
            string(
                abi.encodePacked(
                    _baseURI(),
                    Strings.toString(uint(background)),
                    "/",
                    Strings.toString(uint(element)),
                    "/",
                    Strings.toString(uint(orbitName)),
                    "/",
                    Strings.toString(uint(evo)),
                    ".png"
                )
            );
    }

    /**
     * @notice Returns token metadata
     * @dev Metadata is stored on-chain
     */
    function tokenURI(uint256 tokenId) public view override returns (string memory) {
        // Get Planet
        AtlantisLib.Planet memory planet = planets[tokenId];

        // Get Tier from Level
        (AtlantisLib.Evolution evo, string memory evoString) = _getPlanetEvolution(planet.level);

        // Name
        string memory json = string(abi.encodePacked('{"name": "', name(), "# ", tokenId.toString(), '",'));

        // Description
        json = string(abi.encodePacked(json, '"description": "AtlantisPlanets!",'));

        // Attributes
        if (!revealed) {
            json = string(abi.encodePacked(json, '"attributes": [],'));
        } else {
            json = string(
                abi.encodePacked(
                    json,
                    '"attributes": [{"trait_type": "Element", "value": "',
                    AtlantisLib._planetElementToString(planet.element),
                    '"},',
                    '{"trait_type": "Background", "value": "',
                    AtlantisLib._planetBackgroundToString(planet.background),
                    '"},',
                    '{"trait_type": "Orbit Name", "value": "',
                    AtlantisLib._planetOrbitTypeToString(planet.orbitName),
                    '"},'
                )
            );
            json = string(
                abi.encodePacked(
                    json,
                    '{"trait_type": "Orbit", "value": "',
                    AtlantisLib._planetOrbitToString(planet.orbit),
                    '"},',
                    '{"trait_type": "Evolution", "value": "',
                    evoString,
                    '"},',
                    '{"trait_type": "Level", "value": "',
                    Strings.toString(planet.level),
                    '"}],'
                )
            );
        }

        json = Base64.encode(
            bytes(
                string(
                    abi.encodePacked(
                        json,
                        '"image": "',
                        getImageURI(planet.background, planet.element, planet.orbitName, evo),
                        '"}'
                    )
                )
            )
        );
        return string(abi.encodePacked("data:application/json;base64,", json));
    }
}

contracts/AtlantisRacing.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Context.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import "./interface/IAtlantisSpaceships.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "hardhat/console.sol"; // TODO: Remove in production

// TODO: Restructure Smart Contract
/**
Reference: https://docs.soliditylang.org/en/v0.8.17/style-guide.html
Inside each contract, library or interface, use the following order:
- Type declarations
- State variables
- Events
- Modifiers
- Functions

Functions should be Grouped according to:
- constructor
- receive function (if exists)
- fallback function (if exists)
- external
- public
- internal
- private
*/

// TODO: Write AtlantisRacing Interface (IAtlantisRacing.sol)
/**
 * Identify the exposed functions, including, but not limited to:
 * - stakeSpaceship
 * - stakeSpaceships
 * - unstakeSpaceship
 * - unstakeSpaceships
 * -
 */

// TODO: Refactor internal function names, internal function names should start with "_" (underscore)

// TODO: In general, we do not have to initialize zero values, as they are already zero by default

contract AtlantisRacing is Context, Ownable, IERC721Receiver {
    /**
        Events :O
    */
    event Stake(address indexed user, uint256 indexed tokenID, uint256 shipScore);
    event Unstaked(address indexed user, uint256 indexed tokenID);
    event Payout(address indexed user, uint256 goldAmount, uint256 stardustAmount);
    event EmergencyWithdraw(address user, uint256 tokenID);
    event EmergencyWithdrawStardust(uint256 amount);
    event EmergencyWithdrawGold(uint256 amount);
    event StakeArgonauts(address indexed user, uint256 poolId, uint256 indexed tokenID);
    event UnstakeArgonauts(address indexed user, uint256 indexed tokenID, uint256 poolId);

    /**
        Structs
    */

    struct PoolInfo {
        uint256 poolType;
        uint256 stardustPerSecond;
        uint256 lastRewardTime;
        uint256 accStardustPerPoint;
        uint256 totalPoints;
        uint256 totalCount;
    }

    struct UserInfo {
        uint256 totalGold;
        uint256 totalStardust;
        uint256[4] totalSpeeds;
        uint256[4] rewardDebts;
        uint256[] stakedSpaceships;
    }

    /**
        Constructor (\(T.T)/)
    */

    mapping(uint256 => PoolInfo) public poolInfo;
    uint256 public seasonEndTime;
    uint256 public totalStardustPerSecond;

    IERC20 public gold;
    IERC721 public argonauts;
    IAtlantisSpaceships public spaceships;
    IERC20 public stardust;
    mapping(uint8 => uint256) public argonautMultiplier;
    // mapping of address to poolId to argonauts staked
    mapping(address => mapping(uint256 => uint256[])) argonautsStaked;
    mapping(address => UserInfo) public userInfo;
    uint256 internal amountOfPools = 4;
    bool public seasonStarted = false;
    uint256 public seasonStartTime;

    constructor(address _spaceships, address _argonauts, uint256 _totalStardustPerSecond, uint256 _seasonEndTime) {
        // Set total stardust per second
        totalStardustPerSecond = _totalStardustPerSecond;
        // Set spaceships contract
        spaceships = IAtlantisSpaceships(_spaceships);
        // Set argonauts contract
        argonauts = IERC721(_argonauts);

        // Global race pool, 15% of total
        poolInfo[0].poolType = 0;
        poolInfo[0].stardustPerSecond = (_totalStardustPerSecond * 15) / 100;
        poolInfo[0].lastRewardTime = block.timestamp;
        poolInfo[0].accStardustPerPoint = 0;
        poolInfo[0].totalPoints = 0;
        poolInfo[0].totalCount = 0;

        // Common race pool, 40% of total
        poolInfo[1].poolType = 1;
        poolInfo[1].stardustPerSecond = (_totalStardustPerSecond * 40) / 100;
        poolInfo[1].lastRewardTime = block.timestamp;
        poolInfo[1].accStardustPerPoint = 0;
        poolInfo[1].totalPoints = 0;
        poolInfo[1].totalCount = 0;

        // Uncommon race pool, 35% of total
        poolInfo[2].poolType = 2;
        poolInfo[2].stardustPerSecond = (_totalStardustPerSecond * 35) / 100;
        poolInfo[2].lastRewardTime = block.timestamp;
        poolInfo[2].accStardustPerPoint = 0;
        poolInfo[2].totalPoints = 0;
        poolInfo[2].totalCount = 0;

        // Rare + Epic race pool, 10% of total
        poolInfo[3].poolType = 3;
        poolInfo[3].stardustPerSecond = (_totalStardustPerSecond * 10) / 100;
        poolInfo[3].lastRewardTime = block.timestamp;
        poolInfo[3].accStardustPerPoint = 0;
        poolInfo[3].totalPoints = 0;
        poolInfo[3].totalCount = 0;

        // Scratch generation cut off date
        seasonEndTime = _seasonEndTime;
    }

    /**
        onlyOwner functions >:]
    */
    // TODO: Add natspec Docs
    function setSpaceshipsContract(address _add) external onlyOwner {
        require(_add != address(0), "null addr");
        spaceships = IAtlantisSpaceships(_add);
    }

    // TODO: Add natspec Docs
    function setArgonautsContract(address _add) external onlyOwner {
        require(_add != address(0), "null addr");
        argonauts = IERC721(_add);
    }

    // TODO: Add natspec Docs
    function setStardustContract(address _add) external onlyOwner {
        require(_add != address(0), "null addr");
        stardust = IERC20(_add);
    }

    // TODO: Add natspec Docs
    function setGoldContract(address _add) external onlyOwner {
        require(_add != address(0), "null addr");
        gold = IERC20(_add);
    }

    // TODO: Add natspec Docs
    function setSeasonEndTime(uint256 _time) public onlyOwner {
        seasonEndTime = _time;
    }

    /**
     * @notice Set the multiplier for an argonaut
     * @param _numNFTs The number of NFTs staked
     * @param _multipliers The multiplier of the rewards
     */
    function setArgonautMultiplier(uint8[] memory _numNFTs, uint256[] memory _multipliers) external onlyOwner {
        for (uint256 i; i < _numNFTs.length; i++) {
            argonautMultiplier[_numNFTs[i]] = _multipliers[i];
        }
    }

    // TODO: Add natspec Docs
    function startSeason(bool _state) external onlyOwner {
        uint256 currentTime = block.timestamp;
        seasonStartTime = currentTime;
        seasonStarted = _state;
        massUpdatePools();
    }

    // TODO: Add natspec Docs
    function retrieveStardust() public onlyOwner {
        uint256 total = stardust.balanceOf(address(this));
        stardust.transfer(msg.sender, total);
        emit EmergencyWithdrawStardust(total);
    }

    // TODO: Add natspec Docs
    function retrieveGold() public onlyOwner {
        uint256 total = gold.balanceOf(address(this));
        gold.transfer(msg.sender, total);
        emit EmergencyWithdrawGold(total);
    }

    // TODO: Add natspec Docs
    function adminUnstakeSpaceships(uint256 _id) public onlyOwner {
        IERC721(address(spaceships)).safeTransferFrom(address(this), msg.sender, _id);
        emit EmergencyWithdraw(msg.sender, _id);
    }

    // TODO: Add natspec Docs
    function updatePool(uint256 _poolType) internal {
        uint256 currentTime = block.timestamp;

        // Check if season is over
        if (seasonEndTime <= currentTime) {
            currentTime = seasonEndTime;
        }

        // Update based on pool type
        if (seasonStarted == true && poolInfo[_poolType].totalPoints > 0) {
            uint256 stardustReward = (currentTime - poolInfo[_poolType].lastRewardTime) *
                poolInfo[_poolType].stardustPerSecond;
            poolInfo[_poolType].accStardustPerPoint += (stardustReward / poolInfo[_poolType].totalPoints);
        }

        poolInfo[_poolType].lastRewardTime = currentTime;
    }

    // TODO: Add natspec Docs
    function massUpdatePools() internal {
        for (uint256 i = 0; i < amountOfPools; i++) {
            updatePool(i);
        }
    }

    // TODO: Add natspec Docs
    function stakeSpaceships(uint256[] memory _ids) public {
        if (userInfo[msg.sender].totalSpeeds[0] > 0 && seasonStarted == true) {
            massUpdatePools();
            settlePendingStardust(msg.sender);
        }

        for (uint256 i = 0; i < _ids.length; i++) {
            AtlantisLib.Rarity _type = spaceships.getRarity(_ids[i]);
            console.log("Spaceship Type Before: ", uint256(_type));
            // Convert spaceship type to uint256
            uint256 _spaceshipType = uint256(_type) + 1;
            // If spaceship type is 4, it is an epic and in same pool as rare, so we set it to 3
            if (_spaceshipType == 4) {
                _spaceshipType = 3;
            }
            uint256 _speed = spaceships.getSpeed(_ids[i], _type);
            uint256 currentTime = block.timestamp;
            require(currentTime < seasonEndTime, "Must stake before season end"); // TODO: Rewrite with custom error
            require(_speed > 0, "Spaceship must have more than 0 speed"); // TODO: Rewrite with custom error

            //Take Token and Transfer to Contract for Holding
            IERC721(address(spaceships)).safeTransferFrom(msg.sender, address(this), _ids[i]);
            // Increase to free pool and increase to protected pool
            increasePoolAndUserStats(_speed, 0, msg.sender);
            increasePoolAndUserStats(_speed, _spaceshipType, msg.sender);
            userInfo[msg.sender].stakedSpaceships.push(_ids[i]);

            emit Stake(msg.sender, _ids[i], _speed);
        }

        updateRewardDebts(msg.sender);
    }

    // TODO: Add natspec Docs
    function unstakeSpaceships(uint256[] calldata spaceshipIndexes) public {
        require(userInfo[msg.sender].stakedSpaceships.length >= spaceshipIndexes.length, "Too many indexes"); // TODO: Rewrite with custom error

        uint256[] memory spaceshipIDs = new uint256[](spaceshipIndexes.length);

        if (seasonStarted == true) {
            massUpdatePools();
            settlePendingStardust(msg.sender);
        }

        for (uint256 i = 0; i < spaceshipIndexes.length; i++) {
            uint256 _id = userInfo[msg.sender].stakedSpaceships[spaceshipIndexes[i]];
            spaceshipIDs[i] = _id;
            uint256 _spaceshipRarity = uint256(spaceships.getRarity(_id));
            uint256 _speed = spaceships.getSpeed(_id, spaceships.getRarity(_id));

            // decrease for free pool and protected pool
            decreasePoolAndUserStats(_speed, 0, msg.sender);
            decreasePoolAndUserStats(_speed, _spaceshipRarity, msg.sender);

            userInfo[msg.sender].stakedSpaceships[spaceshipIndexes[i]] = 0;

            // transfer spaceship back to user
            IERC721(address(spaceships)).safeTransferFrom(address(this), msg.sender, _id);
            emit Unstaked(msg.sender, _id);
        }

        updateRewardDebts(msg.sender);

        // remove all the 0s from the stakedSpaceships array and re initialise new staked spaceships array
        uint256[] memory newstakedSpaceships = new uint256[](
            userInfo[msg.sender].stakedSpaceships.length - spaceshipIndexes.length
        );
        uint256 index = 0;
        // TODO: `uint256 i` is more optimized than `uint256 i = 0`
        for (uint256 i = 0; i < userInfo[msg.sender].stakedSpaceships.length; i++) {
            if (userInfo[msg.sender].stakedSpaceships[i] != 0) {
                newstakedSpaceships[index] = userInfo[msg.sender].stakedSpaceships[i];
                index++;
            }
        }
        userInfo[msg.sender].stakedSpaceships = newstakedSpaceships;
    }

    /**
     * @notice Stake argonauts to multiply user's pool total speed according to argonautsMultiplier
     * @param _poolId The pool id to stake argonauts to
     * @param _ids The ids of the argonauts to stake
     */
    function stakeArgonauts(uint8 _poolId, uint256[] memory _ids) public {
        require(userInfo[msg.sender].stakedSpaceships.length > 0, "Must have staked spaceship"); // TODO: Rewrite with custom error
        uint8 currentArgonautsStaked = uint8(argonautsStaked[msg.sender][_poolId].length);
        // Decrease user stats due to argonauts so that can recalculate with new argonauts
        decreaseUserStats(argonautMultiplier[currentArgonautsStaked], msg.sender, _poolId);
        for (uint256 i = 0; i < _ids.length; i++) {
            // TODO: `uint256 i` is more optimized than `uint256 i = 0`
            // Require that length of argonauts[_pooLIds] is less than 5
            require(argonautsStaked[msg.sender][_poolId].length < 5, "Cannot stake more than 5 argonauts per pool");
            argonautsStaked[msg.sender][_poolId].push(_ids[i]);
            //Take Token and Transfer to Contract for Holding
            IERC721(address(argonauts)).safeTransferFrom(msg.sender, address(this), _ids[i]);
            emit StakeArgonauts(msg.sender, _poolId, _ids[i]);
        }

        // Increase user stats due to argonauts
        increaseUserStats(
            argonautMultiplier[uint8(argonautsStaked[msg.sender][_poolId].length - 1)],
            msg.sender,
            _poolId
        );

        // Update pool stats
        updatePool(_poolId);
        // Update reward debts
        updateRewardDebts(msg.sender);
    }

    // Unstakes argonauts and decreases user stats
    // TODO: Add natspec Docs
    function unstakeArgonauts(uint256 _poolId, uint256[] memory _indexes) public {
        require(argonautsStaked[msg.sender][_poolId].length > 0, "Must have staked argonauts");
        require(_indexes.length <= argonautsStaked[msg.sender][_poolId].length, "Too many indexes");

        // Decrease user stats due to argonauts
        decreaseUserStats(
            argonautMultiplier[uint8(argonautsStaked[msg.sender][_poolId].length - 1)],
            msg.sender,
            _poolId
        );

        for (uint256 i = 0; i < _indexes.length; i++) {
            // TODO: `uint256 i` is more optimized than `uint256 i = 0`
            uint256 _id = argonautsStaked[msg.sender][_poolId][_indexes[i]];
            // transfer argonaut back to user
            IERC721(address(argonauts)).safeTransferFrom(address(this), msg.sender, _id);
            emit UnstakeArgonauts(msg.sender, _poolId, _id);
            argonautsStaked[msg.sender][_poolId][_indexes[i]] = 0;
        }

        // remove all the 0s from the argonauts array and re initialise new argonauts array
        uint256[] memory newArgonauts = new uint256[](argonautsStaked[msg.sender][_poolId].length - _indexes.length);
        uint256 index = 0;
        for (uint256 i = 0; i < argonautsStaked[msg.sender][_poolId].length; i++) {
            // TODO: `uint256 i` is more optimized than `uint256 i = 0`
            if (argonautsStaked[msg.sender][_poolId][i] != 0) {
                newArgonauts[index] = argonautsStaked[msg.sender][_poolId][i];
                index++;
            }
        }
        argonautsStaked[msg.sender][_poolId] = newArgonauts;

        // Increase user stats due to argonauts
        increaseUserStats(
            argonautMultiplier[uint8(argonautsStaked[msg.sender][_poolId].length - 1)],
            msg.sender,
            _poolId
        );

        // Update pool stats
        updatePool(_poolId);
        // Update reward debts
        updateRewardDebts(msg.sender);
    }

    // TODO: Add natspec Docs
    function increasePoolAndUserStats(uint256 _speed, uint256 _poolType, address _user) internal {
        poolInfo[_poolType].totalPoints += _speed;
        poolInfo[_poolType].totalCount++;
        userInfo[_user].totalSpeeds[_poolType] += _speed;
    }

    // TODO: Add natspec Docs
    function decreasePoolAndUserStats(uint256 _speed, uint256 _poolType, address _user) internal {
        poolInfo[_poolType].totalPoints -= _speed;
        poolInfo[_poolType].totalCount--;
        userInfo[_user].totalSpeeds[_poolType] -= _speed;
    }

    // Increase user stats due to argonauts
    // TODO: Add natspec Docs
    function increaseUserStats(uint256 _multiplier, address _user, uint256 _poolId) internal {
        userInfo[_user].totalSpeeds[_poolId] *= _multiplier;
        poolInfo[_poolId].totalPoints += userInfo[_user].totalSpeeds[_poolId];
    }

    // Decrease user stats due to argonauts
    // TODO: Add natspec Docs
    function decreaseUserStats(uint256 _multiplier, address _user, uint256 _poolId) internal {
        userInfo[_user].totalSpeeds[_poolId] /= _multiplier;
        poolInfo[_poolId].totalPoints -= userInfo[_user].totalSpeeds[_poolId];
    }

    // TODO: Add natspec Docs
    function updateRewardDebts(address _user) internal {
        // TODO: `uint256 i` is more optimized than `uint256 i = 0`
        for (uint256 i = 0; i < amountOfPools; i++) {
            uint256 rewardDebt = userInfo[_user].totalSpeeds[i] * poolInfo[i].accStardustPerPoint;
            userInfo[_user].rewardDebts[i] = rewardDebt;
        }
    }

    // TODO: might not need
    function settlePendingStardust(address _user) internal {
        UserInfo memory user = userInfo[_user];
        uint256 total;
        // Calculate Owed
        // TODO: `uint256 i` is more optimized than `uint256 i = 0`
        for (uint256 i = 0; i < amountOfPools; i++) {
            uint256 accStardust = user.totalSpeeds[i] * poolInfo[i].accStardustPerPoint;
            console.log("accStardust: %s", accStardust);
            uint256 pending = accStardust - user.rewardDebts[i];
            console.log("pending: %s", pending);
            total += pending;
        }

        // Split total into 80% stardust, 20% gold
        uint256 stardustAmount = (total * 80) / 100;
        uint256 goldAmount = total - stardustAmount;
        stardust.transfer(_user, stardustAmount);

        gold.transfer(_user, goldAmount);

        userInfo[_user].totalGold += goldAmount;
        userInfo[_user].totalStardust += stardustAmount;

        emit Payout(_user, goldAmount, stardustAmount);
    }

    // TODO: Add natspec Docs
    function getRewards() public {
        // TODO: Optimize: Refactor with custom error
        require(seasonStarted == true, "Must wait until season has started");
        massUpdatePools();
        settlePendingStardust(msg.sender);
        updateRewardDebts(msg.sender);
    }

    // TODO: Add natspec Docs
    function viewRewards(address _user) external view returns (uint256[4] memory) {
        UserInfo memory user = userInfo[_user];
        uint256[4] memory rewards;

        if (seasonStarted == true) {
            uint256 currentTime = block.timestamp;
            if (currentTime > seasonEndTime) {
                currentTime = seasonEndTime;
            }
            // TODO: `uint256 i` is more optimized than `uint256 i = 0`
            for (uint256 i = 0; i < amountOfPools; i++) {
                PoolInfo memory pool = poolInfo[i];
                if (pool.totalPoints == 0) {
                    rewards[i] = 0;
                } else {
                    uint256 accStardustPerPoint = pool.accStardustPerPoint;
                    uint256 multiplier = currentTime - pool.lastRewardTime;
                    uint256 stardustReward = multiplier * pool.stardustPerSecond;
                    accStardustPerPoint += (stardustReward / pool.totalPoints);
                    uint256 accStardust = user.totalSpeeds[i] * accStardustPerPoint;
                    uint256 pending = accStardust - user.rewardDebts[i];
                    rewards[i] = pending;
                }
            }
        }

        return rewards;
    }

    /**
        View functions O.O
    */
    // TODO: Add natspec Docs
    function viewUserInfo(address _user) public view returns (UserInfo memory) {
        // Get userinfo memory
        UserInfo memory user = userInfo[_user];
        return user;
    }

    // TODO: Add natspec Docs
    function viewPoolInfo(uint256 _poolType) public view returns (PoolInfo memory) {
        return poolInfo[_poolType];
    }

    // Able to recieve ERC721
    function onERC721Received(address, address, uint256, bytes memory) public virtual override returns (bytes4) {
        return this.onERC721Received.selector;
    }
}

contracts/AtlantisSpaceships.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.11;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./common/RandomlyAssigned.sol";
import "./common/WithLimitedSupply.sol";
import "./interface/IAtlantisSpaceships.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "./interface/IAtlantisComponents.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155Receiver.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "./common/Base64.sol";
// import console log
import "hardhat/console.sol"; // TODO: Remove in production

/// @title Atlantis Spaceships Mint Contract
contract AtlantisSpaceships is ERC721, Ownable, IAtlantisSpaceships, RandomlyAssigned, IERC1155Receiver {
    using Strings for uint256;
    using ECDSA for bytes32;

    string public baseURI;

    uint256 currentSupply;
    // General Mint Settings
    // Public Sale Mint Settings
    uint256 public publicMintPrice = 0 ether;
    uint256 public publicMintMaxPerWallet = type(uint256).max;
    mapping(address => uint256) public publicMintCount;

    // Treasury
    address public treasury;

    // Events
    // Equip event
    event EquipmentModified(
        uint256 indexed spaceshipId,
        uint256 fireComponentId,
        uint256 lightningComponentId,
        uint256 steelComponentId
    );
    event PublicMint(address indexed to, uint256 amount);
    event WithdrawETH(uint256 amountWithdrawn);
    event PublicSaleOpened(bool status, uint256 timestamp);

    // Atlantis
    IAtlantisComponents public components;
    uint8[] public componentsSpeedMultiplier;
    // Mapping of spaceshipIDs to spaceships
    mapping(uint256 => Spaceship) public spaceships;

    constructor(
        address _owner,
        string memory __baseURI,
        address _components
    ) ERC721("Atlantis Spaceships", "SPACESHIPS") RandomlyAssigned(4000, 1) {
        setTreasury(_owner);
        setBaseURI(__baseURI);
        transferOwnership(_owner);
        components = IAtlantisComponents(_components);
    }

    function onERC1155Received(
        address operator,
        address from,
        uint256 id,
        uint256 value,
        bytes calldata data
    ) external pure returns (bytes4) {
        return this.onERC1155Received.selector;
    }

    function onERC1155BatchReceived(
        address operator,
        address from,
        uint256[] calldata ids,
        uint256[] calldata values,
        bytes calldata data
    ) external pure returns (bytes4) {
        return this.onERC1155BatchReceived.selector;
    }

    // -------------------- ATLANTIS FUNCTIONS --------------------------
    /** Token Ids
     * 1 2 3 Fire Lightning Steel Component Level 1
     * 4 5 6 Fire Lightning Steel Component Level 2
     * 7 8 9 Fire Lightning Steel Component Level 3
     * 10 11 12 Fire Lightning Steel Component Level 4
     * 13 14 15 Fire Lightning Steel Component Level 5
     * 16 17 18 Fire Lightning Steel Component Level 6
     * 19 20 21 Fire Lightning Steel Component Level 7
     * 22 23 24 Fire Lightning Steel Component Level 8
     * 25 26 27 Fire Lightning Steel Component Level 9
     * 28 29 30 Fire Lightning Steel Component Level 10
     */

    function modifyEquipment(
        uint256 _spaceshipId,
        uint256 _fireComponentId,
        uint256 _lightningComponentId,
        uint256 _steelComponentId
    ) external {
        // Check if the spaceship is owned by the caller if not throw notOwner error
        if (ownerOf(_spaceshipId) != msg.sender) {
            revert NotOwner();
        }
        // Get spaceship
        Spaceship storage spaceship = spaceships[_spaceshipId];

        // Save the componentId into if it is not zero
        uint256 _oldFireId = spaceship.fireComponentId;
        uint256 _oldLightningId = spaceship.lightningComponentId;
        uint256 _oldSteelId = spaceship.steelComponentId;

        // Set spaceship components to new components
        spaceship.fireComponentId = _fireComponentId;
        spaceship.lightningComponentId = _lightningComponentId;
        spaceship.steelComponentId = _steelComponentId;

        // Fire
        _equipHelper(_oldFireId, _fireComponentId, 1);
        // Lightning
        _equipHelper(_oldLightningId, _lightningComponentId, 2);
        // Steel
        _equipHelper(_oldSteelId, _steelComponentId, 0);

        // Emit event
        emit EquipmentModified(_spaceshipId, _fireComponentId, _lightningComponentId, _steelComponentId);
    }

    // Function to handle oldid and newid
    function _equipHelper(uint256 _oldId, uint256 _newId, uint8 _element) internal {
        // Do nothing if oldId and newId are the same
        if (_oldId == _newId) {
            return;
        }
        // Check that newId is the correct element if not throw wrongelement
        if (_newId != 0 && components.getElement(_newId) != _element) {
            revert WrongElement();
        }
        // Check if oldId is not zero
        if (_oldId != 0) {
            // Transfer oldId to owner
            IERC1155(address(components)).safeTransferFrom(address(this), msg.sender, _oldId, 1, "");
        }
        // Check if newId is not zero
        if (_newId != 0) {
            // Transfer newId to contract
            IERC1155(address(components)).safeTransferFrom(msg.sender, address(this), _newId, 1, "");
        }
        return;
    }

    // Get spaceship
    /**
     * @dev Get details of a spaceship from its id
     * @param _tokenId Token ID of spaceship
     */
    function getSpaceship(uint256 _tokenId) external view returns (Spaceship memory) {
        return spaceships[_tokenId];
    }

    /**
     * @dev Get speed of a spaceship from its id
     * @param _tokenId Token ID of spaceship
     */

    // TODO: Remove _rarity as parameter, rarity should be retrieved from spaceship internally
    function getSpeed(uint256 _tokenId, AtlantisLib.Rarity _rarity) public view returns (uint256) {
        // Get spaceship
        Spaceship memory spaceship = spaceships[_tokenId];
        uint16 fireSpeed = components.getSpeed(spaceship.fireComponentId);
        uint16 lightningSpeed = components.getSpeed(spaceship.lightningComponentId);
        uint16 steelSpeed = components.getSpeed(spaceship.steelComponentId);

        return (uint16(_rarity) + 1) * (fireSpeed + lightningSpeed + steelSpeed + 1);
    }

    /**
     * @dev Get rarity of a spaceship from its id
     * @param _tokenId Token ID of spaceship
     */
    function getRarity(uint256 _tokenId) external view returns (AtlantisLib.Rarity) {
        // Get spaceship
        Spaceship memory spaceship = spaceships[_tokenId];
        return spaceship.rarity;
    }

    // Set spaceship rarity

    // TODO: Add natspec Docs
    function setSpaceshipRarity(
        uint256[] calldata _tokenIds,
        AtlantisLib.Rarity[] calldata _rarity
    ) external onlyOwner {
        // Loop through planet types and set planet type
        for (uint256 i; i < _rarity.length; i++) {
            spaceships[_tokenIds[i]].rarity = _rarity[i];
        }
    }

    // -------------------- ATLANTIS METADATA FUNCTIONS --------------------------

    /**
     * @notice Returns planet orbit as string
     */
    function _spaceshipRarityToString(AtlantisLib.Rarity _rarity) internal pure returns (string memory) {
        if (_rarity == AtlantisLib.Rarity.COMMON) {
            return "Common";
        } else if (_rarity == AtlantisLib.Rarity.UNCOMMON) {
            return "Uncommon";
        } else if (_rarity == AtlantisLib.Rarity.RARE) {
            return "Rare";
        } else if (_rarity == AtlantisLib.Rarity.EPIC) {
            return "Epic";
        } else {
            return "";
        }
    }

    /**
     * @notice Get ImageURI
     */
    function getImageURI(
        AtlantisLib.Rarity _rarity,
        uint8 _lightningLevel,
        uint8 _fireLevel,
        uint8 _steelLevel
    ) internal view returns (string memory) {
        return
            string(
                abi.encodePacked(
                    _baseURI(),
                    uint256(_rarity).toString(),
                    "/",
                    uint256(_lightningLevel).toString(),
                    "/",
                    uint256(_fireLevel).toString(),
                    "/",
                    uint256(_steelLevel).toString(),
                    ".png"
                )
            );
    }

    /**
     * @notice Returns token metadata
     * @dev Metadata is stored on-chain
     */
    function tokenURI(uint256 _tokenId) public view override returns (string memory) {
        // Get Spaceship
        Spaceship memory spaceship = spaceships[_tokenId];

        // Get rarity
        AtlantisLib.Rarity rarity = spaceship.rarity;

        // Name
        string memory json = string(abi.encodePacked('{"name": "', name(), " #", _tokenId.toString(), '",'));

        uint8 lightningLevel = components.getLevel(spaceship.lightningComponentId);
        uint8 fireLevel = components.getLevel(spaceship.fireComponentId);
        uint8 steelLevel = components.getLevel(spaceship.steelComponentId);

        string memory lightningString = lightningLevel != 0 ? uint256(lightningLevel).toString() : "Unequipped";
        string memory fireString = fireLevel != 0 ? uint256(fireLevel).toString() : "Unequipped";
        string memory steelString = steelLevel != 0 ? uint256(steelLevel).toString() : "Unequipped";
        // Description
        json = string(abi.encodePacked(json, '"description": "AtlantisSpaceships!",'));

        // Attributes
        json = string(
            abi.encodePacked(
                json,
                '"attributes": [{"trait_type": "Rarity", "value": "',
                _spaceshipRarityToString(rarity),
                '"},',
                '{"trait_type": "Thunderbolt Wings Level", "value": "',
                lightningString,
                '"},',
                '{"trait_type": "Inferno Thrusters Level", "value": "',
                fireString,
                '"},',
                '{"trait_type": "Argonium Exoshell Level", "value": "',
                steelString,
                '"},'
            )
        );

        json = string(
            abi.encodePacked(
                json,
                '{"trait_type": "Speed", "value": "',
                uint256(getSpeed(_tokenId, rarity)).toString(),
                '"}],'
            )
        );

        json = Base64.encode(
            bytes(
                string(
                    abi.encodePacked(
                        json,
                        '"image": "',
                        getImageURI(rarity, lightningLevel, fireLevel, steelLevel),
                        '"}'
                    )
                )
            )
        );
        return string(abi.encodePacked("data:application/json;base64,", json));
    }

    // -------------------- MINT FUNCTIONS --------------------------

    /**
     * @notice Public Mint
     * @param _mintAmount Amount that is minted
     */
    function mint(address _address, uint256 _mintAmount) public payable ensureAvailabilityFor(_mintAmount) onlyOwner {
        // Check if enough ETH is sent
        require(msg.value == _mintAmount * publicMintPrice, "AtlantisSpaceships: Insufficient CRO!");

        // Check if mints does not exceed total max supply
        require(
            totalSupply() + _mintAmount <= totalMaxSupply(),
            "AtlantisSpaceships: Max Supply for Public Mint Reached!"
        );

        publicMintCount[_address] += _mintAmount;
        currentSupply += _mintAmount;

        for (uint256 i; i < _mintAmount; i++) {
            _safeMint(_address, nextToken());
        }

        emit PublicMint(_address, _mintAmount);
    }

    /**
     * @notice Airdrop multiple nfts to multiple addresses
     * @param _addresses All addresses that will receive nfts
     * @param _mintAmount Amount of nfts that will be minted for each address
     */
    function airdrop(address[] calldata _addresses, uint256[] calldata _mintAmount) external onlyOwner {
        for (uint256 i; i < _addresses.length; i++) {
            mint(_addresses[i], _mintAmount[i]);
        }
    }

    // ------------------------- OWNER FUNCTIONS ----------------------------
    /**
     * @dev Set Revealed Metadata URI
     */
    function setBaseURI(string memory _newBaseURI) public onlyOwner {
        baseURI = _newBaseURI;
    }

    /**
     * @dev Set public mint price
     */
    function setPublicMintPrice(uint256 _publicMintPrice) public onlyOwner {
        publicMintPrice = _publicMintPrice;
    }

    /**
     * @notice Withdraw all ETH from this account to the owner
     */
    function withdrawFund() external onlyOwner {
        (bool success, ) = payable(treasury).call{ value: address(this).balance }("");
        require(success, "Transfer failed");
    }

    /**
     * @notice Sets the treasury address
     */
    function setTreasury(address _treasury) public onlyOwner {
        treasury = _treasury;
    }

    /**
     * @dev Base URI for computing {tokenURI}. If set, the resulting URI for each
     * token will be the concatenation of the `baseURI` and the `tokenId`. Empty
     * by default, it can be overridden in child contracts.
     */
    function _baseURI() internal view virtual override returns (string memory) {
        return baseURI;
    }

    // TODO: Add Docs
    function exists(uint256 tokenId) external view returns (bool) {
        return _exists(tokenId);
    }

    // TODO: Add Docs
    function totalSupply() public view returns (uint256) {
        return currentSupply;
    }
}

