// SPDX-License-Identifier: MIT

pragma solidity ^0.8.11;

import { AtlantisLib } from "../common/AtlantisLib.sol";

interface IAtlantisSpaceships {
    error NotOwner();
    error WrongElement();

    struct Spaceship {
        AtlantisLib.Rarity rarity;
        uint256 fireComponentId;
        uint256 lightningComponentId;
        uint256 steelComponentId;
    }

    function modifyEquipment(
        uint256 _spaceshipId,
        uint256 _fireComponentId,
        uint256 _lightningComponentId,
        uint256 _steelComponentId
    ) external;

    function getSpaceship(uint256 _tokenId) external view returns (Spaceship memory);

    function getSpeed(uint256 _tokenId, AtlantisLib.Rarity _rarity) external view returns (uint256);

    function getRarity(uint256 _tokenId) external view returns (AtlantisLib.Rarity);
}

