import "@openzeppelin/contracts/token/ERC1155/IERC1155Receiver.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

/// @title AtlanteanTrove Quest Rewards Claiming Contract
/// @author Kratos
/*
