// SPDX-License-Identifier: MIT
pragma solidity ^0.8.11;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./common/AddressRegistry.sol";

contract AtlantisAddressRegistry is Ownable, AddressRegistry {
    bytes32 private constant ARGONAUTS = "ARGONAUTS";
    bytes32 private constant ARGO = "ARGO";
    bytes32 private constant XARGO = "XARGO";
    bytes32 private constant GOLD = "GOLD";
    bytes32 private constant STARDUST = "STARDUST";
    bytes32 private constant ATLANTIS = "ATLANTIS";
    bytes32 private constant ATLANTIS_PLANETS = "ATLANTIS_PLANETS";
    bytes32 private constant STAKING_WITH_LOCK = "STAKING_WITH_LOCK";
    bytes32 private constant ATLANTIS_GEMSTONES = "GEMSTONES";
    bytes32 private constant ATLANTIS_EQUIPMENTS = "EQUIPMENTS";
    bytes32 private constant ATLANTIS_SPACESHIPS = "SPACESHIPS";
    bytes32 private constant ATLANTIS_RACING = "RACING";
    bytes32 private constant ATLANTIS_MARKETPLACE = "MARKETPLACE";
    bytes32 private constant ATLANTIS_AUCTION = "AUCTION";
    bytes32 private constant STARDUST_PLEDGING = "PLEDGING";
    bytes32 private constant GOLD_STAKING = "GOLD_STAKING";
    mapping(address => bool) private _addresses;

    function setArgonauts(address contractAddress) external onlyOwner {
        _setAddress(ARGONAUTS, contractAddress);
    }

    function setArgo(address contractAddress) external onlyOwner {
        _setAddress(ARGO, contractAddress);
    }

    function setXargo(address contractAddress) external onlyOwner {
        _setAddress(XARGO, contractAddress);
    }

    function setGold(address contractAddress) external onlyOwner {
        _setAddress(GOLD, contractAddress);
    }

    function setStardust(address contractAddress) external onlyOwner {
        _setAddress(STARDUST, contractAddress);
    }

    function setAtlantis(address contractAddress) external onlyOwner {
        _setAddress(ATLANTIS, contractAddress);
    }

    function setAtlantisPlanets(address contractAddress) external onlyOwner {
        _setAddress(ATLANTIS_PLANETS, contractAddress);
    }

    function setStakingWithLock(address contractAddress) external onlyOwner {
        _setAddress(STAKING_WITH_LOCK, contractAddress);
    }

    function setGemstones(address contractAddress) external onlyOwner {
        _setAddress(ATLANTIS_GEMSTONES, contractAddress);
    }

    function setEquipments(address contractAddress) external onlyOwner {
        _setAddress(ATLANTIS_EQUIPMENTS, contractAddress);
    }

    function setSpaceships(address contractAddress) external onlyOwner {
        _setAddress(ATLANTIS_SPACESHIPS, contractAddress);
    }

    function setRacing(address contractAddress) external onlyOwner {
        _setAddress(ATLANTIS_RACING, contractAddress);
    }

    function setMarketplace(address contractAddress) external onlyOwner {
        _setAddress(ATLANTIS_MARKETPLACE, contractAddress);
    }

    function setAuction(address contractAddress) external onlyOwner {
        _setAddress(ATLANTIS_AUCTION, contractAddress);
    }

    function setPledging(address contractAddress) external onlyOwner {
        _setAddress(STARDUST_PLEDGING, contractAddress);
    }

    function setGoldStaking(address contractAddress) external onlyOwner {
        _setAddress(GOLD_STAKING, contractAddress);
    }

    function getArgonauts() external view returns (address) {
        return getAddress(ARGONAUTS);
    }

    function getArgo() external view returns (address) {
        return getAddress(ARGO);
    }

    function getXargo() external view returns (address) {
        return getAddress(XARGO);
    }

    function getGold() external view returns (address) {
        return getAddress(GOLD);
    }

    function getStardust() external view returns (address) {
        return getAddress(STARDUST);
    }

    function getAtlantis() public view returns (address) {
        return getAddress(ATLANTIS);
    }

    function getAtlantisPlanets() public view returns (address) {
        return getAddress(ATLANTIS_PLANETS);
    }

    function getStakingWithLock() external view returns (address) {
        return getAddress(STAKING_WITH_LOCK);
    }

    function getGemstones() public view returns (address) {
        return getAddress(ATLANTIS_GEMSTONES);
    }

    function getEquipments() public view returns (address) {
        return getAddress(ATLANTIS_EQUIPMENTS);
    }

    function getSpaceships() external view returns (address) {
        return getAddress(ATLANTIS_SPACESHIPS);
    }

    function getRacing() external view returns (address) {
        return getAddress(ATLANTIS_RACING);
    }

    function getMarketplace() external view returns (address) {
        return getAddress(ATLANTIS_MARKETPLACE);
    }

    function getAuction() external view returns (address) {
        return getAddress(ATLANTIS_AUCTION);
    }

    function getPledging() external view returns (address) {
        return getAddress(STARDUST_PLEDGING);
    }

    function getGoldStaking() external view returns (address) {
        return getAddress(GOLD_STAKING);
    }

    function isControllerContract(address _contractAddress) external view returns (bool) {
        if (
            _contractAddress == getAtlantis() ||
            _contractAddress == getGemstones() ||
            _contractAddress == getAtlantisPlanets() ||
            _contractAddress == getEquipments()
        ) {
            return true;
        }
        return false;
    }
}

contracts/AtlantisAuction.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./interface/IAtlantisPlanets.sol";
import "./AtlantisAddressRegistry.sol";

error AuctionSettled();
error AuctionNotInitialized();
error AuctionNotLive();
error ReservePriceNotMet();
error IncrementalPriceNotMet();
error BidsNotSorted();
error NonExistentBid();
error AuctionStillLive();
error WithdrawFailed();
error BidIncrementTooLow();
error NotEOA();

contract AtlantisAuction is Ownable, ReentrancyGuard {
    struct Bid {
        address bidder;
        uint192 amount;
        uint64 bidTime;
    }

    struct BidIndex {
        uint8 index;
        bool isSet;
    }

    event NewBid(address bidder, uint256 value);
    event BidIncreased(address bidder, uint256 oldValue, uint256 increment);
    event AuctionExtended();

    // The max number of top bids the auction will accept
    uint256 public constant MAX_NUM_BIDS = 8;

    // The token contract to mint from
    AtlantisAddressRegistry public addressRegistry;

    // The minimum amount of time left in an auction after a new bid is created
    uint256 public timeBuffer;

    // The minimum price accepted in an auction
    uint256 public reservePrice;

    // The minimum percentage difference between the last bid amount and the current bid
    uint8 public minBidIncrementPercentage;

    // The minimum amount a user needs to submit for a stacked bid
    uint256 public minStackedBidIncrement;

    // The start time of the auction
    uint256 public startTime;

    // The end time of the auction
    uint256 public endTime;

    // Whether or not the auction has settled.
    bool public auctionSettled;

    // The current highest bids made in the auction
    Bid[MAX_NUM_BIDS] public activeBids;

    // The mapping between an address and its active bid. The isSet flag differentiates the default
    // uint value 0 from an actual 0 value.
    mapping(address => BidIndex) public bidIndexes;

    constructor(
        AtlantisAddressRegistry _addressRegistry,
        uint256 _timeBuffer,
        uint256 _reservePrice,
        uint8 _minBidIncrementPercentage,
        uint256 _minStackedBidIncrement,
        uint256 _startTime,
        uint256 _endTime
    ) {
        addressRegistry = _addressRegistry;
        timeBuffer = _timeBuffer;
        reservePrice = _reservePrice;
        minBidIncrementPercentage = _minBidIncrementPercentage;
        minStackedBidIncrement = _minStackedBidIncrement;
        startTime = _startTime;
        endTime = _endTime;
    }

    modifier onlyEOA() {
        if (tx.origin != msg.sender) {
            revert NotEOA();
        }
        _;
    }

    /**
     * @notice Handle users' bids
     * @dev Bids must be made while the auction is live. Bids must meet a minimum reserve price.
     *
     * The first 8 bids made will be accepted as valid. Subsequent bids must be a percentage
     * higher than the lowest of the 8 active bids. When a low bid is replaced, the ETH will
     * be refunded back to the original bidder.
     *
     * If a valid bid comes in within the last `timeBuffer` seconds, the auction will be extended
     * for another `timeBuffer` seconds. This will continue until no new active bids come in.
     *
     * If a wallet makes a bid while it still has an active bid, the second bid will
     * stack on top of the first bid. If the second bid doesn't meet the `minStackedBidIncrement`
     * threshold, an error will be thrown. A wallet will only have one active bid at at time.
     */
    function bid() public payable nonReentrant onlyEOA {
        if (auctionSettled) {
            revert AuctionSettled();
        }
        if (startTime == 0 || endTime == 0) {
            revert AuctionNotInitialized();
        }
        require(block.timestamp > startTime || block.timestamp < endTime, "Auction has ended");

        BidIndex memory existingIndex = bidIndexes[msg.sender];
        if (existingIndex.isSet) {
            // Case when the user already has an active bid
            if (msg.value < minStackedBidIncrement || msg.value == 0) {
                revert BidIncrementTooLow();
            }

            uint192 oldValue = activeBids[existingIndex.index].amount;
            unchecked {
                activeBids[existingIndex.index].amount = oldValue + uint192(msg.value);
            }
            activeBids[existingIndex.index].bidTime = uint64(block.timestamp);

            emit BidIncreased(msg.sender, oldValue, msg.value);
        } else {
            if (msg.value < reservePrice || msg.value == 0) {
                revert ReservePriceNotMet();
            }

            uint8 lowestBidIndex = getBidIndexToUpdate();
            uint256 lowestBidAmount = activeBids[lowestBidIndex].amount;
            address lowestBidder = activeBids[lowestBidIndex].bidder;

            unchecked {
                if (msg.value < lowestBidAmount + (lowestBidAmount * minBidIncrementPercentage) / 100) {
                    revert IncrementalPriceNotMet();
                }
            }

            // Refund lowest bidder and remove bidIndexes entry
            if (lowestBidder != address(0)) {
                delete bidIndexes[lowestBidder];
                _transferETH(lowestBidder, lowestBidAmount);
            }

            activeBids[lowestBidIndex] = Bid({
                bidder: msg.sender,
                amount: uint192(msg.value),
                bidTime: uint64(block.timestamp)
            });

            bidIndexes[msg.sender] = BidIndex({ index: lowestBidIndex, isSet: true });

            emit NewBid(msg.sender, msg.value);
        }

        // Extend the auction if the bid was received within `timeBuffer` of the auction end time
        if (endTime - block.timestamp < timeBuffer) {
            unchecked {
                endTime = block.timestamp + timeBuffer;
            }
            emit AuctionExtended();
        }
    }

    /**
     * @notice Gets the index of the entry in activeBids to update
     * @dev The index to return will be decided by the following rules:
     * If there are less than MAX_NUM_BIDS bids, the index of the first empty slot is returned.
     * If there are MAX_NUM_BIDS or more bids, the index of the lowest value bid is returned. If
     * there is a tie, the most recent bid with the low amount will be returned. If there is a tie
     * among bidTimes, the highest index is chosen.
     */
    function getBidIndexToUpdate() public view returns (uint8) {
        uint256 minAmount = activeBids[0].amount;
        // If the first value is 0 then we can assume that no bids have been submitted
        if (minAmount == 0) {
            return 0;
        }

        uint8 minIndex = 0;
        uint64 minBidTime = activeBids[0].bidTime;

        for (uint8 i = 1; i < MAX_NUM_BIDS; ) {
            uint256 bidAmount = activeBids[i].amount;
            uint64 bidTime = activeBids[i].bidTime;

            // A zero bidAmount means the slot is empty because we enforce non-zero bid amounts
            if (bidAmount == 0) {
                return i;
            } else if (bidAmount < minAmount || (bidAmount == minAmount && bidTime >= minBidTime)) {
                minAmount = bidAmount;
                minIndex = i;
                minBidTime = bidTime;
            }

            unchecked {
                ++i;
            }
        }

        return minIndex;
    }

    /**
     * @notice Get all active bids.
     * @dev Useful for ethers client to get the entire array at once.
     */
    function getAllActiveBids() external view returns (Bid[MAX_NUM_BIDS] memory) {
        return activeBids;
    }

    /**
     * @notice Settles the auction and mints a skateboard ticket NFT to each winner.
     * @dev Bids will be sorted in descending order off-chain due to constraints with
     * sorting structs on-chain via a field on the struct, however we will validate the
     * input on-chain before minting the NFTs. The input bids must be in descending order
     * by amount and all input bids must correspond to a bid in the `activeBids` mapping.
     * @dev Duplicate bids can be passed in to circumvent the validation logic. We are ok
     * with this loophole since this function is ownerOnly.
     * @dev Settlement is only possible once the auction is over.
     */
    function settleAuction(Bid[MAX_NUM_BIDS] calldata sortedBids) external onlyOwner nonReentrant {
        IAtlantisPlanets planets = IAtlantisPlanets(addressRegistry.getAtlantisPlanets());

        if (block.timestamp <= endTime) {
            revert AuctionStillLive();
        }
        if (auctionSettled) {
            revert AuctionSettled();
        }

        // Validate the input bids
        for (uint256 i = 0; i < MAX_NUM_BIDS; ) {
            Bid memory inputBid = sortedBids[i];
            BidIndex memory bidIndex = bidIndexes[inputBid.bidder];
            if (
                !bidIndex.isSet ||
                activeBids[bidIndex.index].bidder != inputBid.bidder ||
                activeBids[bidIndex.index].amount != inputBid.amount ||
                activeBids[bidIndex.index].bidTime != inputBid.bidTime
            ) {
                revert NonExistentBid();
            }

            // The zero-th index has nothing to compare against
            if (i != 0) {
                Bid memory prevBid = sortedBids[i - 1];
                if (inputBid.amount > prevBid.amount) {
                    revert BidsNotSorted();
                }
            }

            unchecked {
                ++i;
            }
        }
        // Get addresses from bids
        address[MAX_NUM_BIDS] memory winners;
        for (uint256 i = 0; i < MAX_NUM_BIDS; ) {
            winners[i] = sortedBids[i].bidder;
            unchecked {
                ++i;
            }
        }

        // Mint tickets to auction winners
        // planets.epicMint(winners);

        auctionSettled = true;
    }

    /**
     * @notice Transfers ETH to a specified address.
     * @dev This function can only be called internally.
     */
    function _transferETH(address to, uint256 value) internal returns (bool) {
        (bool success, ) = to.call{ value: value, gas: 30000 }(new bytes(0));
        return success;
    }

    /**
     * @notice Sets the start and end time of the auction.
     * @dev Only callable by the owner.
     */
    function setAuctionTimes(uint256 _startTime, uint256 _endTime) external onlyOwner {
        startTime = _startTime;
        endTime = _endTime;
    }

    /**
     * @notice Set the auction time buffer.
     * @dev Only callable by the owner.
     */
    function setTimeBuffer(uint256 _timeBuffer) external onlyOwner {
        timeBuffer = _timeBuffer;
    }

    /**
     * @notice Set the auction reserve price.
     * @dev Only callable by the owner.
     */
    function setReservePrice(uint256 _reservePrice) external onlyOwner {
        reservePrice = _reservePrice;
    }

    /**
     * @notice Set the auction minimum bid increment percentage.
     * @dev Only callable by the owner.
     */
    function setMinBidIncrementPercentage(uint8 _minBidIncrementPercentage) external onlyOwner {
        minBidIncrementPercentage = _minBidIncrementPercentage;
    }

    /**
     * @notice Set the auction replacing bid buffer amount.
     * @dev Only callable by the owner.
     */
    function setMinReplacementIncrease(uint256 _minStackedBidIncrement) external onlyOwner {
        minStackedBidIncrement = _minStackedBidIncrement;
    }

    /**
     * @notice Withdraws the contract value to the owner
     */
    function withdraw() external onlyOwner {
        bool success = _transferETH(msg.sender, address(this).balance);
        if (!success) {
            revert WithdrawFailed();
        }
    }
}

contracts/AtlantisEquipments.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "./interface/IAtlantisEquipments.sol";
import "./interface/IAtlantisGemstones.sol";
import "./interface/IStakingWithLock.sol";
import "./AtlantisAddressRegistry.sol";
import "./common/Base64.sol";

/// @title Atlantis - Equipment contract
/// @dev Fuse 2 equipment of the same tier to receive 1 from the next tier. Equipment can be equipped on spaceships to boost their speed in a race.
contract AtlantisEquipments is ERC1155, Pausable, Ownable, IAtlantisEquipments, ReentrancyGuard {
    using Strings for uint256;
    using SafeERC20 for IERC20;
    /// @notice Current payment mode
    PaymentMode public paymentMode;
    /// @notice Current payment token
    IERC20 public paymentToken;
    /// @notice Name of contract
    string public name;
    /// @notice Name of symbol
    string public symbol;
    /// @notice Total supply for each collection ids
    mapping(uint256 => uint256) private _totalSupply;
    /// @notice Base token URI
    string public baseTokenURI;
    /// @notice Max number of tokeniDS
    uint256 public constant TOTAL_TOKEN_IDS = 30;
    /// @notice Mint cost of equipments
    uint256 public mintCost = 200 ether;
    /// @notice Contract address of address registry
    AtlantisAddressRegistry public addressRegistry;
    /// @notice Cost of stardust required
    uint256[5] public stardustCost;
    /// @notice Number of gemstones required
    uint16[5] public gemstonesRequired;
    /// @notice Speeds of equipment
    uint16[10] public equipmentSpeeds;
    /// @notice Nonce used for calculation purposes
    uint256 private nonce;
    /// @notice Address of treasury
    address public treasury;
    /// @notice Max number of gemstones required
    uint16 public constant MAX_GEMSTONES_REQUIRED = 500;
    /// @notice Max stardust cost
    uint256 public constant MAX_STARDUST_COST = 500000 ether;
    /// @notice Max mint cost
    uint256 public constant MAX_MINT_COST = 10000000 ether;
    /// @notice Whitelisted addresses who can call airdrop
    mapping(address => bool) public devAddresses;

    /**
     * @notice Constructor for Atlantis Equipments
     * @param _name Name of contract
     * @param _symbol Symbol of contract
     * @param baseURI base URI with images
     * @param owner Address of owner
     * @param _registry Registry of Atlantis
     * @param _treasury Address of treaury
     */
    constructor(
        string memory _name,
        string memory _symbol,
        string memory baseURI,
        address owner,
        AtlantisAddressRegistry _registry,
        address _treasury,
        uint256[5] memory _stardustCost,
        uint16[5] memory _gemstonesRequired,
        uint16[10] memory _equipmentSpeeds
    ) ERC1155(baseURI) {
        // Check that registry and treasury are not address(0)
        require(address(_registry) != address(0), "AtlantisEquipment: Registry is address(0)");
        require(_treasury != address(0), "AtlantisEquipment: Treasury is address(0)");
        // Set name and symbol
        name = _name;
        symbol = _symbol;
        // Set metadata uri
        setBaseMetadataURI(baseURI);
        // Transform ownership to the owner
        transferOwnership(owner);
        // Get atlantis address
        addressRegistry = _registry;
        // Get treasury
        treasury = _treasury;
        // Initialise stardustCost, gemstonesRequired and equipmentSpeeds
        stardustCost = _stardustCost;
        gemstonesRequired = _gemstonesRequired;
        equipmentSpeeds = _equipmentSpeeds;

        // Set default payment mode
        paymentMode = PaymentMode.TOKEN;

        // Initialise paymentToken to stardust
        paymentToken = IERC20(addressRegistry.getStardust());

        // Add owner to the devAddresses
        devAddresses[owner] = true;
    }

    // ------------------------- VIEW FUNCTIONS ------------------------------

    /**
     * @dev Total amount of tokens in with a given id.
     * @return uint256
     */
    function totalSupply(uint256 id) public view returns (uint256) {
        return _totalSupply[id];
    }

    /**
     * @notice Get ImageURI
     * @param _tokenId The id of the equipment
     * @return returns string of imageURI
     */
    function _getImageURI(uint256 _tokenId) internal view returns (string memory) {
        return
            string(
                abi.encodePacked(
                    _baseURI(),
                    uint256(getElement(_tokenId)).toString(),
                    "/",
                    uint256(getLevel(_tokenId)).toString(),
                    ".png"
                )
            );
    }

    /**
     * @notice Get token metadata with tokenId
     * @param _tokenId The id of the equipment
     * @return returns string of token metadata
     */
    function uri(uint256 _tokenId) public view override returns (string memory) {
        require(exists(_tokenId), "Resources: URI query for nonexistent token");
        string memory _name = _tokenIdToEquipmentNameString(_tokenId);
        string memory json = string(
            abi.encodePacked('{"name": "', _name, " Level ", uint256(getLevel(_tokenId)).toString(), '",')
        );
        // Description
        json = string(
            abi.encodePacked(
                json,
                '"description": "Welcome to the captivating realm of Atlantis, the game-verse and home of the legendary Argonauts. Equipment are valuable items which provides speed boost to your Spaceships. Fuse 2 Equipment of the same level to level it up!",'
            )
        );
        // Attributes
        json = string(
            abi.encodePacked(
                json,
                '"attributes": [{"trait_type": "Type", "value": "',
                _name,
                '"},',
                '{"trait_type": "Level", "value": "',
                uint256(getLevel(_tokenId)).toString(),
                '"},',
                '{"trait_type": "Speed", "value": "',
                uint256(getSpeed(_tokenId)).toString(),
                '"}],'
            )
        );
        json = Base64.encode(bytes(string(abi.encodePacked(json, '"image": "', _getImageURI(_tokenId), '"}'))));
        return string(abi.encodePacked("data:application/json;base64,", json));
    }

    // ------------------------- ADMIN FUNCTIONS ------------------------------

    /**
     * @dev Will update the base URL of token's URI
     * @param _newBaseMetadataURI New base URL of token's URI
     */
    function setBaseMetadataURI(string memory _newBaseMetadataURI) public onlyOwner {
        baseTokenURI = _newBaseMetadataURI;
        emit BaseMetadataURIUpdated(_newBaseMetadataURI);
    }

    /**
     * @dev Set Address Registry contract address
     * @param _addressRegistry Address of Address Registry contract
     */
    function setAddressRegistry(AtlantisAddressRegistry _addressRegistry) external onlyOwner {
        require(address(_addressRegistry) != address(0), "AtlantisEquipments: Address Registry is address(0)");
        addressRegistry = _addressRegistry;
        emit AddressRegistryUpdated(address(_addressRegistry));
    }

    function random() private view returns (uint) {
        uint randomHash = uint(
            keccak256(
                abi.encodePacked(msg.sender, block.coinbase, block.difficulty, block.gaslimit, block.timestamp, nonce)
            )
        );
        return randomHash % 3;
    }

    function mint(address _to, uint256 _quantity) public payable {
        uint256 _id;
        if (paymentMode == PaymentMode.CRYPTO) {
            require(msg.value == _quantity * mintCost, "AtlantisEquipments: Insufficient CRO!");
        } else {
            // Transfer the required tokens from the user
            require(paymentToken.transferFrom(msg.sender, address(this), mintCost * _quantity), "Transfer failed");
        }

        // Loop through quantity
        for (uint256 i = 0; i < _quantity; i++) {
            _id = random() + 1;
            nonce += 1;
            _mint(_to, _id, 1, "");
        }
    }

    // Set mint cost
    function setMintCost(uint256 _newMintCost) external onlyOwner {
        // Mint cost cannot be above max mint cost
        require(_newMintCost <= MAX_MINT_COST, "AtlantisEquipments: Mint cost cannot be above max mint cost");
        mintCost = _newMintCost;
        emit MintCostUpdated(_newMintCost);
    }

    // Set payment mode
    function setPaymentMode(PaymentMode _paymentMode) public {
        paymentMode = _paymentMode;
        emit PaymentModeUpdated(_paymentMode);
    }

    // Set payment token
    function setPaymentToken(IERC20 _paymentToken) public onlyOwner {
        // Cannot be 0
        require(address(_paymentToken) != address(0), "AtlantisEquipments: Payment token cannot be address(0)");
        paymentToken = _paymentToken;
        emit PaymentTokenUpdated(address(_paymentToken));
    }

    /**
     * @dev Mint tokens for each id in _ids
     * @param _to          The address to mint tokens to
     * @param _ids         Array of ids to mint
     * @param _quantities  Array of amounts of tokens to mint per id
     * @param _data        Data to pass if receiver is contract
     */
    function batchMint(
        address _to,
        uint256[] memory _ids,
        uint256[] memory _quantities,
        bytes memory _data
    ) public onlyOwner {
        _mintBatch(_to, _ids, _quantities, _data);
    }

    /**
     * @notice Withdraw all CRO from this account to the owner
     */
    function withdrawFund() external onlyOwner {
        (bool success, ) = payable(treasury).call{ value: address(this).balance }("");
        require(success, "Transfer failed");
    }

    // ------------------------- INTERNAL FUNCTIONS ------------------------------

    /// @dev Gets baseToken URI
    function _baseURI() internal view returns (string memory) {
        return baseTokenURI;
    }

    /**
     * @dev See {ERC1155-_beforeTokenTransfer}.
     *
     * Requirements:
     *
     * - the contract must not be paused.
     */
    function _beforeTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) internal virtual override(ERC1155) {
        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);

        require(!paused(), "ERC1155Pausable: token transfer while paused");
        if (from == address(0)) {
            for (uint256 i = 0; i < ids.length; ++i) {
                _totalSupply[ids[i]] += amounts[i];
            }
        }

        if (to == address(0)) {
            for (uint256 i = 0; i < ids.length; ++i) {
                uint256 id = ids[i];
                uint256 amount = amounts[i];
                uint256 supply = _totalSupply[id];
                require(supply >= amount, "ERC1155: burn amount exceeds totalSupply");
                unchecked {
                    _totalSupply[id] = supply - amount;
                }
            }
        }
    }

    /**
     * @dev Indicates whether any token exist with a given id, or not.
     */
    function exists(uint256 _id) public view virtual returns (bool) {
        return _id > 0 && _id <= TOTAL_TOKEN_IDS;
    }

    /**
     * @dev Get gemstone id from equipment Id
     */
    function getGemstoneIdFromEquipmentId(uint256 _id) public pure returns (uint256) {
        if (_id > 27) {
            revert UpgradeError();
        }
        uint256 _element = (_id % 3);
        uint256 _gemstoneId;
        if (_element == 1) {
            _gemstoneId = 10;
        } else if (_element == 2) {
            _gemstoneId = 11;
        } else if (_element == 0) {
            _gemstoneId = 12;
        }
        return _gemstoneId;
    }

    /**
     * @notice Get equipment name from tokenId
     * @param _tokenId The id of the equipment
     */
    function _tokenIdToEquipmentNameString(uint256 _tokenId) internal pure returns (string memory) {
        uint256 _element = (_tokenId % 3);
        string memory _elementName;
        if (_element == 1) {
            _elementName = "Inferno Thrusters";
        } else if (_element == 2) {
            _elementName = "Thunderbolt Wings";
        } else if (_element == 0) {
            _elementName = "Argonium Exoshell";
        }
        return _elementName;
    }

    /**
     * @notice Fuse equipment
     * @param _id The id of the equipment
     * @param _amountToCreate Amount of next tier equipment to create
     */
    function fuseEquipment(uint256 _id, uint16 _amountToCreate) external nonReentrant {
        IAtlantisGemstones atlantisGemstones = IAtlantisGemstones(addressRegistry.getGemstones());
        IStakingWithLock stakingWithLock = IStakingWithLock(addressRegistry.getStakingWithLock());
        // Total fusion cost
        uint256 _fusionCost = calculateFusionCost(_id, _amountToCreate);

        // gemstone cost
        uint256 _gemstoneCost = calculateGemstonesCost(_id, _amountToCreate);
        // Calculate Equipment required to fuse
        uint256 _amountToBurn = fuseEquipmentsView(_id, _amountToCreate);
        uint256 _toId = _id + 3;
        _burn(msg.sender, _id, _amountToBurn);
        // Mint new Equipment
        _mint(msg.sender, _toId, _amountToCreate, "");
        // Burn Gemstone
        atlantisGemstones.burn(msg.sender, getGemstoneIdFromEquipmentId(_id), _gemstoneCost);
        // Burn stardust if _fusionCost is more than 0
        if (_fusionCost > 0) {
            stakingWithLock.unstakeAndBurn(_fusionCost);
        }
        // Emit fuse event
        emit FuseEquipment(msg.sender, _toId, _amountToCreate, totalSupply(_toId));
    }

    /**
     * @notice Get equipment speed from id
     * @param _id Specific equipment id
     * @return speed of equipment
     */
    function getSpeed(uint256 _id) public view returns (uint16) {
        if (_id > 30) {
            revert IdNotExistsError();
        } else if (_id == 0) {
            return 0;
        }
        uint8 _level = getLevel(_id);
        return equipmentSpeeds[_level - 1];
    }

    /**
     * @notice Get level of equipment from its id
     * @param _id Specific equipment id
     * @return level of equipment
     */
    function getLevel(uint256 _id) public pure returns (uint8) {
        if (_id > 30) {
            revert IdNotExistsError();
        } else if (_id == 0) {
            return 0;
        }
        // Logic above level 5
        uint8 _level;
        if ((_id % 3) == 0) {
            _level = uint8(_id) / 3;
        } else {
            _level = uint8(_id) / 3 + 1;
        }

        return _level;
    }

    /**
     * @notice Get element from id
     * @param _id Specific equipment id
     * @return element
     */
    function getElement(uint256 _id) public pure returns (uint8) {
        if (_id > 30 || _id == 0) {
            revert IdNotExistsError();
        }
        uint8 _element = uint8(_id) % 3;
        return _element;
    }

    /** Token Ids
     * 1 2 3 Fire Lightning Steel Equipment Level 1
     * 4 5 6 Fire Lightning Steel Equipment Level 2
     * 7 8 9 Fire Lightning Steel Equipment Level 3
     * 10 11 12 Fire Lightning Steel Equipment Level 4
     * 13 14 15 Fire Lightning Steel Equipment Level 5
     * 16 17 18 Fire Lightning Steel Equipment Level 6
     * 19 20 21 Fire Lightning Steel Equipment Level 7
     * 22 23 24 Fire Lightning Steel Equipment Level 8
     * 25 26 27 Fire Lightning Steel Equipment Level 9
     * 28 29 30 Fire Lightning Steel Equipment Level 10
     */

    /**
     * @notice Calculates cost of fusing equipment
     * @param _id Specific equipment id
     * @param _amountToCreate Amount of next tier equipment to create
     * @return Fusion cost
     */
    function calculateFusionCost(uint256 _id, uint16 _amountToCreate) public view returns (uint256) {
        // Logic for level 1 - 4
        if (_id < 13) {
            return 0;
        } else if (_id > 27) {
            revert UpgradeError();
        }
        // Logic above level 5
        uint8 _level = getLevel(_id);
        return uint256(_amountToCreate) * stardustCost[_level - 5];
    }

    /**
     * @notice Calculates gemstone cost of fusing equipmenet
     * @param _id Specific equipment id
     * @param _amountToCreate Amount of next tier equipment to create
     * @return Gemstone cost
     */
    function calculateGemstonesCost(uint256 _id, uint16 _amountToCreate) public view returns (uint16) {
        // Logic for level 1 - 4
        if (_id < 13) {
            return 0;
        }
        // Logic above level 5
        uint8 _level;
        if ((_id % 3) == 0) {
            _level = uint8(_id) / 3;
        } else {
            _level = uint8(_id) / 3 + 1;
        }
        return _amountToCreate * gemstonesRequired[_level - 5];
    }

    /**
     * @notice Calculates how much is require dto fuse to get amountToCreate
     * @param _id Specific equipment id
     * @param _amountToCreate Amount of next tier equipment to create
     * @return amount to fuse
     */
    function fuseEquipmentsView(uint256 _id, uint16 _amountToCreate) public view override returns (uint16) {
        if (!exists(_id) || _amountToCreate == 0) {
            revert UpgradeError();
        }
        // Get amount of gemstones to fuse
        uint16 amountToFuse = 2 * _amountToCreate;
        return amountToFuse;
    }

    /**
     * @notice Sets stardust costs
     * @param _stardustCost Array of stardust costs
     */
    function setStardustCosts(uint256[] calldata _stardustCost) external onlyOwner {
        // Loop through and set stardust
        for (uint256 i = 0; i < _stardustCost.length; i++) {
            // Check if the provided value is within the allowed range
            require(_stardustCost[i] <= MAX_STARDUST_COST, "Value exceeds the maximum allowed");
            stardustCost[i] = _stardustCost[i];
        }
        emit StardustCostsUpdated(_stardustCost);
    }

    /**
     * @notice Sets gemstones required
     * @param _gemstonesRequired Array of gemstonesRequired
     */
    function setGemstonesRequired(uint16[] memory _gemstonesRequired) external onlyOwner {
        // Loop through and set gemstonesRequired
        for (uint8 i = 0; i < _gemstonesRequired.length; i++) {
            // Check if the provided value is within the allowed range
            require(_gemstonesRequired[i] <= MAX_GEMSTONES_REQUIRED, "Value exceeds the maximum allowed");
            gemstonesRequired[i] = _gemstonesRequired[i];
        }
        emit GemstonesRequiredUpdated(_gemstonesRequired);
    }

    /**
     * @notice Sets speeds of equipments
     * @param _equipmentSpeeds Array of equipment speeds
     */
    function setEquipmentSpeeds(uint16[] memory _equipmentSpeeds) external onlyOwner {
        // Loop through and set equipmentSpeeds
        for (uint8 i = 0; i < _equipmentSpeeds.length; i++) {
            equipmentSpeeds[i] = _equipmentSpeeds[i];
        }
        emit EquipmentSpeedsUpdated(_equipmentSpeeds);
    }

    /**
     * @dev Airdrop some equipments to an address
     * @param _to          Address of the future owner of the token
     * @param _id          Token ID to mint
     * @param _quantity    Amount of tokens to mint
     * @param _data        Data to pass if receiver is contract
     */
    function airdrop(address _to, uint256 _id, uint256 _quantity, bytes memory _data) external {
        // Only whitelisted addresses can call this function
        require(devAddresses[msg.sender], "Only whitelisted addresses can call this function");
        _mint(_to, _id, _quantity, _data);
    }

    // Whitelist dev addresses
    function whitelistDevAddresses(address[] memory _devAddresses) external onlyOwner {
        for (uint256 i = 0; i < _devAddresses.length; i++) {
            devAddresses[_devAddresses[i]] = true;
        }
    }

    /**
     * @dev Pause contract in case of emergency
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause contract in case of emergency
     */
    function unpause() external onlyOwner {
        _unpause();
    }
}

contracts/AtlantisFaucet.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.11;
import "@openzeppelin/contracts/access/Ownable.sol";
import "./AtlantisAddressRegistry.sol";

// Official incentivised testnet version
contract AtlantisFaucet is Ownable {
    // Whitelist Mapping
    mapping(address => bool) public whitelisted;
    mapping(address => bool) public dripped;
    AtlantisAddressRegistry public addressRegistry;

    // // onlyWhitelisted modifier
    // modifier onlyWhitelisted() {
    //     require(whitelisted[msg.sender], "Faucet: Not whitelisted");
    //     _;
    // }

    // receive
    receive() external payable {}

    constructor(AtlantisAddressRegistry _addressRegistry) {
        addressRegistry = _addressRegistry;
        whitelisted[msg.sender] = true;
    }

    function drip() external {
        //  require(!dripped[msg.sender], "Faucet: Already dripped");
        address argonauts = addressRegistry.getArgonauts();
        address equipment = addressRegistry.getEquipments();
        address gemstones = addressRegistry.getGemstones();
        address spaceships = addressRegistry.getSpaceships();
        address planets = addressRegistry.getAtlantisPlanets();
        address xargo = addressRegistry.getXargo();
        address gold = addressRegistry.getGold();

        // Transfer 500 cro to user
        //payable(msg.sender).transfer(500 ether);

        // Transfer 750000 xArgo to user
        (bool success, ) = xargo.call(abi.encodeWithSignature("devMint(uint256)", 750000 ether));
        require(success, "Faucet: xargo mint failed");

        // Transfer 1000000 gold to user
        (success, ) = gold.call(abi.encodeWithSignature("mint(uint256,address)", 1000000 ether, msg.sender));
        require(success, "Faucet: gold mint failed");

        // devMint Gemstones
        for (uint8 i = 1; i <= 3; i++) {
            (success, ) = gemstones.call(
                abi.encodeWithSignature("airdrop(address,uint256,uint256,bytes)", msg.sender, i, 100000, "0x")
            );
            require(success, "Faucet: gemstone mint failed");
            (success, ) = equipment.call(
                abi.encodeWithSignature("airdrop(address,uint256,uint256,bytes)", msg.sender, i, 1000, "0x")
            );
            require(success, "Faucet: equipment mint failed");
        }
        // devMint Argonauts using external call
        (success, ) = argonauts.call(abi.encodeWithSignature("mint(uint256)", 30));
        require(success, "Faucet: argonaut mint failed");

        // devMint Spaceships
        uint8[] memory spaceshipRarity = new uint8[](4);
        spaceshipRarity[0] = 0;
        spaceshipRarity[1] = 1;
        spaceshipRarity[2] = 2;
        spaceshipRarity[3] = 3;
        address[] memory addresses = new address[](4);
        addresses[0] = msg.sender;
        addresses[1] = msg.sender;
        addresses[2] = msg.sender;
        addresses[3] = msg.sender;
        (success, ) = spaceships.call(
            abi.encodeWithSignature("airdrop(address[],uint8[])", addresses, spaceshipRarity)
        );
        require(success, "Faucet: spaceship mint failed");
        // devMint Planets
        (success, ) = planets.call(abi.encodeWithSignature("faucetMint(address)", msg.sender));
        require(success, "Faucet: planet mint failed");

        // dripped[msg.sender] = true;
    }

    function whitelistAddresses(address[] calldata _addresses) external onlyOwner {
        for (uint256 i; i < _addresses.length; i++) {
            whitelisted[_addresses[i]] = true;
        }
    }
}

contracts/AtlantisGemstones.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "./interface/IStakingWithLock.sol";
import { IAtlantisGemstones } from "./interface/IAtlantisGemstones.sol";
import "./AtlantisAddressRegistry.sol";
import "./common/Base64.sol";

/// @title Atlantis - Gemstones contract
/// @dev Fuse 3 gemstones of the same tier to receive 1 from the next tier. Gemstones can be used to upgrade planets and are also required for certain levels of equipment fusion.
contract AtlantisGemstones is ERC1155, Pausable, Ownable, IAtlantisGemstones, ReentrancyGuard {
    using Strings for uint256;
    /// @notice Address registry contract
    AtlantisAddressRegistry public addressRegistry;
    /// @notice Name of contract
    string public name;
    /// @notice Symbol of contract
    string public symbol;
    /// @notice Base token URI
    string public baseTokenURI;
    /// @notice Total amount of Ids
    uint256 public constant TOTAL_TOKEN_IDS = 12;
    /// @notice Total supply of each tokenId
    mapping(uint256 => uint256) private _totalSupply;
    /// @notice Stardust fusion cost
    uint256 public FUSION_COST = 200 ether;
    /// @notice Whitelisted addresses who can call airdrop
    mapping(address => bool) public devAddresses;
    // ------------------------- EVENTS --------------------------
    event MintGemstone(address indexed _to, uint256 indexed _id, uint256 _amount, uint256 _totalSupply);
    event FuseGemstone(address indexed _from, uint256 indexed _id, uint256 _amount, uint256 _totalSupply);
    error IdNotExistsError();

    // ----------------------- MODIFIERS -------------------------

    constructor(
        string memory _name,
        string memory _symbol,
        string memory _uri,
        address owner,
        address _registry
    ) ERC1155(_uri) {
        name = _name;
        symbol = _symbol;
        addressRegistry = AtlantisAddressRegistry(_registry);

        setBaseMetadataURI(_uri);
        transferOwnership(owner);
        devAddresses[owner] = true;
    }

    // ------------------------- PUBLIC FUNCTIONS ------------------------------
    /**
     * @dev Fuse 3 Gemstones to get another Gemstone of a higher tier
     * @param _id The Gemstone id
     * @param _amountToCreate The amount of Gemstones to create
     * Gemstone Upgrade Sheet
     *           Tier 1 -> Tier 2 -> Tier 3 -> Tier 4
     * FIRE      ID 1   -> ID 4   -> ID 7   -> ID 10
     * LIGHTNING ID 2   -> ID 5   -> ID 8   -> ID 11
     * STEEL     ID 3   -> ID 6   -> ID 9   -> ID 12
     */
    function fuseGemstones(uint8 _id, uint8 _toId, uint256 _amountToCreate) external nonReentrant whenNotPaused {
        IStakingWithLock stakingWithLock = IStakingWithLock(addressRegistry.getStakingWithLock());
        // Calculate Gemstone required to fuse
        uint256 _amountToBurn = calculateGemstoneCost(_id, _toId, _amountToCreate);
        // Burn Gemstones
        _burn(msg.sender, _id, _amountToBurn);
        // Mint new Gemstone
        _mint(msg.sender, _toId, _amountToCreate, "");
        // Total fusion cost
        uint256 fusionCost = calculateFusionCost(_id, _toId, _amountToCreate);
        // Burn stardust
        stakingWithLock.unstakeAndBurn(fusionCost);
        // Emit fuse event
        emit FuseGemstone(msg.sender, _toId, _amountToCreate, totalSupply(_toId));
    }

    // View function to calculate fusion cost
    function calculateFusionCost(uint8 _id, uint8 _toId, uint256 _amountToCreate) public view returns (uint256) {
        // Calculate Gemstone required to fuse
        uint256 _amountToBurn = calculateGemstoneCost(_id, _toId, _amountToCreate);
        return (_amountToBurn * FUSION_COST) / 3;
    }

    // View function to see how many Gemstones required to fuse to amountToCreate
    function calculateGemstoneCost(uint8 _id, uint8 _toId, uint256 _amountToCreate) public view returns (uint256) {
        if (_amountToCreate == 0) revert InvalidInputAmount();
        if (!exists(_id) || !exists(_toId)) revert NonExistentToken();
        if ((_toId % 3 != _id % 3)) revert InvalidElement();

        if (_id >= _toId) revert InvalidUpgrade();

        // Require toId to be more than id
        // Require toId to be in multiples of 3 more than id
        // Calculate how many Gemstones to fuse to get toId
        // Get tier difference
        uint8 tierDifference = (_toId - _id) / 3;
        // Get amount of Gemstones to fuse
        uint256 amountToFuse = 3 ** tierDifference * _amountToCreate;
        return amountToFuse;
    }

    // Setter for fusion cost
    function setFusionCost(uint256 _cost) external onlyOwner {
        FUSION_COST = _cost;
        emit FusionCostUpdated(_cost);
    }

    // Get element from id
    function getElement(uint256 _id) public pure returns (uint8) {
        if (_id > 12 || _id == 0) {
            revert IdNotExistsError();
        }
        uint8 _element = uint8(_id) % 3;
        return _element;
    }

    function _tokenIdtoElementString(uint256 _id) internal pure returns (string memory) {
        if (_id > 12) {
            revert IdNotExistsError();
        }
        uint8 _element = uint8(_id) % 3;
        if (_element == 0) {
            return "Steel";
        } else if (_element == 1) {
            return "Fire";
        } else {
            return "Lightning";
        }
    }

    // Get tier from id
    function getTier(uint256 _id) public pure returns (uint8) {
        if (_id > 12) {
            revert IdNotExistsError();
        }
        uint8 _tier = uint8(_id) / 3;
        if (uint8(_id) % 3 == 0) {
            return _tier;
        } else {
            return _tier + 1;
        }
    }

    /**
     * @dev Mints some amount of tokens to an address
     * @param _to          Address of the future owner of the token
     * @param _id          Token ID to mint
     * @param _quantity    Amount of tokens to mint
     */
    function mint(address _to, uint256 _id, uint256 _quantity) external {
        if (!(addressRegistry.isControllerContract(msg.sender) || msg.sender == owner())) revert OnlyAtlantisOrOwner();
        if (!exists(_id)) revert NonExistentToken();
        _mint(_to, _id, _quantity, "");
        // emit Mint event
        emit MintGemstone(_to, _id, _quantity, totalSupply(_id));
    }

    /**
     * @dev Burns some amount of tokens from an address
     */
    function burn(address _user, uint256 _id, uint256 _quantity) external {
        if (!(addressRegistry.isControllerContract(msg.sender) || msg.sender == owner())) revert OnlyAtlantisOrOwner();
        if (!exists(_id)) revert NonExistentToken();
        _burn(_user, _id, _quantity);
    }

    // ------------------------- PUBLIC VIEW FUNCTIONS ------------------------------
    // Get Image Uri
    /**
     * @notice Get ImageURI
     * @param _tokenId The Gemstone id
     */
    function _getImageURI(uint256 _tokenId) internal view returns (string memory) {
        return
            string(
                abi.encodePacked(
                    _baseURI(),
                    uint256(getElement(_tokenId)).toString(),
                    "/",
                    uint256(getTier(_tokenId)).toString(),
                    ".png"
                )
            );
    }

    function uri(uint256 _tokenId) public view override returns (string memory) {
        if (!exists(_tokenId)) revert NonExistentToken();
        string memory element = _tokenIdtoElementString(_tokenId);
        string memory json = string(
            abi.encodePacked('{"name": "', element, " Gemstone Tier ", uint256(getTier(_tokenId)).toString(), '",')
        );
        // Description
        json = string(
            abi.encodePacked(
                json,
                '"description": "Welcome to the captivating realm of Atlantis, the game-verse and home of the legendary Argonauts. Gemstones are powerful resources in Atlantis and exist in four different tiers. Acquire Gemstones to level up your planets and equipment parts. Fuse 3 Gemstones of the same tier to get a higher tier Gemstone!",'
            )
        );
        // Attributes
        json = string(
            abi.encodePacked(
                json,
                '"attributes": [{"trait_type": "Element", "value": "',
                element,
                '"},',
                '{"trait_type": "Tier", "value": "',
                uint256(getTier(_tokenId)).toString(),
                '"}],'
            )
        );
        json = Base64.encode(bytes(string(abi.encodePacked(json, '"image": "', _getImageURI(_tokenId), '"}'))));
        return string(abi.encodePacked("data:application/json;base64,", json));
    }

    // ------------------------- ADMIN FUNCTIONS ------------------------------

    /**
     * @dev Will update the base URL of token's URI
     * @param _newBaseMetadataURI New base URL of token's URI
     */
    function setBaseMetadataURI(string memory _newBaseMetadataURI) public onlyOwner {
        baseTokenURI = _newBaseMetadataURI;
        emit BaseMetadataURIUpdated(_newBaseMetadataURI);
    }

    /**
     * @notice Set the Address Registry
     * @param _addressRegistry The address of the Address Registry
     */
    function setAddressRegistry(AtlantisAddressRegistry _addressRegistry) external onlyOwner {
        // Require that the address is not null
        require(address(_addressRegistry) != address(0), "Address cannot be null");
        addressRegistry = _addressRegistry;
        emit AddressRegistryUpdated(address(_addressRegistry));
    }

    // ------------------------- INTERNAL FUNCTIONS ------------------------------

    /**
     * @dev Gets baseToken URI
     */
    function _baseURI() internal view returns (string memory) {
        return baseTokenURI;
    }

    /**
     * @dev Gets baseToken URI
     */
    function baseURI() external view returns (string memory) {
        return baseTokenURI;
    }

    /**
     * @dev Total amount of tokens in with a given id.
     */
    function totalSupply(uint256 id) public view virtual returns (uint256) {
        return _totalSupply[id];
    }

    /**
     * @dev Indicates whether any token exist with a given id, or not.
     * @dev There are only Gemstones with tokenIds 1-12
     */
    function exists(uint256 _id) public view virtual returns (bool) {
        return _id > 0 && _id <= TOTAL_TOKEN_IDS;
    }

    /**
     * @dev See {ERC1155-_beforeTokenTransfer}.
     *
     * Requirements:
     *
     * - the contract must not be paused.
     */
    function _beforeTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) internal virtual override(ERC1155) {
        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);

        require(!paused(), "ERC1155Pausable: token transfer while paused");

        if (from == address(0)) {
            for (uint256 i = 0; i < ids.length; ++i) {
                _totalSupply[ids[i]] += amounts[i];
            }
        }

        if (to == address(0)) {
            for (uint256 i = 0; i < ids.length; ++i) {
                uint256 id = ids[i];
                uint256 amount = amounts[i];
                uint256 supply = _totalSupply[id];
                require(supply >= amount, "ERC1155: burn amount exceeds totalSupply");
                unchecked {
                    _totalSupply[id] = supply - amount;
                }
            }
        }
    }

    /**
     * @dev Airdrops some gemstones to an address; for giveaways
     * @param _to          Address of the future owner of the token
     * @param _id          Token ID to mint
     * @param _quantity    Amount of tokens to mint
     * @param _data        Data to pass if receiver is contract
     */
    function airdrop(address _to, uint256 _id, uint256 _quantity, bytes memory _data) external {
        // Only whitelisted addresses can call this function
        require(devAddresses[msg.sender], "Only whitelisted addresses can call this function");
        // Only allow ids from 1-12
        if (!exists(_id)) revert NonExistentToken();
        _mint(_to, _id, _quantity, _data);
    }

    // Whitelist dev addresses
    function whitelistDevAddresses(address[] memory _devAddresses) external onlyOwner {
        for (uint256 i = 0; i < _devAddresses.length; i++) {
            devAddresses[_devAddresses[i]] = true;
        }
    }

    /**
     * @dev Pause contract in case of emergency
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause contract in case of emergency
     */
    function unpause() external onlyOwner {
        _unpause();
    }
}

contracts/AtlantisPlanets.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.11;
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./interface/IAtlantisPlanets.sol";
import "./interface/IAtlantisGemstones.sol";
import "./interface/IStakingWithLock.sol";
import "./common/WithLimitedSupply.sol";
import "./common/RandomlyAssigned.sol";
import "./common/Base64.sol";
import "./AtlantisAddressRegistry.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

/// @title Atlantis Planets Mint Contract
/// @dev Max Supply of 6000 planets
/// @dev First 12 planets are EPIC planets
// Stages
// 0: Before all minting commence
// 1: WhiteList Sale
// 2: Public sale
// 3: Post-Mint (Admin Phase)
// 4: Game Phase

contract AtlantisPlanets is ERC721, Ownable, IAtlantisPlanets, RandomlyAssigned {
    using Strings for uint256;
    using ECDSA for bytes32;

    AtlantisAddressRegistry public addressRegistry;

    string public baseURI;

    uint8 public stage;
    uint256 currentSupply;
    uint256 public whitelistPhase;

    // VIP Whitelist Mint Settings 750 cro
    uint256 public vipMintMaxPerWallet = 10; // VIP Sale Address Mint Cap
    uint256 public vipMintPrice = 0 ether; // VIP Sale Mint Price
    mapping(address => uint256) public vipMintCount;

    // Whitelist Mint Settings 850 cro
    uint256 public whitelistMintMaxPerWallet = 20; // Private Sale Address Mint Cap
    uint256 public whitelistMintPrice = 1 ether; // Private Sale Mint Price
    mapping(address => uint256) public whitelistMintCount;
    address private whitelistSignerAddress;

    // Public Sale Mint Settings 950 cro
    uint256 public publicMintPrice = 0 ether;
    uint256 public publicMintMaxPerWallet = type(uint256).max; // Unlimited mint
    mapping(address => uint256) public publicMintCount;

    // Treasury
    address public treasury;

    // Levelling
    bytes public levelUpGemstone;
    // xARGO base cost
    uint256 public xArgoBaseCost = 40 ether;
    // stardust base cost
    uint256 public stardustBaseCost = 200 ether;
    // stardust base cost scaling
    uint256 public stardustBaseCostScaling = 25;
    // stardust rarity cost scaling
    uint256 public stardustRarityCostScaling = 20;
    // Mapping of planet token id to planet struct
    mapping(uint256 => AtlantisLib.Planet) public planets;

    // Variable to track Gemstone tiers
    uint16 public constant gemstoneTiers = 4;
    // Variable to track max planet level
    uint16 public constant maxPlanetLevel = 50;

    bool public revealed = false;
    string public unrevealedImageURI = "ipfs://bafybeicabmv4ccbblnnpfq6q5rg5sr2qqq4mc7y7y3tqyfztiwzrkff5vi";

    // gemstone token ids is in the following sequence:
    // 1 - Fire 1
    // 2 - Lightning 1
    // 3 - Steel 1
    // 4 - Fire 2
    // 5 - Lightning 2
    // 6 - Steel 2
    // 7 - Fire 3
    // 8 - Lightning 3
    // 9 - Steel 3
    // 10 - Fire 4
    // 11 - Lightning 4
    // 12 - Steel 4

    // Events
    event PlanetUpgraded(uint256 indexed tokenId, uint256 indexed level);
    event PrivateMint(address indexed to, uint256 amount);
    event PublicMint(address indexed to, uint256 amount);

    // -------------------- MODIFIERS ----------------------

    /**
     * @dev Prevent Smart Contracts from calling the functions with this modifier
     */
    modifier onlyEOA() {
        require(msg.sender == tx.origin, "Planets: must use EOA");
        _;
    }

    constructor(
        address _owner,
        address _whitelistSignerAddress,
        string memory __baseURI,
        AtlantisAddressRegistry _addressRegistry
    ) ERC721("Atlantis Planets", "PLANETS") RandomlyAssigned(6000, 13) {
        setTreasury(_owner);
        setWhitelistSignerAddress(_whitelistSignerAddress);
        setBaseURI(__baseURI);
        transferOwnership(_owner);
        currentSupply = 0;
        addressRegistry = _addressRegistry;
    }

    /**
     * @dev Set Revealed Metadata URI
     */
    function setBaseURI(string memory _newBaseURI) public onlyOwner {
        baseURI = _newBaseURI;
    }

    /**
     * @dev Set Unrevealed Metadata URI
     */
    function setUnrevealedImageURI(string memory _newUnrevealedImageURI) public onlyOwner {
        unrevealedImageURI = _newUnrevealedImageURI;
    }

    // -------------------- ATLANTIS PUBLIC FUNCTIONS ----------------------

    /**
     * @dev Get gemstone upgrade requirements for upgrading a planet
     * @param _tokenId The token id of the planet
     * @param _levels The number of levels to upgrade
     * @return gemstoneRequirements The gemstone requirements for upgrading the planet
     */
    function getUpgradeRequirements(
        uint256 _tokenId,
        uint8 _levels
    ) internal view returns (uint16[4] memory gemstoneRequirements) {
        // Get planet struct
        AtlantisLib.Planet memory planet = planets[_tokenId];
        uint8 planetLevel = planet.level;
        uint8 newLevel = planetLevel + _levels;
        uint8 orbit = uint8(planet.orbit);

        if (planetLevel + _levels > maxPlanetLevel) revert ExceededMaxLevel();

        // Cumulative requirement at target level - Cumulative requirement at current level = requirement for upgrade
        gemstoneRequirements[0] =
            toUint16(levelUpGemstone, orbit, newLevel, 0) -
            toUint16(levelUpGemstone, orbit, planetLevel, 0);

        gemstoneRequirements[1] =
            toUint16(levelUpGemstone, orbit, newLevel, 1) -
            toUint16(levelUpGemstone, orbit, planetLevel, 1);

        gemstoneRequirements[2] =
            toUint16(levelUpGemstone, orbit, newLevel, 2) -
            toUint16(levelUpGemstone, orbit, planetLevel, 2);

        gemstoneRequirements[3] =
            toUint16(levelUpGemstone, orbit, newLevel, 3) -
            toUint16(levelUpGemstone, orbit, planetLevel, 3);
    }

    /**
     * @notice Get uint16 value from a byte array
     * @param _bytes The byte array
     * @param orbit Orbit of the planet
     * @param level Level of the planet
     * @param tier Tier of the gemstone
     * @dev This function was modified to serve retriving the gemstone upgrade requirements
     */
    function toUint16(
        bytes memory _bytes,
        uint256 orbit,
        uint256 level,
        uint256 tier
    ) internal pure returns (uint16 tempUint) {
        uint256 _start = orbit * 8 + (level - 1) * 32 + tier * 2;
        require(_bytes.length >= _start + 2, "toUint16_outOfBounds");

        assembly {
            tempUint := mload(add(add(_bytes, 0x2), _start))
        }
    }

    /**
     * @notice Get evolution stage of planet
     * @param level Level of a planet
     */
    function _getPlanetEvolution(
        uint16 level
    ) internal pure returns (AtlantisLib.Evolution evo, string memory evoString) {
        if (level < 20) {
            evo = AtlantisLib.Evolution.ALPHA;
            evoString = "Alpha";
        } else if (level >= 20 && level < 30) {
            evo = AtlantisLib.Evolution.BETA;
            evoString = "Beta";
        } else if (level >= 30 && level < 40) {
            evo = AtlantisLib.Evolution.GAMMA;
            evoString = "Gamma";
        } else if (level >= 40 && level < 50) {
            evo = AtlantisLib.Evolution.DELTA;
            evoString = "Delta";
        } else if (level == 50) {
            evo = AtlantisLib.Evolution.EPSILON;
            evoString = "Epsilon";
        }
    }

    /**
     * @notice Get planet details
     * @dev Planet details include: level, element, orbit, onExpedition
     */
    function getPlanetDetails(uint256 _planetId) external view returns (AtlantisLib.Planet memory) {
        AtlantisLib.Planet memory planet = planets[_planetId];
        return planet;
    }

    /**
     * @notice Return xArgo cost for upgrading a planet
     */
    function getxArgoCost(uint8 currentLevel, uint8 newLevel) internal view returns (uint256 xArgoCost) {
        if (currentLevel == 0 || currentLevel >= newLevel) revert InvalidUpgradeLevel(currentLevel, newLevel);

        // Loop through all levels and add up xArgo cost
        for (uint256 i = currentLevel; i < newLevel; i++) {
            xArgoCost += xArgoBaseCost * i;
        }
    }

    /**
     * @notice Return stardust cost for upgrading a planet
     * @param currentLevel Current Level of Planet
     * @param newLevel New planet level
     * @param orbit Orbit of planet
     */
    function getStardustCost(
        uint8 currentLevel,
        uint8 newLevel,
        uint8 orbit
    ) internal view returns (uint256 stardustCost) {
        if (currentLevel == 0 || currentLevel >= newLevel) revert InvalidUpgradeLevel(currentLevel, newLevel);

        // Loop through all levels and add up stardust cost
        for (uint256 i = currentLevel; i < newLevel; i++) {
            stardustCost +=
                (stardustBaseCost *
                    (((i - 1) * stardustBaseCostScaling + 100) * ((orbit) * stardustRarityCostScaling + 100))) /
                10000;
        }
    }

    /**
     * External function for frontend to retrieve stardust, xArgo, and gemstone costs in 1 multicall
     */
    function getUpgradeCosts(
        uint256 _tokenId,
        uint8 _levels
    ) external view returns (uint256 stardustCost, uint256 xArgoCost, uint16[4] memory gemstoneRequirements) {
        // Get planet struct
        AtlantisLib.Planet memory planet = planets[_tokenId];
        uint8 planetLevel = planet.level;
        uint8 newLevel = planetLevel + _levels;
        uint8 orbit = uint8(planet.orbit);

        require(planetLevel + _levels <= maxPlanetLevel, "Planets: Planet cannot be upgraded to this level!");

        // Get stardust cost
        stardustCost = getStardustCost(planetLevel, newLevel, orbit);

        // Get xArgo cost
        xArgoCost = getxArgoCost(planetLevel, newLevel);

        // Get gemstone requirements
        gemstoneRequirements = getUpgradeRequirements(_tokenId, _levels);
    }

    /**
     * @notice Returns total supply of AtlantisPlanets
     */
    function totalSupply() public view returns (uint256) {
        return currentSupply;
    }

    /**
     * @dev Upgrade a planet by `_levels` levels
     * @param _tokenId Token ID of planet
     * @param _levels Number of levels to upgrade planet by
     */
    function upgradePlanet(uint256 _tokenId, uint8 _levels) external {
        IERC20 xARGO = IERC20(addressRegistry.getXargo());
        IAtlantisGemstones atlantisGemstones = IAtlantisGemstones(addressRegistry.getGemstones());
        IStakingWithLock stakingWithLock = IStakingWithLock(addressRegistry.getStakingWithLock());

        // Check if upgrader is owner of planet
        if (ownerOf(_tokenId) != msg.sender) revert NotOwnerOfPlanet();
        // Check if stage 4
        if (stage != 4) revert InvalidStage(stage, 4);

        AtlantisLib.Planet storage planet = planets[_tokenId];

        uint8 orbit = uint8(planet.orbit);

        if (planet.level + _levels > maxPlanetLevel) revert ExceededMaxLevel();

        // Get xARGO requirements
        uint256 xArgoCost = getxArgoCost(planet.level, planet.level + _levels);
        // Get Stardust requirements
        // Base cost scaling for stardust 0.25 = 25, need math properly
        uint256 stardustCost = getStardustCost(planet.level, planet.level + _levels, orbit);

        // Get gemstone requirements
        uint16[4] memory gemstoneRequirements = getUpgradeRequirements(_tokenId, _levels);

        uint16 planetTypeTierStep = 0;
        uint16 step = 1;
        // Increase planet level
        planet.level += _levels;
        while (step <= gemstoneTiers) {
            uint256 toBurn = gemstoneRequirements[step - 1];
            // Burn gemstones
            if (toBurn > 0) {
                atlantisGemstones.burn(msg.sender, uint256(uint256(planet.element) + 1 + planetTypeTierStep), toBurn);
            }
            step++;
            planetTypeTierStep += 3;
        }
        // Transfer xARGO and Stardust to this contract
        xARGO.transferFrom(msg.sender, address(this), xArgoCost);
        if (stardustCost > 0) {
            stakingWithLock.unstakeAndBurn(stardustCost);
        }

        emit PlanetUpgraded(_tokenId, planet.level);
    }

    // -------------------- MINT FUNCTIONS --------------------------

    /**
     * @dev Mint planet (Whitelist only)
     * @param _mintAmount Amount of planets to mint
     * @param nonce Unique Nonce
     * @param signature Signature provided by the signerAddress
     */
    function whitelistMint(
        uint256 _mintAmount,
        bytes memory nonce,
        bytes memory signature
    ) external payable onlyEOA ensureAvailabilityFor(_mintAmount) {
        // Check if user is whitelisted
        if (!whitelistSigned(msg.sender, nonce, signature, whitelistPhase)) revert InvalidSignature();

        // Check if whitelist sale is open
        if (stage != 1) revert InvalidStage(stage, 1);
        if (whitelistPhase == 1) {
            // Check if enough ETH is sent
            if (msg.value != _mintAmount * vipMintPrice) revert InsufficientCRO(msg.value, _mintAmount * vipMintPrice);

            // Check if mints does not exceed max wallet allowance for public sale
            if (vipMintCount[msg.sender] + _mintAmount > vipMintMaxPerWallet) revert ExceedMaxMintPerWallet();

            vipMintCount[msg.sender] += _mintAmount;
        }
        if (whitelistPhase == 2) {
            // Check if enough ETH is sent
            if (msg.value != _mintAmount * whitelistMintPrice)
                revert InsufficientCRO(msg.value, _mintAmount * whitelistMintPrice);

            // Check if mints does not exceed max wallet allowance for public sale
            if (whitelistMintCount[msg.sender] + _mintAmount > whitelistMintMaxPerWallet)
                revert ExceedMaxMintPerWallet();

            whitelistMintCount[msg.sender] += _mintAmount;
        }
        currentSupply += _mintAmount;

        for (uint256 i; i < _mintAmount; i++) {
            _mintPlanet();
        }
        emit PrivateMint(msg.sender, _mintAmount);
    }

    /**
     * @notice Public Mint
     * @param _mintAmount Amount that is minted
     */
    function mint(uint256 _mintAmount) external payable onlyEOA ensureAvailabilityFor(_mintAmount) {
        // Check if public sale is open
        if (stage != 2) revert InvalidStage(stage, 2);
        publicMintCount[msg.sender] += _mintAmount;
        currentSupply += _mintAmount;
        // Check if enough ETH is sent
        if (msg.value != _mintAmount * publicMintPrice)
            revert InsufficientCRO(msg.value, _mintAmount * publicMintPrice);
        // Check if mints does not exceed total max supply

        for (uint256 i; i < _mintAmount; i++) {
            _mintPlanet();
        }
        emit PublicMint(msg.sender, _mintAmount);
    }

    /**
     * @notice Mint planet
     * @dev Set initial planet level to 1 and random mint to msg.sender
     */
    function _mintPlanet() internal {
        // Get next token Id
        uint256 _tokenId = nextToken();
        // Initialize planet
        planets[_tokenId].level = 1;
        // Mint planet
        _safeMint(msg.sender, _tokenId);
    }

    /**
     * @notice Set whitelist phase
     * @param _whitelistPhase Phase of whitelist
     */
    function setWhitelistPhase(uint256 _whitelistPhase) external onlyOwner {
        whitelistPhase = _whitelistPhase;
    }

    // -------------------- ATLANTIS ADMIN FUNCTIONS ----------------------
    /**
     * @dev Set planet backgrounds
     * @param _tokenIds Token ID of planets
     * @param _backgrounds Backgrounds of planets
     */
    function setPlanetBackgrounds(
        uint256[] calldata _tokenIds,
        AtlantisLib.Background[] calldata _backgrounds
    ) external onlyOwner {
        if (stage != 3) revert InvalidStage(stage, 3);
        // Loop through planet types and set planet type
        for (uint256 i; i < _backgrounds.length; i++) {
            planets[_tokenIds[i]].background = _backgrounds[i];
        }
    }

    /**
     * @dev Set planet orbit names
     * @param _tokenIds Token ID of planets
     * @param _planetOrbitNames Orbit Names of planets
     */

    function setPlanetOrbitNames(
        uint256[] calldata _tokenIds,
        AtlantisLib.OrbitName[] calldata _planetOrbitNames
    ) external onlyOwner {
        if (stage != 3) revert InvalidStage(stage, 3);
        // Loop through planet types and set planet type
        for (uint256 i; i < _planetOrbitNames.length; i++) {
            planets[_tokenIds[i]].orbitName = _planetOrbitNames[i];
        }
    }

    /**
     * @dev Set planet orbit
     * @param _tokenIds Token ID of planets
     * @param _planetOrbits Orbit of planets
     */
    function setPlanetOrbits(
        uint256[] calldata _tokenIds,
        AtlantisLib.Orbit[] calldata _planetOrbits
    ) external onlyOwner {
        if (stage != 3) revert InvalidStage(stage, 3);
        // Loop through planet types and set planet type
        for (uint256 i; i < _planetOrbits.length; i++) {
            planets[_tokenIds[i]].orbit = _planetOrbits[i];
        }
    }

    /**
     * @dev Set planet element
     * @param _tokenIds Token ID of planets
     * @param _gemstoneTypes Element of planets
     */
    function setPlanetElements(
        uint256[] calldata _tokenIds,
        AtlantisLib.Element[] calldata _gemstoneTypes
    ) external onlyOwner {
        // Require stage 3
        if (stage != 3) revert InvalidStage(stage, 3);
        // Set planet type
        for (uint256 i; i < _tokenIds.length; i++) {
            planets[_tokenIds[i]].element = _gemstoneTypes[i];
        }
    }

    /**
     * @notice Set level up gemstone costs
     * @param _data gemstone cost packed in bytes
     * @dev _data is packed as follows:
     *     Cumulative cost for each gemstone tier for each planet type
     *              | Common              | Uncommon            | Rare                | Epic
     *              | T1   T2   T3   T4   | T1   T2   T3   T4   | T1   T2   T3   T4   | T1   T2   T3   T4
     *     Level 1  | 0000 0000 0000 0000 | 0000 0000 0000 0000 | 0000 0000 0000 0000 | 0000 0000 0000 0000
     *     ...
     *     Level 50 | 0122 00af 00e1 0113 | 015c 00d2 010e 014a | 0196 00f5 013b 0181 | 01d0 0118 0168 01b8
     */
    function setLevelUpGemstone(bytes calldata _data) external onlyOwner {
        // Require stage 3
        if (stage != 3) revert InvalidStage(stage, 3);
        levelUpGemstone = _data;
    }

    /**
     * @dev Withdraw ERC20 Tokens From this contract
     * @param _tokenAddress Address of ERC20 token
     * @param _amount Amount of ERC20 token to withdraw
     */
    function withdrawERC20(IERC20 _tokenAddress, uint256 _amount) external onlyOwner {
        _tokenAddress.transfer(treasury, _amount);
    }

    // Setters for base costs
    /**
     * @dev Set xArgo And Stardust base costs
     * @param _xArgoBaseCost xArgo base cost
     * @param _stardustBaseCost Stardust base cost
     */
    function setBaseCosts(uint256 _xArgoBaseCost, uint256 _stardustBaseCost) external onlyOwner {
        xArgoBaseCost = _xArgoBaseCost;
        stardustBaseCost = _stardustBaseCost;
    }

    // Setters for scaling
    /**
     * @dev Set stardust scaling costs
     * @param _stardustBaseCostScaling sd base cost scaling
     * @param _stardustRarityCostScaling Stardust rarity cost scaling
     */
    function setScaling(uint256 _stardustBaseCostScaling, uint256 _stardustRarityCostScaling) external onlyOwner {
        stardustBaseCostScaling = _stardustBaseCostScaling;
        stardustRarityCostScaling = _stardustRarityCostScaling;
    }

    // -------------------- WHITELIST FUNCTION ----------------------

    /**
     * @dev Checks if the the signature is signed by a valid signer for whitelist
     * @param sender Address of minter
     * @param nonce Random bytes32 nonce
     * @param signature Signature generated off-chain
     */
    function whitelistSigned(
        address sender,
        bytes memory nonce,
        bytes memory signature,
        uint256 _whitelistPhase
    ) private view returns (bool) {
        bytes32 _hash = keccak256(abi.encodePacked(sender, nonce, _whitelistPhase));
        return whitelistSignerAddress == ECDSA.toEthSignedMessageHash(_hash).recover(signature);
    }

    // ------------------------- ADMIN FUNCTIONS ----------------------------

    /**
     * @dev Set stage of minting
     */
    function setStage(uint8 _newStage) public onlyOwner {
        stage = _newStage;
    }

    /**
     * @dev Toggle Reveal
     */
    function toggleReveal() public onlyOwner {
        revealed = !revealed;
    }

    /**
     * @dev Set signer address for whitelist mint
     */
    function setWhitelistSignerAddress(address signer) public onlyOwner {
        whitelistSignerAddress = signer;
    }

    /**
     * @dev Set vip mint max per wallet
     */
    function setVipMaxMintPerWallet(uint256 amount) public onlyOwner {
        vipMintMaxPerWallet = amount;
    }

    /**
     * @dev Set vip mint price
     */
    function setVipMintPrice(uint256 _vipMintPrice) public onlyOwner {
        vipMintPrice = _vipMintPrice;
    }

    /**
     * @dev Set whitelist mint max per wallet
     */
    function setWhitelistMaxMintPerWallet(uint256 amount) public onlyOwner {
        whitelistMintMaxPerWallet = amount;
    }

    /**
     * @dev Set public mint price
     */
    function setPublicMintPrice(uint256 _publicMintPrice) public onlyOwner {
        publicMintPrice = _publicMintPrice;
    }

    /**
     * @dev Set whitelist mint price
     */
    function setWhitelistMintPrice(uint256 _whitelistMintPrice) public onlyOwner {
        whitelistMintPrice = _whitelistMintPrice;
    }

    /**
     * @notice Withdraw all CRO from this account to the owner
     */
    function withdrawFund() external onlyOwner {
        (bool success, ) = payable(treasury).call{ value: address(this).balance }("");
        require(success, "Transfer failed");
    }

    /**
     * @notice Sets the treasury address
     */
    function setTreasury(address _treasury) public onlyOwner {
        treasury = _treasury;
    }

    /**
     * @dev Base URI for computing {tokenURI}. If set, the resulting URI for each
     * token will be the concatenation of the `baseURI` and the `tokenId`. Empty
     * by default, it can be overridden in child contracts.
     */
    function _baseURI() internal view virtual override returns (string memory) {
        return baseURI;
    }

    /**
     * @notice Returns if given tokenId exists in AtlantisPlanets
     */
    function exists(uint256 tokenId) external view returns (bool) {
        return _exists(tokenId);
    }

    // ------------------------- TOKEN METADATA ----------------------------

    /**
     * @notice Get ImageURI
     */
    function getImageURI(
        AtlantisLib.Background background,
        AtlantisLib.Element element,
        AtlantisLib.OrbitName orbitName,
        AtlantisLib.Evolution evo
    ) public view returns (string memory) {
        if (!revealed) {
            return unrevealedImageURI;
        }
        return
            string(
                abi.encodePacked(
                    _baseURI(),
                    Strings.toString(uint(background)),
                    "/",
                    Strings.toString(uint(element)),
                    "/",
                    Strings.toString(uint(evo)),
                    "/",
                    Strings.toString(uint(orbitName)),
                    ".png"
                )
            );
    }

    /**
     * @notice Returns token metadata
     * @dev Metadata is stored on-chain
     */
    function tokenURI(uint256 tokenId) public view override returns (string memory) {
        _requireMinted(tokenId);
        // Get Planet
        AtlantisLib.Planet memory planet = planets[tokenId];

        // Get Tier from Level
        (AtlantisLib.Evolution evo, string memory evoString) = _getPlanetEvolution(planet.level);

        // Name
        string memory json = string(abi.encodePacked('{"name": "', name(), " #", tokenId.toString(), '",'));

        // Description
        json = string(
            abi.encodePacked(
                json,
                '"description": "Welcome to the captivating realm of Atlantis, the game-verse and home of the legendary Argonauts. Planets are coveted lands that hold the key to your success in the game. Acquire planets, embark on exciting expeditions, and earn rewards that will supercharge your growth in Atlantis.",'
            )
        );

        // Attributes
        if (!revealed) {
            json = string(abi.encodePacked(json, '"attributes": [],'));
        } else {
            json = string(
                abi.encodePacked(
                    json,
                    '"attributes": [{"trait_type": "Element", "value": "',
                    AtlantisLib._planetElementToString(planet.element),
                    '"},',
                    '{"trait_type": "Background", "value": "',
                    AtlantisLib._planetBackgroundToString(planet.background),
                    '"},',
                    '{"trait_type": "Orbit Name", "value": "',
                    AtlantisLib._planetOrbitTypeToString(planet.orbitName),
                    '"},'
                )
            );
            json = string(
                abi.encodePacked(
                    json,
                    '{"trait_type": "Orbit", "value": "',
                    AtlantisLib._planetOrbitToString(planet.orbit),
                    '"},',
                    '{"trait_type": "Evolution", "value": "',
                    evoString,
                    '"},',
                    '{"trait_type": "Level", "value": "',
                    Strings.toString(planet.level),
                    '"}],'
                )
            );
        }

        json = Base64.encode(
            bytes(
                string(
                    abi.encodePacked(
                        json,
                        '"image": "',
                        getImageURI(planet.background, planet.element, planet.orbitName, evo),
                        '"}'
                    )
                )
            )
        );
        return string(abi.encodePacked("data:application/json;base64,", json));
    }

    /**
     * @notice Set the Address Registry
     * @param _addressRegistry The address of the Address Registry
     */
    function setAddressRegistry(AtlantisAddressRegistry _addressRegistry) external onlyOwner {
        addressRegistry = _addressRegistry;
    }

    function devMint(address _to, uint256 _mintAmount) public ensureAvailabilityFor(_mintAmount) onlyOwner {
        currentSupply += _mintAmount;
        for (uint256 i; i < _mintAmount; i++) {
            // Get next token Id
            uint256 _tokenId = nextToken();
            // Initialize planet
            planets[_tokenId].level = 1;
            // Mint planet
            _safeMint(tx.origin, _tokenId);
        }

        emit PublicMint(_to, _mintAmount);
    }

    function devEpicMint(address[12] calldata _auctionWinners) external onlyOwner {
        require(_auctionWinners.length == 12, "AtlantisPlanets: Invalid length");
        // Mint token Ids 1-12
        for (uint256 i = 1; i < 13; i++) {
            planets[i].level = 1;
            _safeMint(_auctionWinners[i - 1], i);
        }
    }
}

contracts/AtlantisRacing.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import "./interface/IAtlantisSpaceships.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "./common/SetUtils.sol";
import "./interface/IAtlantisRacing.sol";
import "./AtlantisAddressRegistry.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/// @title Atlantis - Racing contract
/// @dev Send your Spaceships to race for rewards. Add supported Nft collections to boost speed and earn a larger share of the rewards.
contract AtlantisRacing is IAtlantisRacing, Ownable, IERC721Receiver, ReentrancyGuard {
    using EnumerableSet for EnumerableSet.UintSet;
    using SetUtils for EnumerableSet.UintSet;
    using SafeERC20 for IERC20;

    /// @notice Address registry contract
    AtlantisAddressRegistry public addressRegistry;
    /// @notice Unix timestamp of season end time
    uint256 public seasonEndTime;
    /// @notice Total reward per second
    uint256 public totalRewardPerSecond;
    /// @notice Precision factor for calculations
    uint256 public immutable ACC_TOKEN_PRECISION;
    /// @notice Unix timestamp of season start time
    uint256 public seasonStartTime;
    /// @notice Max limit of Nfts staked in all pools
    uint256 public constant MAX_NFTS_STAKED = 15;
    /// @notice Number of pools
    uint256 internal constant amountOfPools = 4;
    /// @notice Whether the season has started
    bool public seasonStarted = false;
    /// @notice Information of each respective pool (Global, Common, Uncommon, Rare/Epic)
    mapping(uint256 => PoolInfo) public poolInfo;
    /// @notice Information of each user that stakes spaceships
    mapping(address => UserInfo) public userInfo;
    /// @notice Cached speed
    mapping(uint256 => uint256) public cachedSpeeds;
    /// @notice Multiplier when a user stakes Nfts
    uint256[6] public nftMultiplier;
    /// @notice Mapping of (user) => (tokenId) => (bool) if token is staked
    mapping(address => mapping(uint256 => bool)) public stakedSpaceships;
    /// @notice Mapping of (user) => (PoolId) => address => (EnumerableSet of NFT tokenIds) Number of NFTs staked in each pool
    mapping(address => mapping(uint8 => mapping(address => EnumerableSet.UintSet))) internal stakedNFTs;

    /// @notice Mapping of address to boolean whitelisted status
    mapping(address => bool) public whitelistedCollections;
    /// @notice All tokenIds currently staked
    mapping(address => mapping(uint8 => EnumerableSet.UintSet)) internal currentlyStakedSpaceshipIds;
    /// @notice All tokenIds currently staked
    mapping(address => EnumerableSet.UintSet) internal currentlyStakedIds;
    /// @notice All tokenIds previously staked
    mapping(address => EnumerableSet.UintSet) internal previouslyStakedIds;

    /**
     * @notice Constructor for Atlantis Racing
     * @param _registry Address registry contract
     * @param _totalRewardPerSecond Total stardust per second
     * @param _seasonEndTime Season end time
     */
    constructor(
        AtlantisAddressRegistry _registry,
        uint256 _totalRewardPerSecond,
        uint256 _seasonEndTime,
        uint256[] memory _nftMultipliers
    ) {
        require(_nftMultipliers[0] == 10, "Invalid nft multipliers");
        require(_seasonEndTime > block.timestamp, "Invalid season end time");
        for (uint256 i; i < _nftMultipliers.length; i++) {
            nftMultiplier[i] = _nftMultipliers[i];
        }
        // Set total stardust per second
        totalRewardPerSecond = _totalRewardPerSecond;

        addressRegistry = _registry;
        // Global race pool, 15% of total
        poolInfo[0].poolType = 0;
        poolInfo[0].rewardPerSecond = (_totalRewardPerSecond * 225) / 1000;
        poolInfo[0].stardustWeightage = 80;
        poolInfo[0].goldWeightage = 20;
        poolInfo[0].lastRewardTime = block.timestamp;
        poolInfo[0].accStardustPerPoint = 0;
        poolInfo[0].totalPoints = 0;
        poolInfo[0].totalCount = 0;

        // Common race pool, 50% of total
        poolInfo[1].poolType = 1;
        poolInfo[1].rewardPerSecond = (_totalRewardPerSecond * 45) / 100;
        poolInfo[1].stardustWeightage = 80;
        poolInfo[1].goldWeightage = 20;
        poolInfo[1].lastRewardTime = block.timestamp;
        poolInfo[1].accStardustPerPoint = 0;
        poolInfo[1].totalPoints = 0;
        poolInfo[1].totalCount = 0;

        // Uncommon race pool, 30% of total
        poolInfo[2].poolType = 2;
        poolInfo[2].rewardPerSecond = (_totalRewardPerSecond * 25) / 100;
        poolInfo[2].stardustWeightage = 80;
        poolInfo[2].goldWeightage = 20;
        poolInfo[2].lastRewardTime = block.timestamp;
        poolInfo[2].accStardustPerPoint = 0;
        poolInfo[2].totalPoints = 0;
        poolInfo[2].totalCount = 0;

        // Rare + Epic race pool, 5% of total
        poolInfo[3].poolType = 3;
        poolInfo[3].rewardPerSecond = (_totalRewardPerSecond * 75) / 1000;
        poolInfo[3].stardustWeightage = 80;
        poolInfo[3].goldWeightage = 20;
        poolInfo[3].lastRewardTime = block.timestamp;
        poolInfo[3].accStardustPerPoint = 0;
        poolInfo[3].totalPoints = 0;
        poolInfo[3].totalCount = 0;

        // Scratch generation cut off date
        seasonEndTime = _seasonEndTime;
        ACC_TOKEN_PRECISION = 10 ** 18;

        // Get argonauts from registry
        address argonauts = addressRegistry.getArgonauts();
        // Set whitelisted collections
        whitelistedCollections[argonauts] = true;
    }

    /**
        onlyOwner functions >:]
    */

    /**
     * @notice Setter for season end time
     * @param _time season end time in unix timestamp
     */
    function setSeasonEndTime(uint256 _time) public onlyOwner {
        require(_time > block.timestamp, "Season end time must be in the future");
        seasonEndTime = _time;
        _massUpdatePools();
        emit SeasonEndTimeChanged(_time);
    }

    /**
     * @notice Starts the season
     */
    function startSeason() external onlyOwner {
        require(!seasonStarted, "Season already started");
        require(block.timestamp < seasonEndTime, "Season already ended");
        uint256 currentTime = block.timestamp;
        seasonStartTime = currentTime;
        seasonStarted = true;
        // Update last reward time
        for (uint256 i; i < amountOfPools; i++) {
            poolInfo[i].lastRewardTime = currentTime;
        }
        emit StartSeason(currentTime);
    }

    /**
     * @notice Ends the season early
     */
    function endSeason() external onlyOwner {
        require(seasonStarted, "Season not started");
        require(block.timestamp < seasonEndTime, "Season already ended");
        uint256 currentTime = block.timestamp;
        seasonEndTime = currentTime;
        _massUpdatePools();
        emit EndSeason(currentTime);
    }

    /**
     * @notice Set the Address Registry
     * @param _addressRegistry The address of the Address Registry
     */
    function setAddressRegistry(AtlantisAddressRegistry _addressRegistry) external onlyOwner {
        addressRegistry = _addressRegistry;
        emit AddressRegistryUpdated(address(_addressRegistry));
    }

    /**
     * @notice Used to retrieve any stardust on the contract
     */
    function retrieveStardust() external onlyOwner {
        IERC20 stardust = IERC20(addressRegistry.getStardust());
        uint256 total = stardust.balanceOf(address(this));
        stardust.safeTransfer(msg.sender, total);
        emit EmergencyWithdrawStardust(total);
    }

    /**
     * @notice Used to retrieve any gold on the contract
     */
    function retrieveGold() external onlyOwner {
        IERC20 gold = IERC20(addressRegistry.getGold());
        uint256 total = gold.balanceOf(address(this));
        gold.safeTransfer(msg.sender, total);
        emit EmergencyWithdrawGold(total);
    }

    /**
     * @notice Stake spaceships to earn stardust + gold
     * @param _ids The ids of the spaceships to stake
     */
    function stakeSpaceships(uint256[] memory _ids) external nonReentrant {
        require(block.timestamp < seasonEndTime, "Season already ended");
        IAtlantisSpaceships spaceships = IAtlantisSpaceships(addressRegistry.getSpaceships());
        // Harvest any pending rewards
        if (seasonStarted) {
            _massUpdatePools();
            for (uint8 i; i < amountOfPools; i++) {
                _harvest(i, msg.sender);
            }
        }
        for (uint256 i; i < _ids.length; i++) {
            AtlantisLib.Rarity _type = spaceships.getRarity(_ids[i]);
            // Convert spaceship type to uint256
            uint8 _spaceshipRarity = uint8(_type) + 1;
            // If spaceship type is 4, it is an epic and in same pool as rare, so we set it to 3
            if (_spaceshipRarity == 4) {
                _spaceshipRarity = 3;
            }
            uint256 _speed = spaceships.getSpeed(_ids[i]) * 10;
            cachedSpeeds[_ids[i]] = _speed;
            require(_speed > 0, "Spaceship speed cannot be 0");
            currentlyStakedSpaceshipIds[msg.sender][_spaceshipRarity].add(_ids[i]);
            //Take Token and Transfer to Contract for Holding
            IERC721(address(spaceships)).safeTransferFrom(msg.sender, address(this), _ids[i]);
            // Increase to free pool and increase to protected pool
            if (userInfo[msg.sender].boostingNftsPoolCount[_spaceshipRarity] == 0) {
                _increasePoolAndUserStats(_speed, 0, msg.sender, false);
                _increasePoolAndUserStats(_speed, _spaceshipRarity, msg.sender, false);
            } else {
                uint256 _multiplier = nftMultiplier[userInfo[msg.sender].boostingNftsPoolCount[_spaceshipRarity]];
                _increasePoolAndUserStats((_speed * _multiplier) / 10, 0, msg.sender, false);
                _increasePoolAndUserStats((_speed * _multiplier) / 10, _spaceshipRarity, msg.sender, false);
            }
            stakedSpaceships[msg.sender][_ids[i]] = true;
            userInfo[msg.sender].spaceshipsStaked++;
            emit Stake(msg.sender, _ids[i], _speed);
        }
        _updateRewardDebts(msg.sender);
    }

    /**
     * @notice Unstake spaceships
     * @param _ids The ids of the spaceships to unstake
     */
    function unstakeSpaceships(uint256[] calldata _ids) external nonReentrant {
        IAtlantisSpaceships spaceships = IAtlantisSpaceships(addressRegistry.getSpaceships());

        require(userInfo[msg.sender].spaceshipsStaked >= _ids.length, "Not enough spaceships staked");
        // Harvest any pending rewards
        if (seasonStarted) {
            _massUpdatePools();
            for (uint8 i; i < amountOfPools; i++) {
                _harvest(i, msg.sender);
            }
        }

        for (uint256 i; i < _ids.length; i++) {
            // if id found in stakedSpaceships, unstake it
            uint256 _id = _ids[i];
            require(stakedSpaceships[msg.sender][_id], "Spaceship not staked");
            uint8 _spaceshipRarity = uint8(spaceships.getRarity(_id)) + 1;
            uint256 _speed = cachedSpeeds[_id];
            if (_spaceshipRarity == 4) {
                _spaceshipRarity = 3;
            }

            // decrease for free pool and protected pool
            if (userInfo[msg.sender].boostingNftsPoolCount[_spaceshipRarity] == 0) {
                _decreasePoolAndUserStats(_speed, 0, msg.sender, false);
                _decreasePoolAndUserStats(_speed, _spaceshipRarity, msg.sender, false);
            } else {
                uint256 _multiplier = nftMultiplier[userInfo[msg.sender].boostingNftsPoolCount[_spaceshipRarity]];
                _decreasePoolAndUserStats((_speed * _multiplier) / 10, 0, msg.sender, false);
                _decreasePoolAndUserStats((_speed * _multiplier) / 10, _spaceshipRarity, msg.sender, false);
            }

            stakedSpaceships[msg.sender][_id] = false;
            userInfo[msg.sender].spaceshipsStaked--;
            currentlyStakedSpaceshipIds[msg.sender][_spaceshipRarity].remove(_ids[i]);
            // transfer spaceship back to user
            IERC721(address(spaceships)).safeTransferFrom(address(this), msg.sender, _id);
            emit Unstaked(msg.sender, _id);
        }
        _updateRewardDebts(msg.sender);
    }

    /**
     * @notice Stakes given nfts and uses them to boost the given pool.
     * @param _pid  Id of the pool to boost
     * @param _collectionAddresses Array of collection addresses
     * @param _nfts Array of token ids to stake
     */
    function stakeNfts(
        uint8 _pid,
        address[] calldata _collectionAddresses,
        uint256[] calldata _nfts
    ) external nonReentrant {
        require(block.timestamp < seasonEndTime, "Season already ended");
        // Require pid to be 1-3 only
        require(_pid >= 1 && _pid <= 3, "Invalid pool id");
        require(_nfts.length == _collectionAddresses.length, "NFTs and collection addresses must be same length");
        if (seasonStarted) {
            _massUpdatePools();
            for (uint8 i; i < amountOfPools; i++) {
                _harvest(i, msg.sender);
            }
        }
        // Get userInfo
        UserInfo storage user = userInfo[msg.sender];

        require(user.boostingNftsCount + _nfts.length <= MAX_NFTS_STAKED, "Too many NFTs staked");
        for (uint256 i; i < _collectionAddresses.length; i++) {
            // Check whitelisted nfts
            require(whitelistedCollections[_collectionAddresses[i]], "Collection not whitelisted");
        }
        uint256 _initialNftsCount = user.boostingNftsPoolCount[_pid];
        for (uint256 i; i < _collectionAddresses.length; i++) {
            user.boostingNfts[_pid][_collectionAddresses[i]].push(_nfts[i]);

            // print currentNfts length
            user.boostingNftsPoolCount[_pid]++;
            user.boostingNftsCount++;
            _stake(_pid, _collectionAddresses[i], _nfts[i]);
        }
        uint256 _currentSpeed = user.totalSpeeds[_pid];
        uint256 _initialMultiplier = nftMultiplier[_initialNftsCount];
        uint256 _multiplier = nftMultiplier[userInfo[msg.sender].boostingNftsPoolCount[_pid]];
        _decreasePoolAndUserStats(_currentSpeed - (_currentSpeed * 10) / _initialMultiplier, 0, msg.sender, true);
        _decreasePoolAndUserStats(_currentSpeed - (_currentSpeed * 10) / _initialMultiplier, _pid, msg.sender, true);
        _currentSpeed = userInfo[msg.sender].totalSpeeds[_pid];
        _increasePoolAndUserStats((_currentSpeed * _multiplier) / 10 - _currentSpeed, 0, msg.sender, true);
        _increasePoolAndUserStats((_currentSpeed * _multiplier) / 10 - _currentSpeed, _pid, msg.sender, true);
        _updateRewardDebts(msg.sender);
        emit StakeNfts(_pid, msg.sender, _nfts.length);
    }

    /**
     * @notice Unstakes nfts without unboosting the pool
     * @param _pid  Id of the farm to unstake the nfts from
     * @param _collectionAddresses Array of nft contract addresses
     * @param _nfts Array of structs containing the nft contract and token id to unstake
     */
    function unstakeNfts(
        uint8 _pid,
        address[] calldata _collectionAddresses,
        uint256[] calldata _nfts
    ) external nonReentrant {
        require(_nfts.length == _collectionAddresses.length, "NFTs and collection addresses must be same length");
        require(_pid >= 1 && _pid <= 3, "Invalid pool id");
        if (seasonStarted) {
            _massUpdatePools();
            for (uint8 i; i < amountOfPools; i++) {
                _harvest(i, msg.sender);
            }
        }
        uint256 _initialNftsCount = userInfo[msg.sender].boostingNftsPoolCount[_pid];
        for (uint256 i; i < _collectionAddresses.length; i++) {
            _removeNftFromPool(_pid, _collectionAddresses[i], _nfts[i]);
            _unstake(_pid, _collectionAddresses[i], _nfts[i]);
        }
        uint256 _currentSpeed = userInfo[msg.sender].totalSpeeds[_pid];
        uint256 _initialMultiplier = nftMultiplier[_initialNftsCount];
        uint256 _multiplier = nftMultiplier[userInfo[msg.sender].boostingNftsPoolCount[_pid]];
        _decreasePoolAndUserStats(_currentSpeed - (_currentSpeed * 10) / _initialMultiplier, 0, msg.sender, true);
        _decreasePoolAndUserStats(_currentSpeed - (_currentSpeed * 10) / _initialMultiplier, _pid, msg.sender, true);
        _currentSpeed = userInfo[msg.sender].totalSpeeds[_pid];
        _increasePoolAndUserStats((_currentSpeed * _multiplier) / 10 - _currentSpeed, 0, msg.sender, true);
        _increasePoolAndUserStats((_currentSpeed * _multiplier) / 10 - _currentSpeed, _pid, msg.sender, true);
        _updateRewardDebts(msg.sender);
        emit UnstakeNfts(_pid, msg.sender, _nfts.length);
    }

    /**
     * @notice Get tokenIds of a given collection currently staked by specific user
     * @param _pid  Pool id
     * @param _collectionAddress  Address of the collection contract
     * @param _staker  User for whom to retrieve staked token ids
     * @return Array of tokenIds which are currently staked by the user for the given token contract
     */
    function getCurrentlyStakedTokenIds(
        uint8 _pid,
        address _collectionAddress,
        address _staker
    ) external view returns (uint256[] memory) {
        return stakedNFTs[_staker][_pid][_collectionAddress].toArray();
    }

    /**
     * @notice Get tokenIds of a given collection currently staked by any user
     * @param _collectionAddress  Address of the collection contract
     * @return Array of tokenIds which are currently staked for the given token contract
     */
    function getCurrentlyStakedTokenIds(address _collectionAddress) external view returns (uint256[] memory) {
        return currentlyStakedIds[_collectionAddress].toArray();
    }

    /**
     * @notice Get tokenIds of a given collection that have ever been staked by any user
     * @param _collectionAddress  Address of the collection contract
     * @return Array of tokenIds which have ever been staked for the given token contract
     */
    function getPreviouslyStakedTokenIds(address _collectionAddress) external view returns (uint256[] memory) {
        return previouslyStakedIds[_collectionAddress].toArray();
    }

    /**
     * @notice Harvest all rewards for msg.sender
     */
    function getRewards() external nonReentrant {
        _massUpdatePools();
        for (uint8 i; i < amountOfPools; i++) {
            _harvest(i, msg.sender);
        }
    }

    /**
     * @notice Harvest rewards for a certain poolId for msg.sender
     * @param _pid Pool id
     */
    function getReward(uint8 _pid) external nonReentrant {
        _massUpdatePools();
        _harvest(_pid, msg.sender);
    }

    /**
     * @notice View rewards for a certain poolId and user
     * @param _user  User address
     * @param _pid Pool id
     * @return rewards  Array of rewards [stardust, gold]
     */
    function viewRewards(address _user, uint8 _pid) external view returns (uint256[2] memory) {
        UserInfo storage user = userInfo[_user];
        uint256[2] memory rewards;
        uint256 currentTime = block.timestamp;
        if (currentTime >= seasonEndTime) {
            currentTime = seasonEndTime;
        }
        PoolInfo memory pool = poolInfo[_pid];
        if (!seasonStarted || pool.totalPoints == 0) {
            rewards[0] = 0;
            rewards[1] = 0;
        } else {
            uint256 accStardustPerPoint = pool.accStardustPerPoint;
            uint256 multiplier = currentTime - pool.lastRewardTime;
            uint256 total = multiplier * pool.rewardPerSecond;
            accStardustPerPoint += ((total * ACC_TOKEN_PRECISION) / pool.totalPoints);
            uint256 accStardust = (userInfo[_user].totalSpeeds[_pid] * accStardustPerPoint) / ACC_TOKEN_PRECISION;
            uint256 pending = accStardust - user.rewardDebts[_pid];
            uint256 stardustAmount = (pending * poolInfo[_pid].stardustWeightage) / 100;
            uint256 goldAmount = pending - stardustAmount;
            rewards[0] = stardustAmount;
            rewards[1] = goldAmount;
        }

        return rewards;
    }

    /**
     * @notice View the total speeds of a user across all pools
     * @param _user  User address
     * @return totalSpeeds  Array of total speeds [Global, Common, Uncommon, Rare/Epic]
     */
    function viewTotalSpeeds(address _user) external view returns (uint256[4] memory) {
        // Get userinfo memory
        UserInfo storage user = userInfo[_user];
        return user.totalSpeeds;
    }

    /**
     * @notice View number of spaceships staked by user
     * @param _user  User address
     * @return spaceshipsStaked  Number of spaceships staked
     */
    function viewSpaceshipsStaked(address _user) external view returns (uint256) {
        // Get userinfo memory
        UserInfo storage user = userInfo[_user];
        return user.spaceshipsStaked;
    }

    /**
     * @notice View number of NFTs staked by user
     * @param _pid Pool id
     * @param _collectionAddress  Address of the collection contract
     * @param _user  User address
     * @return boostingNfts Array of nft ids
     */
    function viewBoostingNfts(
        uint8 _pid,
        address _collectionAddress,
        address _user
    ) external view returns (uint256[] memory) {
        // Get userinfo storage
        UserInfo storage user = userInfo[_user];
        uint256[] storage boostingNfts = user.boostingNfts[_pid][_collectionAddress];
        uint256[] memory result = new uint256[](boostingNfts.length);
        // Copy boosting nfts to memory
        for (uint256 i = 0; i < boostingNfts.length; i++) {
            result[i] = boostingNfts[i];
        }
        return result;
    }

    /**
     * @notice View boosting nfts count in a specific pool
     * @param _user  User address
     * @param _pid Pool id
     * @return number of boosting nfts in a specific pool
     */
    function viewBoostingNftsPoolCount(address _user, uint8 _pid) external view returns (uint256) {
        // Get userinfo memory
        UserInfo storage user = userInfo[_user];
        return user.boostingNftsPoolCount[_pid];
    }

    /**
     * @notice View currently staked spaceship ids
     * @param _user  User address
     * @param _pid Pool id
     * @return spaceshipIds Array of spaceship ids
     */
    function viewCurrentlyStakedSpaceships(address _user, uint8 _pid) external view returns (uint256[] memory) {
        return currentlyStakedSpaceshipIds[_user][_pid].toArray();
    }

    /**
     * @notice View pool info
     * @param _pid Pool id
     */
    function viewPoolInfo(uint256 _pid) external view returns (PoolInfo memory) {
        return poolInfo[_pid];
    }

    /**
     * @notice Updates pool's core values
     * @param _pid The pool id
     */
    function _updatePool(uint256 _pid) internal {
        uint256 currentTime = block.timestamp;
        // Check if season is over
        if (seasonEndTime <= currentTime) {
            currentTime = seasonEndTime;
        }
        // Update based on pool type
        if (poolInfo[_pid].totalPoints > 0) {
            uint256 stardustReward = (currentTime - poolInfo[_pid].lastRewardTime) * poolInfo[_pid].rewardPerSecond;
            poolInfo[_pid].accStardustPerPoint += ((stardustReward * ACC_TOKEN_PRECISION) / poolInfo[_pid].totalPoints);
        }

        poolInfo[_pid].lastRewardTime = currentTime;
    }

    /**
     * @notice Updates all pools core values
     */
    function _massUpdatePools() internal {
        for (uint256 i; i < amountOfPools; i++) {
            _updatePool(i);
        }
    }

    /**
     * @notice Update reward debts of user
     * @param _user User address
     */
    function _updateRewardDebts(address _user) internal {
        for (uint256 i; i < amountOfPools; i++) {
            userInfo[_user].rewardDebts[i] =
                (userInfo[_user].totalSpeeds[i] * poolInfo[i].accStardustPerPoint) /
                ACC_TOKEN_PRECISION;
        }
    }

    /**
     * @notice Internal function for staking nfts
     * @param _pid The pool id to stake Nfts in
     * @param _collectionAddress The address of the collection the nft is from
     * @param _tokenId The id of the nft to stake
     */
    function _stake(uint8 _pid, address _collectionAddress, uint256 _tokenId) internal virtual {
        require(!stakedNFTs[msg.sender][_pid][_collectionAddress].contains(_tokenId), "NFT already staked");
        // Transfer ERC721 from user
        IERC721(_collectionAddress).safeTransferFrom(msg.sender, address(this), _tokenId);

        // Update stakers specific data
        stakedNFTs[msg.sender][_pid][_collectionAddress].add(_tokenId);
        // Update token specific data
        currentlyStakedIds[_collectionAddress].add(_tokenId);
        previouslyStakedIds[_collectionAddress].add(_tokenId);
    }

    /**
     * @notice Internal function for unstaking Nfts
     * @param _pid The pool id to unstake Nfts from
     * @param _collectionAddress The address of the collection the Nft is from
     * @param _tokenId The id of the Nft to unstake
     */
    function _unstake(uint8 _pid, address _collectionAddress, uint256 _tokenId) internal virtual {
        require(_isTokenStaked(_pid, msg.sender, _collectionAddress, _tokenId), "NFT not staked");
        // Update tokens currently staked
        stakedNFTs[msg.sender][_pid][_collectionAddress].remove(_tokenId);
        currentlyStakedIds[_collectionAddress].remove(_tokenId);
        // Transfer ERC721 from user
        IERC721(_collectionAddress).safeTransferFrom(address(this), msg.sender, _tokenId);
    }

    /**
     * @notice Checks whether a specific token is currently staked by a specific user
     * @param _pid  Pool id
     * @param _staker  User for whom to retrieve staked token ids
     * @param _collectionAddress Address of the collection the token is from
     * @param _tokenId TokenId to check for staking
     * @return bool True if token is staked by user, false otherwise
     */
    function _isTokenStaked(
        uint8 _pid,
        address _staker,
        address _collectionAddress,
        uint256 _tokenId
    ) internal view returns (bool) {
        return stakedNFTs[_staker][_pid][_collectionAddress].contains(_tokenId);
    }

    /**
     * @notice Increase pool and user stats
     * @param _speed Speed to increase
     * @param _pid Pool id
     * @param _user User address
     * @param _nft Is boosting nft or not
     */
    function _increasePoolAndUserStats(uint256 _speed, uint8 _pid, address _user, bool _nft) internal {
        poolInfo[_pid].totalPoints += _speed;
        if (_speed > 0 && !_nft) {
            poolInfo[_pid].totalCount++;
        }
        userInfo[_user].totalSpeeds[_pid] += _speed;
    }

    /**
     * @notice Decrease pool and user stats
     * @param _speed Speed to decrease
     * @param _pid Pool id
     * @param _user User address
     * @param _nft Is boosting nft or not
     */
    function _decreasePoolAndUserStats(uint256 _speed, uint8 _pid, address _user, bool _nft) internal {
        poolInfo[_pid].totalPoints -= _speed;
        if (_speed > 0 && !_nft) {
            poolInfo[_pid].totalCount--;
        }
        userInfo[_user].totalSpeeds[_pid] -= _speed;
    }

    /**
     * @notice Internal function for harvesting rewards
     * @param _pid Pool id
     * @param _user User address
     * @return pending Pending rewards
     */
    function _harvest(uint8 _pid, address _user) internal returns (uint256 pending) {
        IERC20 gold = IERC20(addressRegistry.getGold());
        IERC20 stardust = IERC20(addressRegistry.getStardust());
        UserInfo storage user = userInfo[_user];
        uint256 oldShares = userInfo[_user].totalSpeeds[_pid];
        PoolInfo storage pool = poolInfo[_pid];
        if (oldShares > 0) {
            uint256 accStardust = (oldShares * pool.accStardustPerPoint) / ACC_TOKEN_PRECISION;
            pending = accStardust - user.rewardDebts[_pid];
        }
        if (pending > 0) {
            user.rewardDebts[_pid] = (oldShares * pool.accStardustPerPoint) / ACC_TOKEN_PRECISION;
        }
        // Split total into 80% stardust, 20% gold
        stardust.safeTransfer(_user, (pending * poolInfo[_pid].stardustWeightage) / 100);
        gold.safeTransfer(_user, (pending * poolInfo[_pid].goldWeightage) / 100);
        emit Payout(
            _pid,
            _user,
            (pending * poolInfo[_pid].goldWeightage) / 100,
            (pending * poolInfo[_pid].stardustWeightage) / 100
        );
    }

    /**
     * @dev Remove nfts from pool
     * @param _pid  Pool id
     * @param _collectionAddress Nft collection address
     * @param _tokenId  Nft id
     */
    function _removeNftFromPool(uint8 _pid, address _collectionAddress, uint256 _tokenId) internal {
        UserInfo storage user = userInfo[msg.sender];
        uint256[] storage currentNfts = user.boostingNfts[_pid][_collectionAddress];
        for (uint256 i; i < currentNfts.length; i++) {
            uint256 currentNft = currentNfts[i];
            if (currentNft == _tokenId) {
                currentNfts[i] = currentNfts[currentNfts.length - 1];
                currentNfts.pop();
                user.boostingNftsPoolCount[_pid]--;
                user.boostingNftsCount--;
                return;
            }
        }
        revert("Nft not found");
    }

    /**
     * @notice Set the whitelisted status of a collection
     * @param _collectionAddresses The address of the collection
     * @param _status The whitelisted status of the collection
     * @dev Only whitelisted collections can be staked
     */
    function setWhitelistedCollections(address[] memory _collectionAddresses, bool _status) external onlyOwner {
        for (uint256 i; i < _collectionAddresses.length; i++) {
            whitelistedCollections[_collectionAddresses[i]] = _status;
        }
    }

    /**
     * @notice For receiving ERC721 tokens
     */
    function onERC721Received(address, address, uint256, bytes memory) public virtual override returns (bytes4) {
        return this.onERC721Received.selector;
    }
}

contracts/AtlantisSpaceships.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.11;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155Receiver.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "./interface/IAtlantisSpaceships.sol";
import "./interface/IAtlantisEquipments.sol";
import "./AtlantisAddressRegistry.sol";
import "./common/Base64.sol";

/// @title Atlantis Spaceships Mint Contract
/// @dev Race your spaceships on the racing contract! Equip equipments to boost speed!
contract AtlantisSpaceships is ERC721, Ownable, IAtlantisSpaceships, IERC1155Receiver, ReentrancyGuard {
    using Strings for uint256;

    AtlantisAddressRegistry public addressRegistry;

    string public baseURI;

    uint256 public currentSupply;
    uint256 public currentIndex = 1;
    // Mapping of spaceshipIDs to spaceships
    mapping(uint256 => Spaceship) public spaceships;

    // Events
    event EquipmentModified(
        uint256 indexed spaceshipId,
        string fireEquipmentString,
        string lightningEquipmentString,
        string steelEquipmentString,
        uint256 speed
    );
    event PublicMint(address indexed to);
    event AddressRegistryUpdated(address indexed newAddressRegistry);
    event BaseURIUpdated(string indexed newBaseURI);
    event SpaceshipRaritySet(uint256 indexed spaceshipId, string rarityString, uint256 speed);

    constructor(
        address _owner,
        string memory __baseURI,
        AtlantisAddressRegistry _addressRegistry
    ) ERC721("Atlantis Spaceships", "SPACESHIPS") {
        setBaseURI(__baseURI);
        transferOwnership(_owner);
        addressRegistry = _addressRegistry;
    }

    // -------------------- ATLANTIS FUNCTIONS --------------------------
    /** Token Ids of equipments
     * 1 2 3 Fire Lightning Steel Equipment Level 1
     * 4 5 6 Fire Lightning Steel Equipment Level 2
     * 7 8 9 Fire Lightning Steel Equipment Level 3
     * 10 11 12 Fire Lightning Steel Equipment Level 4
     * 13 14 15 Fire Lightning Steel Equipment Level 5
     * 16 17 18 Fire Lightning Steel Equipment Level 6
     * 19 20 21 Fire Lightning Steel Equipment Level 7
     * 22 23 24 Fire Lightning Steel Equipment Level 8
     * 25 26 27 Fire Lightning Steel Equipment Level 9
     * 28 29 30 Fire Lightning Steel Equipment Level 10
     */

    function modifyEquipment(
        uint256 _spaceshipId,
        uint256 _fireEquipmentId,
        uint256 _lightningEquipmentId,
        uint256 _steelEquipmentId
    ) external nonReentrant {
        // Check if the spaceship is owned by the caller if not throw notOwner error
        if (ownerOf(_spaceshipId) != msg.sender) {
            revert NotOwner();
        }
        IAtlantisEquipments atlantisEquipments = IAtlantisEquipments(addressRegistry.getEquipments());
        // Get spaceship
        Spaceship storage spaceship = spaceships[_spaceshipId];

        // Set spaceship Equipments to new Equipments
        _equipHelper(spaceship.fireEquipmentId, _fireEquipmentId, 1);
        _equipHelper(spaceship.lightningEquipmentId, _lightningEquipmentId, 2);
        _equipHelper(spaceship.steelEquipmentId, _steelEquipmentId, 0);

        spaceship.fireEquipmentId = _fireEquipmentId;
        spaceship.lightningEquipmentId = _lightningEquipmentId;
        spaceship.steelEquipmentId = _steelEquipmentId;

        emit EquipmentModified(
            _spaceshipId,
            atlantisEquipments.getLevel(_fireEquipmentId) != 0
                ? uint256(atlantisEquipments.getLevel(_fireEquipmentId)).toString()
                : "Unequipped",
            atlantisEquipments.getLevel(_lightningEquipmentId) != 0
                ? uint256(atlantisEquipments.getLevel(_lightningEquipmentId)).toString()
                : "Unequipped",
            atlantisEquipments.getLevel(_steelEquipmentId) != 0
                ? uint256(atlantisEquipments.getLevel(_steelEquipmentId)).toString()
                : "Unequipped",
            getSpeed(_spaceshipId)
        );
    }

    /**
     * @dev Internal function to handle equipping
     * @param _oldId Token ID of old equipment
     * @param _newId Token ID of new equipment
     * @param _element Element of equipment
     */
    function _equipHelper(uint256 _oldId, uint256 _newId, uint8 _element) internal {
        IAtlantisEquipments atlantisEquipments = IAtlantisEquipments(addressRegistry.getEquipments());

        // Do nothing if oldId and newId are the same
        if (_oldId == _newId) {
            return;
        }
        // Check that newId is the correct element if not throw wrongelement
        if (_newId != 0 && atlantisEquipments.getElement(_newId) != _element) {
            revert WrongElement();
        }
        // Check if newId is not zero
        if (_newId != 0) {
            // Transfer newId to contract
            IERC1155(address(atlantisEquipments)).safeTransferFrom(msg.sender, address(this), _newId, 1, "");
        }
        // Check if oldId is not zero
        if (_oldId != 0) {
            // Transfer oldId to owner
            IERC1155(address(atlantisEquipments)).safeTransferFrom(address(this), msg.sender, _oldId, 1, "");
        }
        return;
    }

    /**
     * @notice Get details of a spaceship from its id
     * @param _tokenId Token ID of spaceship
     */
    function getSpaceship(uint256 _tokenId) external view returns (SpaceshipData memory) {
        SpaceshipData memory _spaceshipData = SpaceshipData(spaceships[_tokenId], getSpeed(_tokenId));
        return _spaceshipData;
    }

    /**
     * @notice Get speed of a spaceship from its id
     * @param _tokenId Token ID of spaceship
     */

    function getSpeed(uint256 _tokenId) public view returns (uint256) {
        IAtlantisEquipments atlantisEquipments = IAtlantisEquipments(addressRegistry.getEquipments());
        // Get spaceship
        Spaceship memory spaceship = spaceships[_tokenId];
        // Get rarity
        AtlantisLib.Rarity _rarity = spaceship.rarity;
        uint16 fireSpeed = atlantisEquipments.getSpeed(spaceship.fireEquipmentId);
        uint16 lightningSpeed = atlantisEquipments.getSpeed(spaceship.lightningEquipmentId);
        uint16 steelSpeed = atlantisEquipments.getSpeed(spaceship.steelEquipmentId);

        return (uint16(_rarity) + 1) * (fireSpeed + lightningSpeed + steelSpeed + 1);
    }

    /**
     * @notice Get rarity of a spaceship from its id
     * @param _tokenId Token ID of spaceship
     */
    function getRarity(uint256 _tokenId) external view returns (AtlantisLib.Rarity) {
        // Check exists
        require(_exists(_tokenId), "Spaceship does not exist");
        // Get spaceship
        Spaceship memory spaceship = spaceships[_tokenId];
        return spaceship.rarity;
    }

    /**
     * @dev Set rarity of all spaceships
     * @param _tokenId Token id of spaceship
     * @param _rarity Rarity of spaceship
     */
    function setSpaceshipRarity(uint256 _tokenId, AtlantisLib.Rarity _rarity) internal {
        spaceships[_tokenId].rarity = _rarity;
        uint256 _speed = getSpeed(_tokenId);
        emit SpaceshipRaritySet(_tokenId, _spaceshipRarityToString(_rarity), _speed);
    }

    // -------------------- ATLANTIS METADATA FUNCTIONS --------------------------

    /**
     * @notice Returns planet orbit as string
     * @param _rarity Rarity of spaceship
     * @return String of planet orbit
     */
    function _spaceshipRarityToString(AtlantisLib.Rarity _rarity) internal pure returns (string memory) {
        if (_rarity == AtlantisLib.Rarity.COMMON) {
            return "Common";
        } else if (_rarity == AtlantisLib.Rarity.UNCOMMON) {
            return "Uncommon";
        } else if (_rarity == AtlantisLib.Rarity.RARE) {
            return "Rare";
        } else if (_rarity == AtlantisLib.Rarity.EPIC) {
            return "Epic";
        }
    }

    /**
     * @notice Get ImageURI
     * @param _rarity Rarity of spaceship
     * @param _lightningLevel Lightning level of spaceship
     * @param _fireLevel Fire level of spaceship
     * @param _steelLevel Steel level of spaceship
     */
    function getImageURI(
        AtlantisLib.Rarity _rarity,
        uint8 _lightningLevel,
        uint8 _fireLevel,
        uint8 _steelLevel
    ) public view returns (string memory) {
        return
            string(
                abi.encodePacked(
                    _baseURI(),
                    uint256(_rarity).toString(),
                    "/",
                    uint256(AtlantisLib._getEquipmentTier(_steelLevel)).toString(),
                    "/",
                    uint256(AtlantisLib._getEquipmentTier(_fireLevel)).toString(),
                    "/",
                    uint256(AtlantisLib._getEquipmentTier(_lightningLevel)).toString(),
                    ".png"
                )
            );
    }

    /**
     * @notice Returns token metadata
     * @param _tokenId Token ID of spaceship
     * @dev Metadata is stored on-chain
     * @dev Metadata is stored in JSON format
     * @return String of token metadata
     */
    function tokenURI(uint256 _tokenId) public view override returns (string memory) {
        _requireMinted(_tokenId);
        IAtlantisEquipments atlantisEquipments = IAtlantisEquipments(addressRegistry.getEquipments());
        // Get Spaceship
        Spaceship memory spaceship = spaceships[_tokenId];

        // Get rarity
        AtlantisLib.Rarity rarity = spaceship.rarity;

        // Name
        string memory json = string(abi.encodePacked('{"name": "', name(), " #", _tokenId.toString(), '",'));

        uint8 lightningLevel = atlantisEquipments.getLevel(spaceship.lightningEquipmentId);
        uint8 fireLevel = atlantisEquipments.getLevel(spaceship.fireEquipmentId);
        uint8 steelLevel = atlantisEquipments.getLevel(spaceship.steelEquipmentId);

        string memory lightningString = lightningLevel != 0 ? uint256(lightningLevel).toString() : "Unequipped";
        string memory fireString = fireLevel != 0 ? uint256(fireLevel).toString() : "Unequipped";
        string memory steelString = steelLevel != 0 ? uint256(steelLevel).toString() : "Unequipped";
        // Description
        json = string(
            abi.encodePacked(
                json,
                '"description": "Welcome to the captivating realm of Atlantis, the game-verse and home of the legendary Argonauts. Acquire Spaceships to take part in the Atlantis race and earn attractive rewards!",'
            )
        );

        // Attributes
        json = string(
            abi.encodePacked(
                json,
                '"attributes": [{"trait_type": "Rarity", "value": "',
                _spaceshipRarityToString(rarity),
                '"},',
                '{"trait_type": "Thunderbolt Wings Level", "value": "',
                lightningString,
                '"},',
                '{"trait_type": "Inferno Thrusters Level", "value": "',
                fireString,
                '"},',
                '{"trait_type": "Argonium Exoshell Level", "value": "',
                steelString,
                '"},'
            )
        );

        json = string(
            abi.encodePacked(json, '{"trait_type": "Speed", "value": "', uint256(getSpeed(_tokenId)).toString(), '"}],')
        );

        json = Base64.encode(
            bytes(
                string(
                    abi.encodePacked(
                        json,
                        '"image": "',
                        getImageURI(rarity, lightningLevel, fireLevel, steelLevel),
                        '"}'
                    )
                )
            )
        );
        return string(abi.encodePacked("data:application/json;base64,", json));
    }

    // -------------------- MINT FUNCTIONS --------------------------

    /**
     * @notice Public Mint
     * @param _address Address of user
     */
    function _mintOne(address _address) internal {
        currentSupply += 1;
        _safeMint(_address, currentIndex);
        currentIndex += 1;

        emit PublicMint(_address);
    }

    /**
     * @notice Airdrop multiple nfts to multiple addresses
     * @param _addresses All addresses that will receive nfts
     * @param _rarities Amount of nfts that will be minted for each address
     */
    function airdrop(address[] calldata _addresses, uint8[] calldata _rarities) external onlyOwner {
        // Check that both arrays are the same length
        require(_addresses.length == _rarities.length, "Arrays must be the same length");
        for (uint256 i; i < _addresses.length; i++) {
            _mintOne(_addresses[i]);
            setSpaceshipRarity(currentIndex - 1, AtlantisLib.Rarity(_rarities[i]));
        }
    }

    // ------------------------- OWNER FUNCTIONS ----------------------------
    /**
     * @dev Set Metadata URI
     * @param _newBaseURI new base uri
     */
    function setBaseURI(string memory _newBaseURI) public onlyOwner {
        baseURI = _newBaseURI;
        emit BaseURIUpdated(_newBaseURI);
    }

    // Set Address registry
    function setAddressRegistry(AtlantisAddressRegistry _addressRegistry) public onlyOwner {
        addressRegistry = _addressRegistry;
        emit AddressRegistryUpdated(address(_addressRegistry));
    }

    /**
     * @dev Base URI for computing {tokenURI}. If set, the resulting URI for each
     * token will be the concatenation of the `baseURI` and the `tokenId`. Empty
     * by default, it can be overridden in child contracts.
     */
    function _baseURI() internal view virtual override returns (string memory) {
        return baseURI;
    }

    /**
     * @notice Checks if token exists
     * @param tokenId Token ID
     */
    function exists(uint256 tokenId) external view returns (bool) {
        return _exists(tokenId);
    }

    /**
     * @notice Returns total supply of spaceships
     */
    function totalSupply() public view returns (uint256) {
        return currentSupply;
    }

    function onERC1155Received(
        address operator,
        address from,
        uint256 id,
        uint256 value,
        bytes calldata data
    ) external pure returns (bytes4) {
        return this.onERC1155Received.selector;
    }

    function onERC1155BatchReceived(
        address operator,
        address from,
        uint256[] calldata ids,
        uint256[] calldata values,
        bytes calldata data
    ) external pure returns (bytes4) {
        return this.onERC1155BatchReceived.selector;
    }
}

